css:
@-moz-document domain("taringa.net"){
body
{
    min-width:320px!important;
    width:100%!important;
    background:black!important;
}
.v6 body, .v6 p, .v6 ul, .v6 ol, .v6 dl, .v6 input, .v6 textarea ,.v6 .v6-container .v6-content,
.v6 nav.tabs,.v6 nav.tabs ul li a,.v6 .list-l ul li,.v6 .list-m ul li a ,.v6 .top-usuarios ul li,
.v6 .list-s ul li a,.v6 .list-s h2 ,.v6 .list-m h2 ,#main,.box div.title h2
{
    background:black!important;
    color:white!important;
}
.v6 .list-l ul li a
{
    color:red!important;
    text-shadow:none!important;
}
.comment-actions
{
    display:block!important;
}
.comment-replies
{
    margin-left:10%!important;
    width:90%;
}
.fixme
{
    margin-top:36px
}
#main
{
    position:relative;
    top:-24px;
}

header
{
    position:fixed!important;
    height:36px!important;
    background:#f11!important;
    border-color:gray!important;
}

header h1 a
{
    height:36px!important;
    background-position:0 -10px!important;
}

.nav-principal
{
    height:36px!important;
}

.nav-principal a
{
    line-height:36px!important;
}
.nav-principal .active
{
    box-shadow:0 0 3px 2px white;
    background:white;
    color:blue!important;
}
.nav-principal a
{
    color:#eee!important;
}
.nav-principal .active a
{
    border-top:2px solid #15c!important;
    border-bottom:1px solid #39f;
    color:#15c!important;
}
.nav-principal li
{
    transition:1s all;-webkit-transition:1s all;
}
.nav-principal li:hover
{
    transition:.3s all;-webkit-transition:.3s all;
    background:white;
}
.nav-principal a:hover
{
    transition:.3s all;-webkit-transition:.3s all;
    color:black!important;
}
.v6 .v6-container
{
    position:relative;
    top:-24px!important;
}
.nav-user .buscar
{
    transition:.9s all;-webkit-transition:.9s all;
    top:-34px;left:255px;
    height:36px;
    width:195px;
    background:whitesmoke!important;
    border:0px solid red!important;
    border-radius:0px!important;
    box-shadow:inset 1px 0 2px black;
}
.nav-user .buscar input
{
    color:black!important;
    width:130px!important;
}
.nav-user .buscar:hover
{
    transition:.6s all;-webkit-transition:.6s all;
    background:white!important;
}
.user-action .count:before
{
    border-color:red transparent transparent!important;
}
.user-action .count
{
    background:red!important;
    z-index:9;
}
.nav-user
{
    width:450px;
    height:33px!important;
    padding:0!important;
}
.login
{
    height:32px!important;
}
.ico-shout
{
    top:0px;
    height:36px!important;
    border-radius:0px!important;
    box-shadow:-1px 0px 1px black;
}
.login .user-actions
{
    margin:0!important;
    height:36px!important;
    line-height:36px!important;
    background-color:rgba(0,0,0,.2)!important;
    border-radius:0px!important;
    box-shadow:0 0 2px black,inset 0 5px 8px rgba(255,255,255,.1);
    z-index:2;
}
.login .user-action:hover
{
    transition:all .5s;-webkit-transition:all .5s;
    background-color:#5bf!important;
}
.user-actions .user-action
 {
     transition:all 1s;-webkit-transition:all 1s;
     padding:0px 4px!important;
     border-left:1px solid #15a;
     height:36px
 }
.user-action .user-picture
{
    width:34px!important;height:34px!important;
    margin-top:1px!important;
}
.content-left li
{
    height:45px!important;
}
.content-left li .avatar,.content-left li .avatar img
{
    height:46px!important;width:60px!important;
}
.list-l ul li h3
{
    font-size:16px!important;
}
.list-l li
{
    transition:all 1.8s;-webkit-transition:all 1.8s;
}
.list-l li:hover
{
    transition:all .6s;-webkit-transition:all .6s;
    background-color:#eee!important;
}
.list-l .social-media
{
    top:22px!important;
}
.sidebar
{
    background:black!important;
    box-shadow:inset 1px 0 2px
}
#google_ads_div_tar_h_300x250_general_td, .zeti-ads,#google_ads_div_tar_p_300_mundial-2014_td,
#google_ads_div_tar_c_300x250_grupos-organizaciones_td
{
    display:none!important;
}
.top-usuarios li
{
    border-left:1px solid lightgray;border-right:1px solid lightgray;
}
#asd input,.myComment textarea.markItUpEditor
{
    background:white;
    color:black
}
#point-bar,.masdiez
{
    transition:all 1s;-webkit-transition:all 1s;
    background:#15c!important;
}
#point-bar a{transition:all 1s;-webkit-transition:all 1s;}
#point-bar a:hover,.masdiez:hover
{
    transition:all .3s;-webkit-transition:all .3s;
    background:#27f!important;
    border-radius:3px
}
@media (max-width:1023px)
{
    .box.sticky .title
    {
        display:none!important;
    }
    .box.sticky
    {
        background:none!important;
        border:none!important;
    }
    .my-shout-textarea-mi textarea
    {
        width:90%!important;
    }
    .highlightTextarea,#newPost .mandatory-fields-notice,.big-group-data
    {
        width:100%!important;
    }
    .social-bar.bottom
    {
        width:100%;
        font-size:90%!important;
    }
    .post-box.tops-large
    {
        display:block;
        width:100%!important;
    }
    .post-box.tops-large .post-box-item
    {
        width:14%!important;
    }
    .post-box-item
    {
        width:23%!important;
    }
    .post-box-item img
    {
        width:98%!important;
        height:auto!important;
    }
    .post-box-item a
    {
        font-size:14px!important;
    }
    .post-content
    {
        width:100%!important;
    }
    .fixme,.myComment .comment-data
    {
        display:none!important;
    }
    .myComment,#post-data-stats,#comments,.post-box.tops-medium
    {
        width:100%!important;
    }
    #post-data-stats
    {
        margin:0!important;
        padding:1%!important;
        width:98%!important;
    }
    #myComment-text-box,.markItUpHeader, .myFastreply .markItUpHeader,.myComment textarea, .myFastreply textarea
    {
        width:98%!important;
    }
    #asd input
    {
        width:142px!important;
    }
    .pie-wiroos
    {
        left:180px!important;
    }
    .emoji-categories
    {
        width:99.6%!important;
    }
    .emoji-list
    {
        width:85%
    }
    #sidebar,#newPost #sidebar
    {
        display:block!important;
        float:none!important;
        position:relative!important;
        width:100%;
        border-top:1px solid gray;
        margin:0!important;
        box-shadow:inset 0 1px 3px gray;
    }
    #post-author-box
    {
        width:320px;
        margin:0 auto;
    }
    body
    {
        overflow-x:hidden!important;
    }
    #main
    {
        width:100%;
        min-width:480px;
        background:white
    }
    #main-col
    {
        float:none!important;
        display:block!important;
        width:100%!important;
    }
    #full-col
    {
        width:98%!important;
        margin:1%!important;
        padding:0!important;
    }
    .sidebar
    {
       display:none!important;
    }
    .v6 .v6-container
    {
        min-width:480px!important;
        width:auto!important;
    }
    #footer .wrapper
    {
        width:auto;
        min-width:480px;
    }
    #footer-links
    {
        width:auto!important;
        min-width:480px;
    }
    .post-info .social-bar
    {
        text-align:left;
    }
    .details
    {
        font-size:10px!important;
    }
    .v6-content
    {
        margin:0!important;
        padding:0!important;
        width:100%!important;
    }
    header
    {
        min-width:480px!important;
        
        position:absolute!important;
    }
    .v6 .nav-principal > ul > li > a
    {
        font-size:11px!important;
    }
    .nav-user
    {
        width:470px;
    }
    .nav-user .buscar
    {
        top:38px;
        left:404px;
        width:114px!important;
        height:36px!important;
        position:absolute!important;
    }
    .nav-user .buscar input
    {
        font-size:12px!important;
        width:54px!important;
    }
    .tabs .player-post
    {
        float:right!important;
    }
}
@media (max-width:799px)
{
    .post-title
    {
        font-size:22px!important;
    }
    header
    {
        height:72px!important;
    }
    .v6 .v6-container
    {
        top:12px!important;
    }
    header h1 a
    {
        height:36px!important;
        background-position:-8px -10px!important;
        width:130px!important;
    }
    .nav-user
    {
        top:36px;
        position:absolute;
        width:100%;
        height:36px!important;;
        background:#222;
        box-shadow:inset 0 2px 4px black;
        border-top:1px solid white;
    }
    .nav-user .login
    {
        right:0!important;
        position:absolute!important;
    }
    .nav-user .user-actions
    {
        background-color:transparent!important;
        box-shadow:0 0 0;
    }
    .nav-user .buscar
    {
        top:0;
        left:0!important;
        width:150px!important;
        height:36px!important;
    }
    .nav-user .buscar input
    {
        width:82px!important;
        font-size:15px!important;
    }
    .v6-country,.content-right,.tabs .active,.v6 .list-l ul li .social-media:before
    {
        display:none!important;
    }
    .v6 .content-left
    {
        margin:0!important;
        width:100%!important;
    }
    .v6 .content-left li
    {
        padding:1%!important;
        width:98%!important;
    }
    .v6 .v6-container
    {
        min-width:450px!important;
    }
    .tabs a
    {
        font-size:10px!important;
    }
    #main
    {
        top:12px;
    }
    .v6 .list-l ul li .social-media
    {
        right:0!important;
        display:block!important;
    }
    .user-action .count:after
    {
       border-color:#222!important;
    }
}
}
