css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.baidu.com") {
  .result-left-img,.result-left-img-as,#content_right,.image_single_video,.op_vd_mininewest_toleft,.op_sp_img_toleft{
    display:none!important;
  }/*清除左边栏图片*/
  #content_left,#rs,#foot,#page{
    padding-left:165px;
  }
  #s_tab a{
    color:	rgb(102,102,102);
  }
  #s_tab a:hover{
    color:	#000;
  }
  #s_tab{
    color:rgb(221,75,57);
  }/*上方颜色微调*/
  a,em {
    text-decoration:none!important;
  }
  .c-gap-bottom-small a:hover,.op_tieba2_container a:hover,.c-gap-right-small:hover,#rs a:hover,.s_help a:hover,#u a:hover,.c-default:hover,.c:hover{
    text-decoration:underline!important;
  }/*小文字焦点下划线*/
  .t a:hover{
    text-decoration:none!important;
  }

  #search .s_ipt_wr,#search .s_btn_wr{
    display:none!important;
  }
  #search{
   padding-left:145px!important;
    padding-bottom:30px;
    padding-top:18px;
    background-color:#f2f2f2;
    width:auto;
  }
  #search a{
    color:#777;
    font-size:13px;
  }
  #search a:hover{
    text-decoration:none!important;
    color:#333333;
  }
  #foot{
    display:none;
  }
  #head{
    width:auto;
  }
  .s_logo{
    position:relative;
    top:32px;
  }
   #form{
   padding-left:0px;
   clear:left;
   margin-left:130px;
 }
}
