css:
@-moz-document url-prefix("https://www.burning-seri.es"), url-prefix("http://www.burning-seri.es"), url-prefix("https://burning-seri.es"), url-prefix("http://burning-seri.es") {
@viewport {
	width: device-width;
/*	min-zoom: 0.9;
	zoom:1;
	max-zoom: 1.1;
*/}
@media screen and (min-width: 752px) {
}
div#root {
   width: 95%;
   min-width: 750px;
   background-repeat:no-repeat, no-repeat, repeat-x;
   background-image: url(https://b-s.cc/img/header.v4.jpg), url('data:image/png;base64,'), url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAACpCAIAAABlOMBdAAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAADsMAAA7DAcdvqGQAAAATSURBVChTY1CymTuKR/HgxTZzAYSNpbTfPz5bAAAAAElFTkSuQmCC');
   background-size: 900px, 230px 169px, 1px 169px;
   background-position: 0% 0%, 900px 0px, 230px 0px; 
   max-width:1280px;
   box-shadow: 0px 0px 5px #000;
   }
#root > header > h1 > a {
    width:570px;
    height:150px;
}
#root > nav:nth-child(2) > ul:nth-child(1) > li:nth-child(1) {
    width:12%;
    min-width: 80px;
    max-width: 150px;
}
#root > nav:nth-child(2) > ul:nth-child(1) > li:nth-child(3) {
    width:12%;
    min-width: 90px;
    max-width: 150px;
}
#root > nav:nth-child(2) > ul:nth-child(1) > li:nth-child(5) {
    width:12%;
    min-width: 90px;
    max-width: 150px;
}
.home .column {
   width: 49% ! important;
   } 
.home #shoutbox dl {
    max-height: 651px!important;
}
#serInput {
    width: 100%!important;
   }
 .andere-serien div.genre ul,#serSeries {
   column-width:250px ! important; 
   -moz-column-width:250px ! important;
   -moz-column-count:auto!important;
   -webkit-column-width:250px ! important;
   }
img[src$="7f9b59f2.jpg"] {
    width:0px;
    height:0px;
    background-image: url('http://ia.media-imdb.com/images/M/MV5BMTcyODc4OTU1N15BMl5BanBnXkFtZTcwODAwMjYyOQ@@._V1_SX200_SY284_.jpg');
    background-position:center top;
    background-repeat: no-repeat;
    padding: 0px 8px 284px 8px;
    overflow: hidden;
}
img[src$="fe75380c.jpg"] {
    width:0px;
    height:0px;
    background-image: url('http://ia.media-imdb.com/images/M/MV5BMTc4OTI4MzIxOV5BMl5BanBnXkFtZTcwNTkzNDExMw@@._V1_SX212_SY239_.jpg');
    background-position:center top;
    background-repeat: no-repeat;
    padding: 0px 0px 239px 0px;
    overflow: hidden;
}
#sp_left {
   width: calc(100% - 210px);
   }
#shorturl_field {
    display:none;
   }  
.app > div:nth-child(2) > p:nth-child(3):after{
  content:'Anmerkung des Darktheme-Teams: \000D\000A Wir empfehlen zusätzlich die App "Andstream", da Hubi bei einigen Hostern versagt. Für Chromecast-Besitzer gibt es des Weiteren die App "Localcast". Chromecast läuft am besten mit Streamcloud, über Umwege funktionieren aber auch Ecostream, Primeshare und Movshare.'; 
  visibility: visible;
  display: block;
  border: 1px solid #000;
  margin-top: 5px;
  border-radius:2px;
  padding-left: 5px;
  box-shadow: 0px 0px 4px #000;
}
img[src$="qrcode.png"] {
    width:0px;
    height:0px;
    background-image: url(data:image/svg+xml;base64,);
    background-position:center top;
    background-repeat: no-repeat;
    padding: 0px 0px 200px 0px;
}
.app > div:nth-child(2){
    width: calc(100% - 210px) ! important;
}
@media screen and (max-width: 752px) {
div#root {
    width: 100%;
    min-width: 240px;
    max-width: 540px;
    background-image:none;
    border: none;
}
#root > header {
    background: url('https://b-s.cc/img/header.v4.jpg') scroll 0% 0% #127;
    background-repeat: repeat-x;
    background-size: cover;
    border-top-left-radius:10px;
    border-top-right-radius:10px;
}
#root > section:nth-child(3),#root > br:nth-child(4), #root > footer:nth-child(5) {
    background-color: #fff;
}
#root > header > h1 > a {
    width:100%;
    height: 100%;
    min-height: 80px;
    max-height: 250px;
}
.andere-serien input, .vorgeschlagene-serien #serForm input {
    width:100%!important;
}
.home > h2:nth-child(1), .suche > h2:nth-child(1) {
    display:none;
}
section.suche {
    padding-top: 0!important;
    max-width: 400px;
}
section.suche > form:nth-child(2) > fieldset:nth-child(1) > label {
    width: 100%;
}
.suche > form > fieldset > label > input[type=text] {
    width: 200px;
}
.suche > form > fieldset > label:nth-child(6) {
    padding-top: 25px;
    font-size: 0px;
}
#teaser {
    margin-top: 0px !important;
}
.home .column {
    width: 100%!important;
}
.home #column2 {
    float: left!important;
}
.andere-serien ul, .vorgeschlagene-serien ul {
    -moz-column-count: auto!important;
    -moz-column-width: 210px;
}    
#root > footer:nth-child(5) > ul:nth-child(1) > li:nth-child(1) {
    display: none!important;
}
#other-series-nav > ul:nth-child(2) {
    display:none!important;
}
#shoutbox {
    display:none!important;
}
#user {
    display:none!important;
}
section label {
    width: 100%!important;
}
#sp_left {
    width: 100%;
}
.nowrap {
    white-space: normal;
}
#shorturl {
    display:none;
}
img[alt=Video]{
    width:100%;
}
#sp_right {
    width: 100%;
    max-width:480px;
    float: none;
    margin-left: auto;
    margin-right: auto;
}
#sp_right > img:nth-child(1) {
    margin-left: calc(0.5 * 100% - 89px);
    margin-right: calc(0.5 * 100% - 89px);
}
}
@media screen and (min-width: 550px) and (max-width:649px) {
div#root {
    max-width: 644px;
}
#root > header > h1 > a {
    min-height: 103px;
}
}
    @media screen and (min-width: 650px) and (max-width:752px) {
div#root {
    max-width: 747px;
}
#root > header > h1 > a {
    min-height: 122px;
}
}
@media screen and (max-width: 399px) {
#root > nav:nth-child(2) > ul:nth-child(1) > li:nth-child(1), #root > nav:nth-child(2) > ul:nth-child(1) > li:nth-child(4), #root > nav:nth-child(2) > ul:nth-child(1) > li:nth-child(6) {
    display: none!important;
}
#root > nav:nth-child(2) > ul:nth-child(1) {
    max-width: 100%;
}
#other-series-nav {
    width: 38%;
    min-width: 93px;
}
#root > nav:nth-child(2) > ul:nth-child(1) > li:nth-child(3) {
    width: 30%;
    min-width: 72px;
}    
#root > nav:nth-child(2) > ul:nth-child(1) > li:nth-child(5) {
    width:30%;
    min-width:52px;
}
}
@media screen and (min-width: 400px) and (max-width: 751px) {
#root > nav:nth-child(2) > ul:nth-child(1) > li:nth-child(4), #root > nav:nth-child(2) > ul:nth-child(1) > li:nth-child(6) {
    display: none!important;
}
#root > nav:nth-child(2) > ul:nth-child(1) {
    max-width: 100%;
}
#root > nav:nth-child(2) > ul:nth-child(1) > li:nth-child(1) {
    width: 23%;
}
#other-series-nav {
    width: 29%;
    min-width: 93px;
}
#root > nav:nth-child(2) > ul:nth-child(1) > li:nth-child(3) {
    width: 23%;
    min-width: 72px;
}    
#root > nav:nth-child(2) > ul:nth-child(1) > li:nth-child(5) {
    width:23%;
    min-width:52px;
}
}
}
