css:
/*===================== Tum sitedeki ayarlar ====================*/
@-moz-document domain("www.arena-tr.com") { 

/* site govdesi */
#inner {background :#e7e7e7;margin-top :-10px;}
/* ust bardaki navigasyon cubugu (upload ,download vs.)*/
#member_info_bar {text-shadow: #f00 0px 0px 5px;  }
/*üye adları ayarı */
#member_info span {;}
/* site bodysi*/
/* site arkaplanı ve konumu*/
#wrap {background:#000;margin-top :20px;}
/* user menusu*/
/* mesajlardaki tarih ayarı*/
.smalldate{color:#33a;}
/*site baslıgı(banner)*/
#header { display: none; } 
div.whiteBox{background-color :#ddd; margin-top : 0px; height:16px;line-height :0;}
div.mainContent{ line-height: 22px;   }
div.torrent-box{background-color : #e7e7e7;height : 90px;}
div.torrentDetails{height : 25px;}
div.comments{background-color:#e7e7e7;}
div.tabItems{background-color:#e7e7e7;}
}
/*===================== Anasayfadaki ayarlar ====================*/
@-moz-document url("http://www.arena-tr.com/?p=home&pid=1") {
#menu {background-image:url(http://www.top10lar.com/wp-content/uploads/Galatasaray-Duvar-Ka%C4%9F%C4%B1d%C4%B1_002.jpg); }

#member_info_bar {background-image:url(http://www.top10lar.com/wp-content/uploads/Galatasaray-Duvar-Ka%C4%9F%C4%B1d%C4%B1_002.jpg);text-shadow: #f00 0px 0px 5px;  }
/* sunum kapakları */
div.holder {background :#e7e7e7;height:300px;}
#footer{width:932px;background:#666;}
#shoutboxNotifications {display :none;} 
/* anasayfa slaytı*/
div.widget{margin-top :-5px;}
/* son torrentlerdeki yazı ayarı */
#slider {font-family :"comic sans ms";line-height:18px;padding-left :29px;margin-top :-18px;width :900px;background :#e7e7e7;height:310px;}
/*anasayfadaki anket*/
div.widget table tbody tr td table    {display :none;}
div.widget table tbody tr td h4{display:none;}
div.widget table tbody tr td p{display:none;}
/* mini ayarları*/
/* mininin uzunlugu*/
div.shoutbox {max-height: 510px;}
/* mini yazı rengi ,arka planı ,konumu ve uzunlugu*/
.shoutbox { border: 0px solid #555;margin-top: 2px; width: 937px; padding: 1px;background:#e7e7e7; color :#00f ;background-image :url(http://i1186.photobucket.com/albums/z379/araniaexumai/logominigs_zps3f77e6de.png); background-position :right top ;background-repeat :repeat-y ; background-size :15% 2% ;background-attachment :scroll;}
/* yazılar arası bosluk*/
.shoutbox tr { line-height: 13px; }
/*mini alanı ayarları*/
/* mini genişliği ve yazı tipi*/
#shoutbox_list { width: 932px;font-family :arial;margin-left :0px;}
/*üye ve yazı boyutları ve yazı rengi*/
.sMemberDetails {font-size :12px;}
.sMemberShout { color: #444; font-size: 12px;}
/*mini duyurusu*/
#shoutboxNotifications {display :none;} 
/* mesaj barı*/
#tinymceShoutbox {width: 932px;margin-top :2px;}

div.a{color:transparent;}
div.small{background-color :#fff;}
div.mainContent{ line-height: 21px;   }
}
/*====================== Teknik Destek Sayfası  =================*/
@-moz-document url("http://www.arena-tr.com/?p=firstlinesupport&pid=1601") {
/*teknik destek sayfası*/
.flsTeamDetails{background:transparent ;} 
.flsTeamTitle{color :#fff;font-weight :bold; } 
.flsTeamDescription{color :#33f;font-weight :bold;  } 
.mainContent{background-image:url(http://www.merterdal.net/wp-content/uploads/2014/03/mert.jpg);background-position :top; background-size :160% 50%;}
.flsTeams{border-color :transparent;background-image :url(http://www.atomity.com/wp-content/uploads/2014/01/semi_transparent_black2.png);}
#member_info{font-weight :bold;text-shadow: #555 0px 0px 5px; }
.small{font-weight :bold;}
}
/*======================= Staff Sayfası ========================*/
@-moz-document url("http://www.arena-tr.com/?p=staff&pid=31") {
.staffname{background-color :#c7c7c7;}
#plugins {background-color :#e7e7e7;}
#plugins h4{color:#fa3;text-shadow: #a81 0px 0px 5px;}
#plugins {background-image :url(http://i.imgur.com/gVS8C.png);background-position :center;background-size :42% 42%;}
#menu {background-color:#e7e7e7; }
}
/*======================= Forum Ayarları ========================*/
@-moz-document url("http://www.arena-tr.com/?p=forums&pid=11") {
.f_icon{background-image :url(http://1.bp.blogspot.com/-tUEAq54_N2w/UFh3f_vxI3I/AAAAAAAAAJc/KsAcHOAZa94/s1600/blue_matrix-Banner.jpg);background-repeat :repeat-x ; background-size :188% 100% ;background-position :left;}

.f_title{color :transparent;font-weight :bold;  background-image :url(http://1.bp.blogspot.com/-tUEAq54_N2w/UFh3f_vxI3I/AAAAAAAAAJc/KsAcHOAZa94/s1600/blue_matrix-Banner.jpg);background-repeat :repeat-x ; background-size :20% 100% ; background-position :left;}

.f_last{color :transparent;font-weight :bold; background-image :url(http://1.bp.blogspot.com/-tUEAq54_N2w/UFh3f_vxI3I/AAAAAAAAAJc/KsAcHOAZa94/s1600/blue_matrix-Banner.jpg);background-repeat :repeat-x ; background-size :100% 100% ;background-position :left;}

.last_post_info{background-color:#c7c7c7;}
#forumCategory{background-color:#e7e7e7;}
#forumCategory table tbody td {background-color :#e7e7e7;}
.forumTable a {display :block;}
.floatright {display:none;}
.forumTableHeader{display:none;}
.subforums a {display:inline-block;}
.ol li a{width:40px;}
.forumList td {background-color :#e7e7e7;}
.forumList td a {background-color :#fff;width:200px;}
}

@-moz-document url("http://www.arena-tr.com/?p=request&pid=101") {

.smalldate{color:#000;}
}
