css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(fanfiktion.de), domain(forum.fanfiktion.de){
    body {background:#000 !important; color:#cccccc !important}
    a {color:#fff !important}
    p {color: #CCCCCC !important}
    em {color: #FFFFCC !important}
    h1 {color: #CCCCCC !important}
    h2 {color: #CCCCCC !important}
    .comments-header {background:#001000 !important}
    .comment-header {background:#405040 !important}
    .submenu-content {background:#4060a0 !important}
    .vendor-content-box {background:#303030 !important}
    .top-band-footer {background:#101000 !important}
    .box-content {background-color: #303030 !important;}
    #tooltip-body{color:white !important;}
    div.tooltip-bodystd {background-color: #000 !important;}
    .entry {margin: 0 0 3px !important; background:#ffffff !important;}
    div.entry {background:#303030 !important;}
    div.Qcon2 {background:#303030 !important;}
    div.vendor-content-box-float{background:#909090 !important;}
    div.ffclayer {background-color:#000 !important;}
    .sponsorship-html{background:#606060 !important;}
    #newsContent{font-size:1.2em !important;}
    .entryhome p{font-size: 1.2em !important;}
    div.story {background:#000;}
    div.story-text {color:white !important;}
    div.story-metadata-top {color:white !important;  background:#000;}
    div.story-metadata-left-top {color:white !important;  background:#000;}
    div.story-metadata-right-top {color:white !important;  background:#000;}
    #main { background:#000 !important; }
    #nav-left { background-color:#000 !important; }
    div.topic { background-color:#000 !important; }
    span.topic-selected { color:white !important; }
    div.action-menulink { color:white !important; }
    span.action-menulink { color:white !important; }
    span.action-jslink { color:white !important; }
    span.action-back { color:white !important; }
    #nav-top-bg { background: url("http://i1270.photobucket.com/albums/jj616/erdelf/nav_top_bg_zps9ca89a0f.png~original") repeat-x
        scroll 0% 0% transparent !important;}
    div.story-metadata-bottom { background-color:#000 !important; }
    div.socialshareprivacy { background-color:#000 !important; }
    .social_share_privacy_area { background-color:#000 !important; }
    .normaltext {color:white !important;}
    .smalltext {color:white !important;}
    .boldtext {color:white !important;}
    .dirtext {color:white !important;}
    span {color:white !important;}
    tr{background-color:#000 !important;}
    div.fdesc { color:white !important;}
    div.review-text, div.review-reply-text { color:white !important; }
    div.body {background-color:#000 !important;}
    div.review-metadata, div.review-reply-metadata{background-color:rgba(45, 45, 45, 0.61) !important;}
    #w-top{display:none !important;}
    div.titletxt, div.pagelinks:nth-child(1){color:white !important;}
    div.forumheader { border-bottom: 5px solid rgba(27,26,26,0.8)!important;}
    div.post{background:black !important;}
    div.answertext {color: white !important;}
    div.qa {color: white !important;}
    div.topic { color:white !important;}
    .pageviewframe > div:nth-child(19) {border-top: 1px solid rgba(27,26,26,0.8)}
}
