/* "userstyles.org dark theme" (v1.0) by Marc12345 */

@namespace url("http://www.w3.org/1999/xhtml");

@-moz-document url-prefix("https://userstyles.org") {
    ::-moz-selection {
        background-color:blue;
        color:white;
    }
    ::selection {
        background-color:blue;
        color:white;
    }
    body {
        background-color:black;
        color:white;
    }
    a, a:visited {
        color:rgb(0, 84, 255);
        text-decoration:none;
        transition:color .25s ease-in;
    }
    a:hover, a:visited:hover {
        color:rgb(0, 168, 255);
        text-decoration:underline;
    }
    header#main-header a, #left-sidebar a {
        color:#0000DD;
    }
    header#main-header {
        background-color:white;
        border-radius:0px;
        color:black;
    }
    #left-sidebar > nav, footer#related {
        background-color:white !important;
        border-radius:0px !important;
        color:black !important;
    }
    #left-sidebar > nav input {
        background-color:white;
        border:1px solid black;
        color:black;
    }
    #search-terms {
        font-family:"Consolas", monospace;
        width:140px;
    }
    #search-terms[value=""] {
        color:black;
        font-style:italic;
    }
    #search-submit {
        cursor:pointer;
        float:none;
        font-family:"Consolas", monospace;
        margin-left:-1px;
    }
    #search-submit:hover {
        background-color:lightgray;
    }
    .more {
        border-top-color:black;
    }
    #screenshots {
        border-radius:0px;
    }
    ul#style-settings {
        list-style-type:square;
    }
    ul#style-settings > li {
        margin-bottom:1px;
    }
    ul#style-settings select.style-setting-option {
        background-color:black;
        border:1px solid white;
        color:white;
    }
    ul#style-settings input.color {
        border:1px outset white;
    }
    /*ul#style-settings*/ input[type="text"],
    /*ul#style-settings*/ input[type="url"],
    input[type="submit"],
    input,
    textarea
    {
        background-color:black;
        border:1px solid white;
        color:white;
    }
    .install-status {
        border-radius:0px;
    }
    .install-info {
        background-color:#0064AA;
        background-image:none;
    }
    .install-info a {
        color:white !important;
        text-decoration:underline;
    }
    .install {
        background-color:#489615;
        background-image:none;
    }
    .install:hover {
        background-color:#5CAA29;
        background-image:none;
    }
    .install::-moz-selection, .install *::-moz-selection {
        background-color:transparent;
        color:white;
    }
    .install::selection, .install *::selection {
        background-color:transparent;
        color:white;
    }
    pre#view-code {
        background-color:black;
        border:1px solid white;
        color:white;
    }
    pre#view-code > code {
        font-family:"Consolas", monospace;
    }
    abbr {
        cursor:default;
    }
    ul#discussions > li:not([class]) {
        list-style-type:square;
    }
    body > div:nth-child(11) {
        background-color:black !important;
        border-color:white !important;
    }
    body > div:nth-child(11) > div > div:nth-child(1),
    body > div:nth-child(11) > div > div:nth-child(3)
    {
        border:2px solid gray !important;
    }
    /* no ads! */
    .ad {
        display:none;
    }
}

@-moz-document url-prefix("https://forum.userstyles.org") {
    ::-moz-selection {
        background-color:blue;
        color:white;
    }
    ::selection {
        background-color:blue;
        color:white;
    }
    body {
        background-color:black;
        color:white !important;
    }
    a, a:visited {
        color:rgb(0, 84, 255) !important;
        text-decoration:none;
        transition:color .25s ease-in;
    }
    a:hover, a:visited:hover {
        color:rgb(0, 168, 255) !important;
        text-decoration:underline;
    }
    #Head {
        background-color:black;
        box-shadow:0px 0px 5px white;
        color:white;
    }
    #Head h1 > a:hover {
        text-decoration:none;
    }
    #Head form > div > input[type] {
        background-color:black;
        border:1px solid white;
        border-radius:0px;
        box-shadow:none;
        color:white;
        font-family:"Consolas", monospace;
        text-shadow:none;
    }
    #Head form > div > input[type="text"] {
       margin-right:-1px;
    }
    #Head form > div > input[type="submit"] {
        background-image:none;
        font-weight:normal;
    }
    #Head form > div > input[type="submit"]:hover {
        background-color:white;
        border-color:black;
        color:black;
        outline:1px solid white;
    }
    .Box {
        background-color:white;
        border:none;
        border-radius:0px;
        box-shadow:none;
    }
    .PanelInfo {
        border-top:none;
    }
    .PanelInfo li {
        border-bottom:1px solid white;
    }
    .BoxCategories ul > li {
        background-color:black !important;
        border-radius:3px;
    }
    .BoxCategories ul > li.Active {
        background-color:white !important;
        box-shadow:inset 0px 0px 3px black;
    }
    span.Count {
        background-color:black;
        border:1px solid white;
        color:white;
    }
    .Active span.Count {
        background-color:white;
        border:1px solid black;
        color:black;
    }
    .BoxCategories
    .PanelCategories li:first-child,
    .PanelCategories li:nth-last-child(2)
    {
        border-bottom:2px solid white;
    }
    .User h1 {
        color:white;
    }
    .Tabs li a span {
        background-color:white;
        border:1px solid black;
        color:black;
    }
    #Body .Tabs, #Body .Foot {
        background-color:white;
        border:none;
        border-radius:0px;
        color:black;
    }
    #Body .Tabs .TabLink, #Body .Foot .TabLink {
        background-color:white;
        border:1px solid black;
        border-radius:0px;
    }
    /* .Tabs .SubTab {
        border:1px solid black;
        border-radius:0px;
    } */
    div.Title {
        font-family:"Consolas", monospace;
    }
    div.Meta > span {
        color:white;
    }
    .DataList .Meta .Announcement {
        border:1px solid white;
        background-color:red;
        color:white;
    }
    .ProfilePhotoMedium {
        outline:1px solid white;
    }
    body.Profile ul.Activities {
        border-top-color:white;
    }
    code, pre {
        background-color:black;
        border:1px solid white;
        border-radius:0px;
        color:white;
    }
    .DataList .Item {
        border-bottom-color:white;
    }
    .ItemContent > div.Excerpt,
    .Activities li.Condensed .Excerpt {
        color:white;
    }
    body ul div.Comment blockquote.UserQuote {
        background-color:black;
        border:1px solid white;
        color:white;
    }
    div.Comment blockquote.UserQuote div.QuoteText {
        border-left:1px solid white;
        color:white;
    }
    .Attachment:hover .FileHover {
        background-color:black;
        border:1px solid white;
        box-shadow:0px 0px 10px white;
        color:white;
    }
}
