css:
@-moz-document url-prefix("https://www.tumblr.com/dashboard"), url-prefix("https://www.tumblr.com/inbox"), url-prefix("https://www.tumblr.com/settings"), url-prefix("https://www.tumblr.com/likes"), url-prefix("https://www.tumblr.com/blog"), url-prefix("https://www.tumblr.com/followers"), url-prefix("https://www.tumblr.com/reblog"), url-prefix("https://www.tumblr.com/new"), url-prefix("https://www.tumblr.com/liked"), url-prefix("https://www.tumblr.com/edit"), url-prefix("https://www.tumblr.com/tagged"), url-prefix("http://www.tumblr.com/dashboard"), url-prefix("http://www.tumblr.com/inbox"), url-prefix("http://www.tumblr.com/settings"), url-prefix("http://www.tumblr.com/likes"), url-prefix("http://www.tumblr.com/blog"), url-prefix("http://www.tumblr.com/followers"), url-prefix("http://www.tumblr.com/reblog"), url-prefix("http://www.tumblr.com/new"), url-prefix("http://www.tumblr.com/liked"), url-prefix("http://www.tumblr.com/edit"), url-prefix("http://www.tumblr.com/tagged") {
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background:url('http://i.imgur.com/NoAVdgw.png') top no-repeat!important;
}
}

@-moz-document url-prefix("https://www.tumblr.com"), url-prefix("http://www.tumblr.com") {
body {
background: url('http://static.panoramio.com/photos/large/25901037.jpg') center #fefefe no-repeat fixed !important;
background-size:cover!important;
}

.search_form_field {
position: relative;
left: -9999px;
}

input#search_query,
input.search_query {
position: relative;
left: 10029px;
}
    
ol#posts li blockquote,
body.mceContentBody blockquote {
padding-left: 10px !important;
border-width: 4px !important;
}

.blue_bar {
background:none!important;
border:none!important;
border-bottom:none!important;
}

.section.first.divider {
background:none!important;
}

#sidebar_footer_nav.pinned_sidebar_footer {
display:none;
}

#right_column:after {
background:none !important;
}

.search_form_row {
background:rgba(255, 255, 255, 0.1)!important;
}

.selection_nipple {
display:none!important;
}

div#right_column {
background:none!important;
}

.chrome.blue,
.chrome.flat.close,
button.chrome,
.button.gray.light,
#cancel {
border-color:#d9d9d9!important;
background:#f0f0f0!important;
border-radius:0px !important;
color:grey!important;
}

span.follow_icon,
a.icon_plus {
color:white!important;
}

span.follow_icon:hover,
a.icon_plus:hover {
background:grey!important;
}

span.follow_icon {
border-radius:3px!important;
}
    
form.dashboard_options_form {
border:none!important
}

div.post_avatar {
background:none!important;
}

.chrome.blue.txt,
.chrome.blue.options,
.chrome.grey,
.send_fan_mail,#send {
background:#555!important;
color:#ddd!important;
}

a.post_permalink {
background-image:none!important;
color:white!important;
border-bottom-left-radius:15px!important;
border-top-right-radius:6px!important;
}

.plexi.active {
background:rgba(0,0,0,0.25)!important;
}
    
.plexi.color.show {
background:rgba(0,0,0,0.25)!important;
}

body#dashboard_iframe,
#ask_form {
background:none!important;
}
    
.ui_dialog_lock.opaque {
background:rgba(0,0,0,0.25)!important;
}    

a.follow:hover {
background:grey!important;
}
    
.post a:before,
div#right_column a:before {
background:none!important;
border-right:none!important;
}
}

@-moz-document url-prefix("https://www.tumblr.com/search"), url-prefix("http://www.tumblr.com/search") {
input{color:rgba(255,255,255,0.7)!important;}

.search_results_bottom_wrapper.posts_wrapper,
#container:before { 
background:none!important;
}
    
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background:none!important;
}
    
.ui_peepr_glass{
background:rgba(0,0,0,0.25)!important;
}
    
span.tumblelog_mask_item_fader,
span.tumblelog_mask_item.left{
background:none!important;
}
}

@-moz-document url-prefix("https://www.tumblr.com/reblog"), url-prefix("https://www.tumblr.com/new"), url-prefix("https://www.tumblr.com/blog"), url-prefix("https://www.tumblr.com/inbox"), url-prefix("https://www.tumblr.com/edit"), url-prefix("http://www.tumblr.com/reblog"), url-prefix("http://www.tumblr.com/new"), url-prefix("http://www.tumblr.com/inbox"), url-prefix("http://www.tumblr.com/edit") {
body#tinymce {
background:white!important;
}

html {
background:inherit!important;
}
}
