css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain('facebook.com'){

html,
body.fbIndex,
body.fbIndex > div,
body.fbIndex #pagelet_bluebar,
body.fbIndex #blueBarHolder,
body.fbIndex #blueBar > div,
body.fbIndex #blueBar,
body.fbIndex .loggedout_menubar_container{
	height: 100% !important;
}

body.fbIndex{
	background-color: /*[[background]]*/ !important;
}

body.fbIndex .loggedout_menubar_container{
	padding: 0 20px;
	position: relative !important;
}

body.fbIndex .loggedout_menubar_container,
body.fbIndex .loggedout_menubar_container,
body.fbIndex .loggedout_menubar{
	min-width: 500px !important;
}

body.fbIndex #blueBarHolder #blueBar,
body.fbIndex .loggedout_menubar_container{
	background: none !important;
}

body.fbIndex .loggedout_menubar{
	position: relative !important;
	left: 0 !important;
	width: 100% !important;
	height: 550px !important;
	padding: 0 !important;
	margin: 0 !important;
	text-align: center !important;
}

body.fbIndex .loggedout_menubar > a,
body.fbIndex .menu_login_container{
	clear: both !important;
	float: none !important;
}

body.fbIndex .loggedout_menubar > a{
	text-align: center !important;
}

body.fbIndex .loggedout_menubar > a:before{
	text-shadow: 4px 4px 0px rgba(250, 250, 250, 0.4) !important;
	color: #eee !important;	
}

body.fbIndex input{
	color: #eee !important;	
	text-shadow: 2px 2px 0px #444 !important;
}

body.fbIndex .loggedout_menubar > a:before{
	display: inline-block !important;
	content:'FACEBOOK LOGIN' !important;
	font-size: 50px !important;
	font-weight: bold !important;
	text-align: center !important;
}

body.fbIndex .loggedout_menubar > a i,
body.fbIndex .signup_bar_container,
body.fbIndex #blueBarHolder #blueBar:after,
body.fbIndex #globalContainer,
body.fbIndex #login_form table tbody > tr:first-child,
body.fbIndex #login_form table tbody > tr:last-child{
	display: none !important;
}

body.fbIndex #login_form table,
body.fbIndex #login_form table tbody,
body.fbIndex #login_form  table tr,
body.fbIndex #login_form  table tr td{
	display: block !important;
	padding: 0 !important;
}

body.fbIndex #login_form  table tr td{
	padding-top: 20px !important;
}

body.fbIndex #login_form table tbody tr input,
body.fbIndex #login_form table tbody tr #loginbutton{
	width: 100% !important;
	height: 100px !important;
	font-size: 70px !important;
	text-align: center !important;
	padding: 0 !important;
}

body.fbIndex #login_form table tbody tr input{
	background: rgba(250, 250, 250, 0.5) !important;
	border-color: rgba(0, 0, 0, 0.25) !important;
	-webkit-box-shadow: 0px 0px 4px 1px rgba(50, 50, 50, 0.15) !important;
	-moz-box-shadow: 0px 0px 4px 1px rgba(50, 50, 50, 0.15) !important;
	box-shadow: 0px 0px 4px 1px rgba(50, 50, 50, 0.15) !important;
}

body.fbIndex #login_form table tbody tr #loginbutton input{
	text-shadow: 3px 3px 0px rgba(0, 0, 0, 0.35)!important;
	transition: text-shadow 1s ease !important;
	-webkit-transition: text-shadow 1s ease !important;
	-moz-transition: text-shadow 1s ease !important;
}

body.fbIndex #login_form table tbody tr input:hover,
body.fbIndex #login_form table tbody tr input:focus,
body.fbIndex #login_form table tbody tr #loginbutton:hover input{
	background: rgba(250, 250, 250, 0.8) !important;
	border-color: rgba(0, 0, 0, 0.25) !important;
	text-shadow: -3px 3px 0px rgba(0, 0, 0, 0.35)!important;
	transition: text-shadow 1s ease !important;
	-webkit-transition: text-shadow 1s ease !important;
	-moz-transition: text-shadow 1s ease !important;
	-webkit-box-shadow: 0px 0px 4px 1px rgba(50, 50, 50, 0.35) !important;
	-moz-box-shadow: 0px 0px 4px 1px rgba(50, 50, 50, 0.35) !important;
	box-shadow: 0px 0px 4px 1px rgba(50, 50, 50, 0.35) !important;
}

body.fbIndex #login_form table tbody tr #loginbutton{
	padding: 0 !important;
	border-color: transparent !important;
}

body.fbIndex .menu_login_container .inputtext, 
body.fbIndex .menu_login_container .inputpassword,
body.fbIndex .uiButtonConfirm{
	background: rgba(250, 250, 250, 0.65) !important;
}

body.fbIndex #login_form table tbody tr #loginbutton input{
	color: /*[[background]]*/!important;
}
body.fbIndex .uiButtonConfirm,
.uiButton, 
.uiButtonSuppressed:active, 
.uiButtonSuppressed:focus, 
.uiButtonSuppressed:hover{
	background-image: none !important;
}

}
