/*============================================================
  IWJ ja jp liquid compact 2014/02
  ============================================================*/

/*=== Global ===*/
@-moz-document domain("iwj.co.jp"){

/*--- unwanteds ---*/


/*--- generic containers ---*/
body {
 margin: 0 !important; padding: 0 !important;
 text-aling: left !important; font-family: inherit !important; font-size: inherit !important;
 }
#container, #wrapper
 {width: auto !important; margin: 0 !important; padding: 0 !important;}

}

/*=== Top page ===*/
@-moz-document url("http://iwj.co.jp/"), url("https://iwj.co.jp/"){
/*=== TOP PAGE ===*/
/*--- top page 2columns layout ---*/
#main
 {width: 100% !important; margin: 0 -150pt 0 0 !important; padding: 0 !important;}
#main #wrap-o
 {width: auto !important; margin: 0 150pt 0 0 !important; padding-right: 0.5em;}
#wrap-o * {box-sizing: border-box; max-width: 100% !important;}
#twitter {width: 150pt !important; margin: 0 !important;}
#twitter * {box-sizing: border-box; max-width: 150pt !important;}

/*--- top page header ---*/
#header {margin: 0 !important; padding: 0 !important;}
#header h1
 {width: auto !important; height: auto !important; background-size: 100% 100%;}
#header #site-description {
 position: static !important;
 width: auto !important;
 font-family: inherit !important; text-indent: 1em;
 }
#header .login, #header .register
 {position: static !important; display: inline-block !important;}
#header .profile {position: static !important; float: right;}

/*--- top page footer ---*/
#footerArea {
 position: static !important;
 z-index: auto !important;
 width: auto !important; margin: 0 !important; padding: 0 !important;
 }

/*--- top page top topics 2clolumns with grid layout ---*/
#top {width: auto !important;}
#top #grid {
 float: right;
 width: 100% !important; height: auto !important;
 margin: 0 0 0 -100pt !important;
 }
table#grid_panel
 {display: block !important; width: auto !important; margin: 0 0 0 100pt !important;}
table#grid_panel tbody, table#grid_panel tr {display: block !important;}
table#grid_panel td {display: block !important; float: left; width: 170pt !important;}
table#grid_panel td ul li {background-size: 100% auto !important;}
div#selection {box-sizing: border-box; width: 100pt !important; margin: 0 !important;}

}

/*=== Archives page ===*/
@-moz-document url-prefix("http://iwj.co.jp/wj/open/archives/"), url-prefix("https://iwj.co.jp/wj/open/archives/"){

#header, .grid, #footer {padding: 0 !important;}
.single-format-standard #header #title,
.single-format-standard #header #description
 {margin: 0 !important; font-size: inherit !important;}
.single .grid-item.post-section, .page .grid-item.post-section
 {width: auto !important; font-size: 1em !important;}
.gads-footertext-view, .gads-headerimage
 {width: auto !important; margin: 0 !important; padding: 0 !important;}
#wrapper div.grid {width: auto !important; height: auto !important;}
#wrapper div.grid>div {position: static !important;}
.single .post-title, .page .post-title {margin: 0 !important; font-size: inherit !important;}
.single .post-content, .page .post-content, .comment-list p {font-size: inherit !important;}
.post-date, .post-title, .post-content, .post-feedback, .sidebar-title, .sidebar-content,
.comment-title, .comment-list
 {margin: 0 !important;}
.post-content p, .post-content h3 {
 margin: auto !important;
 font-size: inherit !important; line-height: inherit !important;
 }

}
