css:
@-moz-document regexp("https?://www\\.tumblr\\.com/((login|dashboard|new/blog|inbox|help|preferences|lookup|explore|following|likes)|(blog|dashboard|new|tumblelog|tagged|spotlight|edit)/.*)") {

/* blue fix */
/*div.search_form_row*/#search_query {background-color: rgba(133, 139, 216, .5)}
div.l-header .selection_nipple {border-bottom-color: rgba(133, 139, 216, .5)}

#left_column, #right_column {
	background-color: transparent;
}
.controls_section.user_list li.item .follow_list_item_blog:before {
  background-image: none !important;
  border-right: none !important;
  }
.radar div.radar_footer .radar_avatar:before {
  background-image: none;
  }
#right_column:after {display:none}

body:not(.mceContentBody) {
/*  background:url("http://i.imgur.com/YLdvo.jpg") no-repeat fixed !important;
  background-color: rgba(44,71,98, 0.95) !important;
  background-position: 0% 0% !important;*/
  background: url('https://33.media.tumblr.com/182242248b76eddcb57ed899713db14e/tumblr_ndpuh3UxN81rxghfko2_1280.jpg') fixed no-repeat top left rgb(218,226,247) !important;
  background-size: auto 100% !important;
}


ol#posts li blockquote,
body.mceContentBody blockquote {
margin-left: 0 !important;
margin-right: 0 !important;
padding-left: 10px !important;
border-width: 4px !important;
border-color: #a8bccf !important;
}
ol#posts li blockquote blockquote,
body.mceContentBody blockquote blockquote {
border-color: #839aaf !important;
}
ol#posts li blockquote blockquote blockquote,
body.mceContentBody blockquote blockquote blockquote {
border-color: #6b7d8f !important;
}
ol#posts li blockquote blockquote blockquote blockquote,
body.mceContentBody blockquote blockquote blockquote blockquote {
border-color: #4c5e6f !important;
}
ol#posts li blockquote blockquote blockquote blockquote blockquote,
body.mceContentBody blockquote blockquote blockquote blockquote blockquote {
border-color: #36434f !important;
}
div.l-content.l-content.l-content.l-content {
background: rgba(133, 139, 216, .5);
border-radius: 20px;
margin: auto;
padding: 20px 20px;
}

h1 {
color: #666666;
text-shadow: 0 0 3px #ffffff;
}
.no_posts_found, h1.dashboard_header {
color: #666666;
}
img#content_top, img#content_bottom {
display: none;
}
#right_column {
position: relative;
background-color: transparent;
float: right;
width: 215px;
color: white;
margin-top: -1px;
margin-right: -1px;
padding-bottom: 20px;
}

div.l-header .selection_nipple {
width: 0;
height: 0;
border-left: 12px solid transparent;
border-right: 12px solid transparent;
border-bottom: 12px solid #AFB6E7;
font-size: 0;
position: relative;
top: 4px;
vertical-align: top;
margin: 0 auto;
display: none;
}

ol#posts li.post .permalink {
background: #9F85F1;
}

ol#posts li.notification {
background-color: rgba(149, 157, 219, 1);
border-bottom: 1px solid white;
}

ol#posts li.notification.first_notification {
background-color: rgba(149, 157, 219, 1);
}

#posts .notification {
width: 464px;
color: rgba(255, 255, 255, .45);
font-family: "Lucida Grande","Helvetica Neue",Helvetica,Arial,sans-serif;
font-size: 11px;
line-height: 19px;
background-color: #959DDB;
border-left: solid 1px white;
border-right: solid 1px white;
border-bottom: solid 1px white;
-webkit-background-clip: padding-box;
margin: 0 0 0 -1px;
padding: 7px 65px 7px 11px;
position: relative;
}

#posts .notification.single_notification {
clear: both;
border: solid 1px white;
margin-top: 20px;
-webkit-border-radius: 6px;
-moz-border-radius: 6px;
border-radius: 6px;
}

ol#posts li.notification.last_notification {
border-bottom: 1px solid white;
}

#posts .notification.first_notification {
clear: both;
border-top: solid 1px white;
margin-top: 20px;
-webkit-border-top-left-radius: 6px;
-webkit-border-top-right-radius: 6px;
-moz-border-radius-topleft: 6px;
-moz-border-radius-topright: 6px;
border-top-left-radius: 6px;
border-top-right-radius: 6px;
}

#posts .notification .nipple {
width: 0;
height: 0;
border-top: 6px solid transparent;
border-bottom: 6px solid rgba(255, 255, 255, 0);
border-right: 6px solid white;
position: absolute;
left: -5px;
top: 10px;
}

#posts .notification .nipple.border {
border-top: 7px solid transparent;
border-bottom: 7px solid transparent;
border-right: 7px solid white;
position: absolute;
left: -7px;
top: 9px;
}

#posts .notification.alt .nipple {
border-right: 6px solid white;
}

#right_column a#dashboard_switch_blog_menu_current #dashboard_switch_blog_arrow_current {
background: none;
}

#nav .nav_item.active {
background: rgba(0,60,100,0.5) !important;
}
#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {
background: rgba(0,60,100,0.5) !important;
}
ol#posts li.notification.alt {
background-color: rgba(149, 157, 219, 1);
}
ol#posts li.notification.first_notification {
background-color: rgba(149, 157, 219, 1);
}
ol#posts li.notification {
background-color: rgba(149, 157, 219, 1);
border-bottom: 1px solid #fff;
}
ol#posts li.notification.last_notification {
border-bottom: 1px solid #fff;
}
ol#posts li.post .permalink {
background: #9F85F1;
}
#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs {
background: rgba(0,0,0,0.8);
border: solid #fff;
}
ol#posts li.post .post_avatar{
border-bottom: 0px solid #0A2939;
}

form.dashboard_options_form {
background-color: rgba(0,0,0,0.7);
border-bottom: 1px solid #fff;
border-bottom-left-radius: 10px 10px;
border-bottom-left-radius: 10px 10px;
border-bottom-right-radius: 10px 10px;
border-bottom-right-radius: 10px 10px;
border-top-left-radius: 10px 10px;
border-top-left-radius: 10px 10px;
border-top-right-radius: 10px 10px;
border-top-right-radius: 10px 10px;
color: #cccccc;
display: block;
font-size: 13px;
margin-bottom: 25px;
padding: 15px 22px;
}
form.dashboard_options_form .option_container {
border-top: 1px solid #fff;
}
.no_posts_found, h1.dashboard_header {
color: #666666;
}

DIV.plexi.show.color {background-color: rgba(133, 139, 216, .8)}
/*
  'new post' icons
*/
.new_post_label {
  padding: 72px 1px 7px 0 !important;
}

.new_post_label > SPAN {
  text-transform: lowercase;
  color: rgba(133, 139, 216, 1);
}

.new_post_label > I::before {content: none}
A.new_post_label > I {
  height: 90px;
  background: /*url('http://i.imgur.com/MzMKA.png')*/url('https://38.media.tumblr.com/d0eeb83c197bacc87b586a6d9994481c/tumblr_ndpuh3UxN81rxghfko1_1280.png') no-repeat;
  /*background-size: auto;*/
  transition: background-position .08s linear;
  z-index: -1;
}
#new_post_label_text > I {background-position: -8px 5px}
#new_post_label_photo > I {background-position: -85px 5px}
#new_post_label_quote > I {background-position: -162px 5px}
#new_post_label_link > I {background-position: -239px  5px}
#new_post_label_chat > I {background-position: -316px 5px}
#new_post_label_audio > I {background-position: -393px 5px}
#new_post_label_video > I {background-position: -470px  5px}

#new_post_label_text:hover > I {background-position: -8px 1px}
#new_post_label_photo:hover > I {background-position: -85px 1px}
#new_post_label_quote:hover > I {background-position: -162px 1px}
#new_post_label_link:hover > I {background-position: -239px  1px}
#new_post_label_chat:hover > I {background-position: -316px 1px}
#new_post_label_audio:hover > I {background-position: -393px  1px}
#new_post_label_video:hover > I {background-position: -470px  1px}

#new_post_label_text:active > I {background-position: -8px 5px}
#new_post_label_photo:active > I {background-position: -85px 5px}
#new_post_label_quote:active > I {background-position: -162px 5px}
#new_post_label_link:active > I {background-position: -239px 5px}
#new_post_label_chat:active > I {background-position: -316px 5px}
#new_post_label_audio:active > I {background-position: -393px 5px}
#new_post_label_video:active > I {background-position: -470px 5px}

}
