css:
@-moz-document domain("n0l.ch") {
html, body, time, div.body.hyphenate {
  	font-size: 1em !important;
  	font-family: "Trebuchet MS",Trebuchet,serif !important;
}
html {
	background: white repeat-x !important;
	background-image: url("https://lh5.googleusercontent.com/-9mPfq_bTK4o/T4qBmWVF8eI/AAAAAAAAAMQ/ZojfswVTVrQ/I/backgrnd777.jpg"),url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAIAAAAVCAYAAABsZT8HAAAAAXNSâ€¦wQhmMDAwMDIzIUoyMOBkMmFIk6SJCMYxRH+rKwMDAwAAAGzcCz5PMGZsAAAAASUVORK5CYII=") !important;
	overflow: visible !important;
}
body {
	background: transparent !important;
  	color: black !important;
  	margin: 25px 8px 8px !important;
}
* {
	text-shadow: none !important;
}
* {
  	font-family: "Trebuchet MS", Trebuchet, serif, tahoma, verdana ,arial, sans-serif, Lucida Sans, monospace !important;
}
a, span.mentioned.unimportant {
	color: #034 !important;
  	text-decoration: underline !important;
  	font-family: tahoma, verdana ,arial, sans-serif, Lucida Sans !important;
}
a.post_no, a.post_no:hover {
  	color: black !important;
	font-family: tahoma,verdana,arial,sans-serif,Lucida Sans !important;
  	text-decoration: none !important;
	font-size: 0.8em !important;
	font-weight: bold !important;
}
a.hide-thread-link, a.unhide-thread-link {
	font-size: 0px !important;
}
a.hide-thread-link:before {
	content: url(http://i64.fastpic.ru/big/2014/0705/44/cf9123c36033f851c848ebb6796d7e44.png) !important;
}
a.unhide-thread-link:before {
	content: url(http://i62.fastpic.ru/big/2014/0705/95/1aa938b5944937432d01850573170995.png) !important;
}
div#menu.menu *, div#menu.menu, footer {
	text-decoration: none !important;
  	color: #ccc !important;
	font-size: 11px !important;
	background: black !important;
  	text-decoration: none !important;
}
div#menu.menu ul, div#menu.menu ul * {
	font-weight: bold !important;
  	font-family: tahoma !important;
}
footer pre.unimportant {
	line-height: 1.2em !important;
}
h1, .unimportant:not(pre), div#blotter.blotter {
	font-weight: bold !important;
	text-decoration: none !important;
  	color: #034 !important;
	font-size: 11px !important;
	font-family: tahoma,verdana,arial,sans-serif,Lucida Sans !important;
  	letter-spacing: 0px !important;
}
div#blotter.blotter a {
	font-size: 11px !important;
	font-family: tahoma,verdana,arial,sans-serif,Lucida Sans !important;
}
hr {
	border-color: black !important;
	opacity: 0.15 !important;
	z-index: -1 !important;
  	border-style: solid !important;
  	border-width: 1px !important;
  	height: 0px;
}
a pre.unimportant {
	color: #ccc !important;
}
div#blotter.blotter {
  	background: transparent !important;
}
div#menu.menu a:before {
	content: "[    " !important;
	font-weight: normal !important;
  	color: #ccc !important;
}
div#menu.menu a:after {
	content: "    ]" !important;
	font-weight: normal !important;
  	color: #ccc !important;
}
div#menu.menu a:hover, div#menu.menu a:hover strong, a:hover pre.unimportant, span.spoiler:hover a {
	color: white !important;
}
div#menu.menu {
	top: 0px !important;
}
.body-borders {
	display: none !important;
}
span.subject {
	color: #002244 !important;
	font-size: 1.15em !important;
}
div.post.reply {
	background: none #eee !important;
	border-radius: 5px !important;
  	border: 1px solid #ccc !important;
}
div.post.reply.highlighted div.body.hyphenate {
	color: black !important;
}
div.post.reply.highlighted, div.post.reply.post-hover {
  	border-radius: 5px !important;
	-moz-border-radius: 5px !important;
	-webkit-border-radius: 5px !important;
	box-shadow: grey -1px -1px 4px,grey 1px 1px 8px !important;
  	-moz-box-shadow: grey -1px -1px 4px,grey 1px 1px 8px !important;
	-webkit-box-shadow: grey -1px -1px 4px,grey 1px 1px 8px !important;
	color: black !important;
}
div.banner, div.banner a {
	background: transparent !important;
  	font-size: 1em !important;
  	font-family: tahoma,verdana,arial,sans-serif,Lucida Sans !important;
  	font-weight: normal !important;
  	color: black !important;
  	padding: 4px !important;
}
div.banner a {
  	color: #034 !important;
  	text-decoration: underline !important;
  	font-family: tahoma, verdana ,arial, sans-serif, Lucida Sans !important;
}
select {
	padding-left: 0px !important;
}
table {
	display: table !important;
	padding: 10px 20px !important;
	background: rgba(255,255,255,0.4) !important;
	border: 1px solid #A6A697 !important;
	border-radius: 5px !important;
	width: auto !important;
}
form table tr th {
	padding: 4px !important;
}
tbody tr th, select, span#toggle-frame, span#toggle-gallery, textarea, input, div.pages {
	font-family: sans-serif !important;
	-moz-appearance: none !important;
  	color: black !important;
	font-size: 12px !important;
	border: 1px solid #ccc !important;
	background: #eee !important;
}
div.pages {
	padding: 8px !important;
}
input[type=submit] {
	height: 23px !important;
  	font-weight: bold !important;
}
span#toggle-frame, span#toggle-gallery {
	font-weight: bold !important;
}
span#toggle-frame:hover, span#toggle-gallery:hover {
	opacity: 1 !important;
}
span.mentioned.unimportant a {
	font-weight: normal !important;
	font-size: 11px !important;
  	font-family: tahoma, verdana ,arial, sans-serif, Lucida Sans !important;
}
span.spoiler a {
	color: black !important;
}
span.mark span.spoiler, span.spoiler span.mark {
	background: black !important;
  	color: black !important;
}
span.mark {
	color: black !important;
  	background: yellow !important;
}
input#sbmt_btn2 {
	margin-left: 6px !important;
}
span.mark span.spoiler:hover, span.spoiler:hover span.mark {
	background: brown !important;
  	color: white !important;
}
span.quote {
	color: #539 !important;
}
span.quote span.spoiler:hover {
	color: #A375FF !important;
}
p.fileinfo {
	margin: 0 0 0 20px !important;
	padding: 0 !important;
	font-size: 0.8em !important;
}
p.fileinfo span.unimportant, span.unimportant span.image_id a {
	font-weight: normal !important;
  	font-size: 13px !important;
}
p.fileinfo, p.fileinfo span.unimportant, span.omitted, span.toolong, summary {
	color: #666 !important;
}
code *, code, * code, pre.unimportant, pre#ascii {
	font-family: monospace !important;
  	font-weight: normal !important;
}
}
