css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
    domain("adf.ly"),
    domain("ay.gy"),
    domain("darkhax.net"),
    domain("j.gs"),
    domain("q.gs")
{

/***** ELIMINAR PUBLICIDAD *****/

#CashSlideDiv, #cookie_notice {
    display: none !important;
}

#rf {
    visibility: hidden !important;
}


/***** BORRAR IMAGEN *****/

#bottom, #top, #bottomAd, #top span {
    background-image: none !important;
}


/***** COLORES DE BORDE *****/

.sm_content {
    border-color: rgb(0, 64, 128) !important;
}


/***** COLORES DE FONDO *****/

.sm_content {
    background-color: rgb(8, 8, 8) !important;
}

html, body, td {
    background-color: rgb(16, 16, 16) !important;
}

#sitebar {
    background: -moz-linear-gradient(center top , rgb(24, 24, 24), rgb(0, 0, 0)) repeat scroll 0% 0% transparent !important;
}

#bottom {
    background-color: rgb(8, 8, 8) !important;
    border: 1px solid rgb(0, 0, 0) !important;
    color: #0099FF !important;
}

#bottomAd {
    background-color: transparent !important;
}

#bee {
    border-radius: 0px 0px 16px 0px !important;
}


/***** COLORES DE LETRAS *****/

p {
    color: rgb(255,255,255) !important;
}


/***** COLORES DE ENLACES *****/

#bottom a, #bottom a:link, a {
    color: #00BBFF !important;
}
#bottom a:hover, a:hover {
    color: #0099FF !important;
}
#bottom a:visited, a:visited {
    color: #0066CC !important;
}
}
