css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://cs-mapping.com.ua/forum/") {
    body {
        background-color: #191919 !important;
    }
    body center table, body form div div.smallfont {
        display: none !important;
    }
    select, input, textarea {
        background-color: #404040 !important;
        color: white !important;
    }
    div.page, td.alt1, td.alt1Active, div.panel {
        background-color: #404040 !important;
    }
    table.tborder {
        background-color: #323232 !important;
        border: 1px solid #212121 !important;
    } 
    div.panel {
        border: 1px outset !important;
    }
    td.alt2, td.panelsurround, td#vBulletin_editor, #controlbar, .controlbar, div.imagebutton, span.imagebutton, div#vBulletin_editor, pre.alt2 {
        background-color: #373737 !important;
    }
    td.thead, td.tcat, td.vbmenu_control {
        background-image: url(http://5.firepic.org/5/images/2014-07/08/07m2yb9s5z0r.png) !important;
    }
    img.inlineimg {

    }
    td.tfoot {
        background-color: #373737 !important;
    }
    hr {
        color: #1f1f1f !important;
    }
}
