css:
@-moz-document domain("otvet.mail.ru") {
* {
		border-radius: 0.4em  !important;
		font-family: Ariel Black, Tahoma !important ;
	}

    a.nav-header__link:nth-child(2),
    a.nav-header__link:nth-child(3),
	#Stats,
    .OrFrame,
    .DayLead,
    .trg-b-list,
    .torg_mail_ru__200x300,
    .logoWhite3Head,
    .question-leaders,
    .category-leaders,
    .torg-answer-banner__content-wrapper 
    {display: none !important}

	h,h1,h2,h3,h4,h5,[class*="orange"], #questext *, .questext {
		color: hsl(0,0%,0%) !important;
/* 		text-shadow: -0.1em 0.1em 0.05em hsl(210,50%,70%) !important */
	}

	.answ-now-top,.answ-now-last,button,#button{
/* 		background: linear-gradient(90deg, hsla(220,100%,50%,.3),hsla(60,100%,50%,1),hsla(220,100%,50%,.3)) !important; */
        background: hsla(33,100%,60%,1) !important;
        
	}
     .a--atext {
        max-width: 200em;
        line-height: 1.3;
  }
	body>*, body, .Answ, .Quesstions *
    ,#menu, menu, .page, .column, 
    #portal-headline {
        border: hsl(210,50%,50%) !important;
		background: linear-gradient(45deg, hsl(51,100%,90%),hsl(120,6%,100%)) !important;
		color: hsl(180,41%,31%) !important;
		/*border: 0.5px solid !important;  */
		/* text-shadow: 0.1em 0.1em 0.05em hsl(210,30%,70%) !important; */
		
	}

	label,#label{
		/* background: hsl(210,30%,70%) !important; */
		color: hsl(82,56%,42%) !important;
		/* border: hsl(210,30%,50%) !important; */
		text-shadow: 0.1em 0.1em 0.05em hsl(210,30%,70%) !important;
	} 

	a:visited{
		color: hsl(275,100%,51%) !important;
	}

	a:hover{
		background: hsl(120,76%,80%) !important;
		color: hsl(25,86%,55%) !important;
	}

	a:link, a:active{
		/* background: hsl(210,60%,70%) !important; */
/* 		color: hsl(210,100%,30%) !important; */
	}

	input,text,#input,#text,edit,#edit,message,#message,option,#option,select,#select,.form--text{
		background: hsl(180,6%,100%) !important;
		color: hsl(0,0%,0%) !important;
		border: hsl(210,30%,50%) !important;
/* 		text-shadow: 0.1em 0.1em 0.05em hsl(210,30%,70%) !important; */
		font-family: DejaVu Sans !important ;
	}
.column_left, .column_right {
padding: 1.4em 1.25em;
width: 0em;
min-width: 0em;
background-color: #f6f6f6;
padding-bottom: 100em;
}
.a--box { 
border-left: 2px solid #FFFFFF;
border-top: 1px dotted #D3D6D8;
margin-top: -1px;
padding: 1.4em;
position: relative;
}
.a--ava {
top: -0.2em;
left: 2.5em;
}
.w-pm-menu {
background: #4682B4;
}
.pm-menu {
background: #4682B4;
}
.pm-toolbar__search__container {
background: hsl(210,50%,50%) !important
}

.a--atext img { 
max-width: 100%;
}
.wrapper { 
margin: 0px;
}
.com img { 
margin-bottom: 1em;
max-width: 100%;
}
.column_center { 
max-width: 100%;
}

.wrapper_page_width {
    min-width: 120em !important
}
