css:
@namespace url("http://www.w3.org/1999/xhtml");
@-moz-document domain("elephant.2chblog.jp") {
    h1,#cinfo,#social {
        display: none;
    }
    #topRss,li.iTwt,li.iHtb {
        display: none!important;
    }
    div.article {
        padding-bottom: 5px;
    }
}
@-moz-document domain("invariant0.blog130.fc2.com") {
    div.content:nth-child(1) {
        display: none!important;
    }
    div.sidemenu_content:nth-child(4) {
        display: none!important;
    }
}
@-moz-document domain("morikinoko.com") {
    section:not(#comments),.ad-wrap2,.ad-wrap1,.rss-middle {
        display: none!important;
    }
    .news-wrap ,.blogroll-top-wrap ,.hl-about,.article-social-btn ,div.plugin-memo:nth-child(2),div.plugin-memo:nth-child(6),div.plugin-memo:nth-child(7),div.plugin-memo:nth-child(8),.content-wrap {
        display: none!important;
    }
    .post_no_waku {
        line-height: 0.5;
    }
    .mtex,.mtex-link {
        color: #252525;
        font-family: 'Hiragino Kaku Gothic Pro', 'ヒラギノ角ゴ Pro W3', 'ＭＳ Ｐゴシック', 'MS PGothic', 'Osaka', sans-serif;
        font-size: 1em;
        font-weight: bold;
    }
    .commenttext {
        margin: 2px;
    }
    .well {
        padding: 5px 10px;
    }
    .mtexb2 {
        margin-top: 20px;
    }
}
@-moz-document domain("minnanohimatubushi.2chblog.jp") {
    div.blog-title-outer,div.RSS,div.RSS1,div.PR, .garss_category_2,
    .article-body-inner>iframe,.article-body-more>iframe{
        display: none!important;
    }
    div#container {
        width: 860px;}
    div#main {
         width:860px;
        border: 2px solid #347BB0;
    }
    .wareclass_frame,
    div#sub,
    div.popularArticlesWithImage.tag.A{
        display: none!important;
    }
    div#main div.column-inner{
        margin-left:0;
        width:850px!important;
        border:none;
    }

    div.article-body-inner{
        margin:0 5px 0 30px;
    }
    
}
@-moz-document domain("s2-log.com") {
    section:not(#comments),.ad-wrap2,.ad-wrap1,.rss-middle {
        display: none!important;
    }
    .news-wrap ,.blogroll-top-wrap ,.hl-about,.article-social-btn ,div.plugin-memo:nth-child(2),div.plugin-memo:nth-child(6),div.plugin-memo:nth-child(7),div.plugin-memo:nth-child(8),.content-wrap {
        display: none!important;
    }
    .post_no_waku {
        line-height: 0.5;
    }
    .mtex,.mtex-link {
        color: #252525;
        font-family: 'Hiragino Kaku Gothic Pro', 'ヒラギノ角ゴ Pro W3', 'ＭＳ Ｐゴシック', 'MS PGothic', 'Osaka', sans-serif;
        font-size: 1em;
        font-weight: bold;
    }
    .commenttext {
        margin: 2px;
    }
    .well {
        padding: 5px 10px;
    }
    .mtexb2 {
        margin-top: 20px;
    }
}
@-moz-document domain("ssmatomeantenna.info") {
    #headerconteinar,#rightbox {
        display: none!important;
    }
}
@-moz-document domain("horahorazoon.blog134.fc2.com") {
    div#left div.plugin-freearea {
        display: none;
    }
    h1,ul#nav,div.garss,div.horazon,div.navi2,div.osusumentry {
        display: none !important;
    }
}
