css:
@-moz-document domain("mydramalist.com") {
#content-top{display:none;}

.showNotes{display:none;}

.sort1 .btnManageList{display:none;}
.sort1:hover .btnManageList{display:block;}

.sort5{display:none;}
.section.mylist .table tr th:nth-child(6){display:none;}

.spnsr_right{display:none !important;}

#content{display:block;}
#wrapper{margin:0 auto;width:100%;overflow:hidden;}

@-webkit-keyframes test123 {
    from {right: -180px;}
    to {right: 0px;}
}

#header{width:100% !important;margin:0;padding:0;background:#3783bb;margin-bottom:15px;z-index:1000000000;position:relative;}
#header .login-form{float:right;width:150px;height:70px;margin:0;}

#menu-main{width:auto;height:70px;}

#menu-main .menu{width:400px;height:70px;float:left;margin-top:0px;}
#menu-main .menu>li{}
#menu-main .menu>li>a:hover{background: #2f649f;}

#menu-main .menu>li .dropdown-menu .item{float:none;clear:both;min-width: 0;}
#menu-main .menu>li .dropdown-menu{width:auto !important;max-width:none;border-radius:0 0 6px 6px;}

#menu-main .menu>li>a i{display:none;}

/*[[langue]]*/

#mainSearch{float:left;}

#header .login-form #notifications-dropdown{width:50px;height:50px;margin:9px;}

#header .login-form .account{margin:0px;padding:0 !important;}
body{background:#fff;}

#header .login-form .notifications{width:auto;height:auto;margin:0;}

#header .header-account .username{line-height:70px;padding:0 10px;}

#header .header-account .account:hover{background:#2f649f;}
#header .header-account{margin:0;padding:0;height:70px;}

#header .header-account .account .dropdown-box, #header .header-account .account .dropdown-menu{right:0px;}

#header .header-account .account .dropdown-menu{margin-top:0;border-top:0;border-radius:0 0 6px 6px;margin-right:10px;min-width:200px;text-align:left;}

#header .account.open .dropdown-menu {-webkit-animation: test123 .5s;}
  
#header .account .icons-arrow_grey{margin: 27px 20px 0 0 !important;}
  
  #mainSearch{margin-top:20px;}
  
  #header .menu div.item a{padding: 10px 30px;}
  #header .login-form div.item a{padding: 10px 30px;}
  
  #wrapper #content{margin-left:auto;margin-right:auto;display:block;position:relative;width:1000px;float:none;background:#fff;box-shadow:none;}
  
  #footer{width:100%;background: #3783bb;}
}

style_setting_options.value:
#menu-main .menu>li>a{padding:24px 10px;font-size:0px;}
#menu-main .menu>li>a:before{font-size:16px;}

#menu-main .menu>li .dropdown-menu .item a{font-size:0px;line-height:0px;padding:5px 30px;}
#menu-main .menu>li .dropdown-menu .item a:before{font-size:13px;line-height:28px;}

#menu-main .menu>li:nth-child(1)>a:before{content:'Accueil';}
#menu-main .menu>li:nth-child(1) .dropdown-menu .item:nth-child(1){display:none;}
#menu-main .menu>li:nth-child(1) .dropdown-menu .item:nth-child(2) a:before{content:'Articles';}
#menu-main .menu>li:nth-child(2)>a:before{content:'Series';}
#menu-main .menu>li:nth-child(2) .dropdown-menu .item:nth-child(1) a:before{content:'Aperçu';}
#menu-main .menu>li:nth-child(2) .dropdown-menu .item:nth-child(2) a:before{content:'Critiques';}
#menu-main .menu>li:nth-child(2) .dropdown-menu .item:nth-child(3) a:before{content:'Recommandations';}
#menu-main .menu>li:nth-child(2) .dropdown-menu .item:nth-child(4) a:before{content:'Top Séries';}
#menu-main .menu>li:nth-child(2) .dropdown-menu .item:nth-child(5) a:before{content:'Nouveautés';}
#menu-main .menu>li:nth-child(2) .dropdown-menu .item:nth-child(6) a:before{content:'Ajouter une nouvelle série';}
#menu-main .menu>li:nth-child(3)>a:before{content:'Films';}
#menu-main .menu>li:nth-child(3) .dropdown-menu .item:nth-child(1) a:before{content:'Aperçu';}
#menu-main .menu>li:nth-child(3) .dropdown-menu .item:nth-child(2) a:before{content:'Critiques';}
#menu-main .menu>li:nth-child(3) .dropdown-menu .item:nth-child(3) a:before{content:'Recommandations';}
#menu-main .menu>li:nth-child(3) .dropdown-menu .item:nth-child(4) a:before{content:'Top Films';}
#menu-main .menu>li:nth-child(3) .dropdown-menu .item:nth-child(5) a:before{content:'Ajouter un nouveau film';}
#menu-main .menu>li:nth-child(4)>a:before{content:'Personnalités';}
#menu-main .menu>li:nth-child(4) .dropdown-menu .item:nth-child(1) a:before{content:'Aperçu';}
#menu-main .menu>li:nth-child(4) .dropdown-menu .item:nth-child(2) a:before{content:'Top Personnalités';}
#menu-main .menu>li:nth-child(4) .dropdown-menu .item:nth-child(3) a:before{content:'Ajouter une nouvelle personnalité';}
#menu-main .menu>li:nth-child(5)>a:before{content:'Forum';}
