css:
@-moz-document url-prefix("http://www.nicovideo.jp/watch/") {
html {
  overflow-x: hidden;
}

/*[[theme]]*/

.wallImage, .chipWallListItemList, .videoThumb, #videoHeaderMenu .searchContainer, .toggleTagEdit, .size_small .videoMainInfoContainer, .size_small #videoMenuTopList, .nicoruWrapper.tagControlShow, #videoTagContainerPin, .readMore.videoDetailOpenButton, .ranking, .dicIcon, .hiddenInfo, .videoInfoLeft, #breadcrumb, #videoHeader .toggleDetailExpand, .videoMenuToggle, .socialButtons, .currentThreadName, #playerTabContainer table, .commonsTreeExists #topVideoInfo .parentVideoInfo, .videoMenuToggle.videoMenuToggleButton.firstNavigation, #textMarquee, #appliPanel, .nicoru-button, .time, .resNo, .vpos, .commentTableHeaderWrapper, .nicoSpotAds, .playerTabAds, #outline, #footer {
  display: none !important;
}

.videoMainInfoContainer {
  display: block !important;
  height: 6.65em;
  width: 51.7em;
  box-sizing: border-box;
  border: none !important;
  position: absolute;
  bottom: -41.5em;
  left: 0em;
  height: 1em;
}

#videoTagContainer {
  min-width: 94.8em !important;
  height: 2em !important;
  min-height: 2em !important;
  left: -8.63em !important;
}

.size_normal #videoTagContainer {
  left: 0 !important;
}

#videoDetailInformation {
  margin-top: 3em;
  width: 1234px !important;
  margin-left: -8.63em;
}

.size_normal #videoDetailInformation {
  margin-left: 0;
}

#videoHeaderTagList {
  padding-left: 0 !important;
  -webkit-filter: grayscale(100%);
  filter: grayscale(100%);
}

#videoHeaderTagList li {
  margin: 0 21px 4px 0 !important;
}

.size_small #content {
  padding-bottom: 1.5em;
}

.size_small.content-fix #content {
  padding-bottom: 0;
}

.size_normal .videoMainInfoContainer {
  width: 69em !important;
}

.size_normal #videoMenuTopList {
  bottom: -40.8em !important;
}

#videoTagContainer {
  width: 1008px !important;
  position: absolute;
  top: 7em;
  left: 0;
  padding: 0 3em;
}

.userProfile, .ch_prof {
  transition: 0.25s linear;
  opacity: 0.55;
  position: relative;
  top: 1.6em;
  -webkit-transform: scale(0.87);
  -moz-transform: scale(0.87);
  transform: scale(0.87);
}

.userProfile:hover, .ch_prof:hover {
  opacity: 1;
}

.videoDetailExpand {
  width: 100%;
  text-align: center;
}

#videoHeader {
  padding: 1em;
  margin: 2em auto -10em;
}

#videoHeader.menuOpened #videoMenuWrapper {
  margin-bottom: 0;
}

#videoHeader #videoHeaderDetail {
  margin: 0 0 0 -8.63em !important;
  width: 1234px !important;
  border-bottom: 1px solid #cfcfcf;
}

.size_normal #videoHeader #videoHeaderDetail {
  margin: 0 !important;
}

#videoHeaderDetail h2 {
  display: inline;
  max-width: 100%;
}

.videoHeaderTitle {
  font-size: 1.5em !important;
}

#topVideoInfo .videoDescription, .shortVideoInfo {
  box-sizing: border-box;
  width: 71.8%;
  padding: 2em 0 0 2em;
  margin-bottom: 13em;
}

#topVideoInfo .videoStats {
  width: 56.8em;
  text-align: right;
  font-size: 12px;
  position: relative;
  top: -2em;
  left: 18em;
  color: #666666;
}

#topVideoInfo .videoStats span {
  font-weight: normal !important;
}

#videoHeader .shortVideoInfo .shortDescription {
  max-width: 845px !important;
}

#videoHeader #videoMenuWrapper {
  height: 0px !important;
  overflow: visible;
  position: absolute;
  top: auto;
  bottom: 0;
}

.videoInformation {
  height: 0 !important;
}

#videoMenuWrapper {
  overflow: visible !important;
}

#videoMenuTopList {
  -webkit-filter: grayscale(100%);
  filter: grayscale(100%);
  width: 18em !important;
  height: 75px;
  padding: 0;
  position: absolute;
  top: auto;
  bottom: -61em;
  height: 5em;
  right: 8em;
}

#videoMenuTopList .videoMenuList {
  display: block !important;
  float: left !important;
  width: 3em !important;
}

#videoMenuTopList .videoMenuList:nth-child(n+3) {
  display: none !important;
}

#videoMenuTopList .videoMenuList a {
  color: transparent;
}

#videoMenuTopList li.videoMenuList {
  width: 111px;
}

#playerTabContainer .playerTabHeader .playerTabItem {
  width: 178px;
}

.playerTabContentItem {
  height: 114.5% !important;
}

.commentTableContainer {
  width: 344px !important;
  background: #fafafa;
  overflow-x: hidden !important;
  top: -1.5em !important;
  line-height: 1.7;
  height: 37em;
}

#commentDefaultHeader {
  top: 39em;
  width: 22em;
  z-index: 10000000;
  -webkit-transform: scale(1.15);
  -moz-transform: scale(1.15);
  transform: scale(1.15);
}

#playerCommentPanel .section .commentTable .commentTableHeaderWrapper {
  background: #515151;
}

.commentTable {
  border-color: transparent !important;
}

#topVideoInfo .videoMainInfoContainer .hiddenInfo {
  position: absolute;
  top: 4.4em;
  left: 6em;
}

#topVideoInfo .hiddenInfo .hiddenInfoTabContent {
  position: relative;
  left: -12em;
  bottom: 1em;
}

.videoStats {
  position: relative;
  bottom: 1.9em;
  left: 16em;
}

.extraVideoTrigger.trigger {
  position: relative;
  bottom: 2em;
  right: 12em;
}

.socialLinkGoogle {
  width: 85px !important;
}

.socialLinkFacebook, .socialLinkTwitter {
  width: 120px !important;
}

#videoHeader .toggleDetailExpand {
  z-index: 10;
}

#videoHeader .toggleDetailExpand .open, #videoHeader .toggleDetailExpand .close {
  text-decoration: none;
}

.playerContainer {
  height: 52em !important;
}

.size_normal #playerTabWrapper {
  height: 557px !important;
  width: 27.5em;
  position: absolute;
  top: 7.15em;
  right: -1em;
}

.comment {
  width: 315px !important;
  border: none !important;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

.comment {
  border-right: none;
}

#playerTabContainer {
  border: 1px solid transparent;
}

#playerTabContainer .playerTabHeader .playerTabItem {
  border-left: 1px solid #cfcfcf;
  border-bottom: 1px solid #cfcfcf;
  background: #f4f4f4;
}

#playerTabContainer .playerTabHeader .playerTabItem.active, #playerTabContainer .playerTabHeader .playerTabItem:hover {
  background: #fafafa;
}

.playerTabContent {
  top: -1em !important;
}

.size_normal .panel_ads_shown #playerTabContainer.has_panel_ads .playerTabContent {
  height: 92.7%;
}

#playlist {
  background: #f4f4f4;
  display: none;
}

#videoExplorerExpand {
  display: none;
}

#videoExplorerExpand .expandButton {
  color: #515151;
}

.ichiba_mainitem {
  background: #fafafa;
  box-sizing: border-box;
  padding: 1em !important;
  border: 1px solid #cfcfcf;
}

#ichibaMain .balloonUe {
  width: 153px !important;
}

#ichibaMain .balloonShita img {
  width: 153px !important;
  margin-top: 0.5em;
}

.thumbnail {
  margin-top: 0.5em !important;
}

body.size_normal:not(.full_with_browser) #playerNicoplayer {
  position: absolute;
  left: -1em;
}

body:not(.full_with_browser) #playerNicoplayer {
  position: absolute;
  top: 7.15em;
}

.size_small #playerNicoplayer {
  position: static !important;
}

.size_small #playerNicoplayer:before {
  display: none !important;
}

body.size_normal:not(.full_with_browser) #playerNicoplayer:before {
  height: 2.8em !important;
  top: 37.8em !important;
}

#wallImageContainer {
  top: 0.8em !important;
}

.size_normal #wallImageContainer {
  height: 50.67em !important;
}

body:not(.full_with_browser) #playerNicoplayer:before, .full_with_browser #playerNicoplayer:before {
  content: "";
  display: block;
  width: 3.9em;
  position: absolute;
  background: #1b1b1b;
  z-index: 100;
  left: 4.5em;
  background: -moz-linear-gradient(top, #232323 0%, #131313 100%);
  background: -webkit-linear-gradient(top, #232323 0%, #131313 100%);
  height: 2.7em;
}

body:not(.full_with_browser) #playerNicoplayer:before {
  top: 30.4em;
}

.full_with_browser #playerNicoplayer:before {
  bottom: 2.4em;
}

}

style_setting_options.value:
.cell {
  border-bottom: 1px solid rgba(0, 0, 0, 0.1) !important; }
  .cell:hover {
    background: transparent !important;
    color: black !important; }

body, .playerContainer, #playerTabContainer, .commentTableContainer {
  background: #272727 !important; }
  body *:not(.userName), .playerContainer *:not(.userName), #playerTabContainer *:not(.userName), .commentTableContainer *:not(.userName) {
    color: #f7f7f7 !important; }

#videoHeaderDetail, .cell {
  border-bottom: 1px solid rgba(255, 255, 255, 0.4); }

#playerTabContainer *:not(.userName):not(.comment) {
  color: #272727 !important; }
