/* i really want this to be global */
* {
    font-family: "Comic Sans MS","Microsoft Yahei" !important;
	text-shadow: 0.05em 0.05em 0.2em #999999 !important;
}

pre, code, kbd, samp, var {
    font-family: "Comic Sans MS", "Microsoft Yahei", monospace !important;
	text-shadow: 0.05em 0.05em 0.2em #999999 !important;
}

