css:
@-moz-document domain('w3school.com.cn') {
@media print {
  #sidebar, #navsecond, #tpn, #footer, #menu, #header, #bpn
  {
    display:none;
  }
  #maincontent {
    position: absolute;
    top:20px;
    left:20px;
  }
  body {
    font-size: 16px;
    font-family: "微软雅黑";
  }
}

#sidebar, #footer, #header, body#editor div#header {
  display: none;
}

body, body#editor h2{
  font-family: "微软雅黑";
  font-size: 14px;
}
pre, textarea#TestCode {
  font-family: anonymous, monaco, consolas, "Source Code Pro";
}
}
