css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wikifoundry.com"),domain("wikifoundrycentral.com"),domain("warriorcatclans2.com"),domain("scribblenautsguide.com"),domain("www.thejonasbrotherssite.com"),domain("www.demilovatofansite.com"),domain("closinglogos.com") {

/* Global Styles */
    
html { position: relative; }
    
.button, input.submit, input.button, button, .WPC button, .WPC .WPC-button, .WPC .WPC-submit, .WPC .WPC-cancel {
    background: #FF4A3A !important;
    border: 0;
    padding: 8px 10px;
    color: #fff !important;
    border-radius: 2px;
    border-bottom: 2px solid #BF382B !important;
}
    
input[type="text"]:focus, textarea:focus {
    border-color: #4C9ED9 !important;
}
    
.WPC .WPC-cancel, .cancel, #addMessageTo {
    background: #ccc !important;
    color: #333 !important;
    border-bottom: 2px solid #aaa !important;
    padding-bottom: 9px;
}
    
.button:hover, input.submit:hover, input.button:hover, button:hover, .WPC button, .WPC .WPC-button, .WPC .WPC-submit, .WPC .WPC-cancel {
    background: #FF6253;
}
    
.WPC button:active, .WPC .WPC-button:active, .WPC .WPC-submit:active, .WPC .WPC-cancel:active, .cancel:active, .button:active, input.submit:active, input.button:active, button:active {
    outline: none;
    padding-top: 8px;
    border-bottom: 0 !important;
    margin-top: 2px;
}
    
.WPC .imageSm, .imageSm, .WPC .avatarMissing, .avatarMissing, .WPC .avatarAnonymous, .avatarAnonymous {
    border: 0;
    border-radius: 2px;
}

input[type="text"], textarea {
    border: 1px solid #ddd !important;
    box-shadow: 2px 2px 10px 0 rgba(0,0,0,0.1) inset;
    padding: 6px;
    background: #fff !important;
    border-radius: 2px;
    margin-bottom: 10px;
}
    
#threadFormDiv td.labelCell {
    color: rgb(102, 102, 102);
}
    
#threadFormDiv {
    padding: 12px;
    border: 1px solid rgb(238, 238, 238);
    border-radius: 2px;
}
    
#threadFormDiv td.labelCell {
    padding: 8px;
}
    
#outer { margin: 0 0 12px 0; }
    
#pageAttributes {
    margin: 0 -10px;
    padding: 20px;
    background: transparent;
    border: 2px solid rgba(0, 0, 0, 0.1);
    border-left: 0;
    border-right: 0;
    background-color: #fff;
    color: #666;
}
    
#pageAttributes .WPC-tagModule {
    margin: 10px 0 10px 0;
}
    
.tableList .farRightCell {
    padding-right: 16px;
}
    
.tableList td img, .avatarMissing {
    margin: 16px 16px 6px 16px !important;
}

.tableList td:first-of-type {
    text-align: center;
    padding-bottom: 20px;
}
    
.threadRater, #firstToReplyDiv {
    display: none !important;
}
    
/* Header Styles */
    
#userHeader {
    background: #333;
    color: #999;
    border: 0;
    padding: 4px;
    position: absolute;
    width: 100%;
    z-index: 1;
    top: 0px;
    left: 0;
}
    
#userCredentials li {
    background: none;
}
    
#userHeader #userCredentials .logo {
    display: none;
}
    
#userHeader .on #action_mywetpaint {
    background: none;
}
    
#myWetpaintMenu {
    right: 0;
    left: auto !important;
    width: 240px;
    max-height: 300px;
    overflow-y: auto;
}
    
#pageToolbarTooltip {
    display: none;
}
    
#myWetpaintMenuInner {
    background: #333;
    padding: 10px;
    word-wrap: break-word;
}
    
#userCredentials {
    background: none;
}
    
#myWetpaintMenu ul {
    border-top: 0;
    border-bottom: 0;
}
    
#myWetpaintMenu ul:last-of-type li a {
    display: block;
    text-align: center;
    padding: 10px 0px;
    background: rgba(230, 75, 75, 1);
    color: #fff;
    border-radius: 2px;
    text-transform: uppercase;
}    
    
#myWetpaintMenu ul:last-of-type li a:hover {
    text-decoration: none;
    opacity: 0.8;
    color: #fff;
}
    
#pageAttributes tr td:first-of-type {
    padding-right: 20px !important;;
}
    
#pageAttributes tr td:nth-child(2)::before {
    content: "Page Information";
    display: block;
    font-size: 18px;
    margin-bottom: 6px;
    color: #999;
}
    
#userHeader #myWetpaintMenu .myWikisList li:first-of-type {
    border-top: 0; padding: 0 0 10px 0;
}
    
#userHeader #myWetpaintMenu .myWikisList li {
    border-top: 1px dotted rgba(255, 255, 255, 0.1);
    padding: 10px 0;
}
    
#userHeader a:link, #userHeader a:visited {
    color: rgba(230, 75, 75, 1);
}
    
#userHeader a:link:hover, #userHeader a:visited:hover {
    color: rgba(230, 75, 75, 1);
    text-decoration: underline;
}
    
/* Page Styles */
    
#WPC-bodyContentContainer {
    margin: 37px auto 0px auto !important;
    position: inherit;
}
    
#allcontent {
    background: none;
}
    
a.expandToggleCollapsed,a.expandToggleExpanded {
    background-image: url('http://image.wikifoundry.com/image/3/1_rM_2ZA9PxfZZWzvQwCaQ352/GW29H29');
}
    
/* Site Header */
#siteHeader {
    text-align: center;
    margin-top: 10px;
    border-radius: 5px 5px 0 0;
}
    
.WPC-fixedWidth #siteHeader { background: #fff; }
    
/* Navigation Styles */
    
#gnAccount {
    padding-right: 10px !important;
}
    
#gnMain {
    padding-left: 5px !important;
}
    
#gnMain li {
    background: none;
    padding-right: 0;
}
    
#gnMain li:last-of-type {
    border-right: 0;
}
    
#gnMain ul.subNav {
    box-shadow: 2px 2px 2px 0 rgba(0,0,0,0.3);
}
    
#gnMain li.subNav {
    padding: 8px;
    background-image: none;
    border-bottom: 1px dashed rgba(0,0,0,0.2);
}
    
#gnMain li.subNav:last-of-type {
    border-bottom: 0;
}
    
#gnMain li.subNav span {
    border-right: 0;
}
    
#gnMain li span {
    padding-right: 10px;
    border-right: 1px dotted rgba(0,0,0,0.3);
}
    
#gnMain li:last-of-type span {
    border-right: 0;
}

.join-button button {
    border: 0 !important;
    background: url('http://dmiqhxtnnyjjh.cloudfront.net/img/bg/auth/btn_join.png?v=20111214172750') no-repeat scroll left top transparent !important;
}
    
/* Side Navigation Styles */
    
.treeList li a {
    padding: 6px 6px 6px 100px;
}
    
.treeList li div {
    top: 6px;
}
    
.treeList div.noKids {
    top: 13px;
}
    
/* Search Styles */
    
.tabNavigation {
    border-bottom: 0;
}
    
.tabNavigation li.selected {
    border: 1px solid #CCC;
    padding-bottom: 2px;
}
    
.searchForm input.text {
    padding: 4px 4px 4px 6px;
    width: 180px;
    border-radius: 2px;
}
    
.searchForm input.submit {
    background-image: url('http://image.wikifoundry.com/image/3/fVjeeumSHXcGRIvEzuV6tg250/GW27H27') !important;
    border: 0 !important;
    width: 27px;
    height: 27px;
    margin-top: 0;
}
    
#gnPromoLinks ul {
    padding-top: 5px;
}

#siteSearch {
    z-index: 0;
}
    
/* Profile Styles */

.announcement {
    color: #fff;
    background: #333;
    border: 0;
}
    
.myAccountItem {
    color: #ccc;
    margin-bottom: 6px;
}
    
.myAccountItem:first-of-type {
    margin-top: 6px;
}
    
#userAvatarImage {
    border-radius: 2px;
}
   
.compliments {
    margin-top: 10px;
}
    
.compliment {
    border: 1px solid #111;
    background: #222;
}
    
.compliment a, .compliment a:link, .compliment a:visited, .compliment a:link:hover, .compliment a:visited:hover {
    color: #FF4A3A;
}
    
#myWetpaintPanel {
    display: none;
}
    
.friendList li {
    width: 50%;
    text-align: center;
    height: 7em;
    overflow: hidden;
    margin: 0;
}
    
.friendList .avatarHolder {
    margin: 0px auto;
}
    
.secondInnerCell .innerColumn .bordered {
    border: 0;
    color: #999;
    font-size: 12px;
}
    
#approvedFriendsList .tableList .header {
    background: none;
    color: #999;
    padding-top: 14px;
    border-top: 1px solid #ddd;
    text-align: center;
}
    
.pagingAction {
    width: 100%;
    text-align: center;
    margin: 10px 0;
}
    
.pagingWrapper {
    text-align: center;
}
    
/* Inbox Styles */
    
.tableList .header {
    background: #333;
    color: #ccc;
}
    
.tabNavigation li:last-of-type {
    margin-right: 0;
}
    
/* Dialogue Styles */

#addImage-gallerySearch-query { width: 82% !important; padding: 6px; margin-top: 9px; border-radius: 2px; }

.cardReportList .photosDropletCard { max-width: 29%; } 
.cardReportList .photosDropletCard.cardListItem { float: left; width: 30%; }
    
#addImageGalleryResults,#albumListResults { width: auto !important; }
#pseudoBrowse { margin-top: 0 !important; }
#addImageSearch { margin-top: -6px; }
#WPC-addWidgetSearch { margin-top: -4px; }
.cardReportList .cardListItem { width: auto; float: none; min-height: auto; }
    
.WPC .WPC-proto-subWindow { border: 0; padding: 10px; height: auto; }

.WPC .WPC-dialog { box-shadow: 0 0 10px 0 rgba(0,0,0,0.2); height: auto !important; }
    
.WPC .WPC-proto-content { background: none;}

#WPC-moduleList div.albumsCard { padding: 10px 0px 4px; border-bottom: 1px solid #DDD; cursor: pointer; }
    
#WPC-moduleList div.moduleThumb span img { border: 0; }

.WPC .WPC-proto .WPC-proto-content-container, .WPC .WPC-proto .WPC-proto-content-container .WPC-proto-content, .WPC .WPC-proto .WPC-proto-content-container .WPC-proto-content { border-radius: 0; box-shadow: none; }

.WPC input.WPC-text, .WPC input.WPC-file, .WPC select.WPC-select, .WPC textarea.WPC-textarea { border: 1px solid #ccc; margin: 5px 0 10px 0; }
    
.WPC .WPC-proto-title { width: auto; padding: 8px 23px 6px 10px; }
.WPC .WPC-proto-title, .WPC .WPC-proto-content-container, .WPC .WPC-proto-control-grab { border: 0; }
    
.WPC .WPC-proto-control-grab { top: 0; display: none; }
    
/* Widget Styles */
.WPC .WPC-widgetLayout .WPC-widgetItem, #WPC-googleCalendarWidget { display: none; }
.WPC .WPC-widgetLayout .WPC-widgetUnknownSecurityHolder, .WPC-widgetItem:nth-child(4), .WPC-widgetItem:nth-child(1) { display: block !important; }
    
.WPC .WPC-widgetLayout .WPC-widgetItem.WPC-moreWidgets { display: none !important; }
.WPC .WPC-widgetLayout .WPC-widgetItem { width: 128px; background: none; text-align: center; margin: 10px; }

/* Page Tools Styles */
    
.WPC-toolsMenu button.submit {
    margin-top: -10px;
}

.WPC-tagModule { display: none; }
    
/* Footer Styles */
    
#footer { background-color: #333; position: absolute; bottom: 0; left: 0; height: 40px; width: 100%; margin: 0; color: #fff; }

#createdWithContent { margin: 12px 0 0 0; }

#footer ul { margin: 10px 0 0 12px; }

/*[[centreall]]*/
   
}

/* Chrome Dropdown Fix */
    
@media screen and (-webkit-min-device-pixel-ratio:0) { .WPC-selectListDropDown { margin: 22px 0 0 0 !important; } }

/* Site-Specific Changes */
@-moz-document domain("destinywolfrpg.wikifoundry.com"),domain("rp-limitless.wikifoundry.com"),domain("walkalongwithlimitless.wikifoundry.com") {

body { background-color: #111; }

}

@-moz-document domain("rp-limitless.wikifoundry.com"),domain("walkalongwithlimitless.wikifoundry.com") {

#allcontentInner { padding-top: 0; }

}

style_setting_options.value:
#WPC-bodyContentContainer { width: 1000px; }
#pageContentInner * { max-width: 760px; }
#pageContentInner img { height: auto; }
#pageContentInner { overflow: hidden; }
