css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.motobit.com/util/base64-decoder-encoder.asp") {

body {font-family: 'Verdana'!important;color:#ddd;margin:10px;line-height:1.3;background:#444!important;}

a {color:#0af!important;}
h4 {border:0!important;}
h1 {margin-bottom:10px!important;background: none!important;font-variant:normal!important; font-size:14px!important;letter-spacing:normal!important;}
h2 {background:inherit!important; text-align:left!important; padding:0!important;font-size:12px!important;}

.dmain {background:none!important;border:0!important;}
h1 br,iframe,#dcenter div+div,.links,.afoot {display:none!important;}

div{display:block!important;padding:0!important; }
div[style]{background:none!important;padding:0!important;border:0!important}

#dcenter {font-size:11px!important;background:none!important;border:0!important;padding:5px!important;}

/* Hide other textaea box that appears after a conversion */
form[name="formtobase64"] textarea {display:none!important}

}
