css:
/* Ashun's NuNu Shack  

	For the October 2014 Blue/Gray Shacknews revision.
	CC0 1.0 Public Domain. Feel free to modify!

	Theme colors:
	
		262428	Dark blue-gray
		313034	Lighter blue-gray
		E8E3BE	Bland tan
		FF295E	NuShack pink!
		C1272D	Coral red
		6DAFBF	Hi-light blue
		2C2636	Super dark purple
		4E4560	Muted dark purple
		695D82	Muted light purple

	*/



@-moz-document
	url-prefix('http://www.shacknews.com/chatty'),
	url-prefix('https://www.shacknews.com/chatty') {



	/* Include fonts */

/* latin-ext */
@font-face {
  font-family: 'Lato';
  font-style: italic;
  font-weight: 400;
  src: local('Lato Italic'), local('Lato-Italic'), url(https://fonts.gstatic.com/s/lato/v14/S6u8w4BMUTPHjxsAUi-qJCY.woff2) format('woff2');
  unicode-range: U+0100-024F, U+0259, U+1E00-1EFF, U+2020, U+20A0-20AB, U+20AD-20CF, U+2113, U+2C60-2C7F, U+A720-A7FF;
}
/* latin */
@font-face {
  font-family: 'Lato';
  font-style: italic;
  font-weight: 400;
  src: local('Lato Italic'), local('Lato-Italic'), url(https://fonts.gstatic.com/s/lato/v14/S6u8w4BMUTPHjxsAXC-q.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
}
/* latin-ext */
@font-face {
  font-family: 'Lato';
  font-style: italic;
  font-weight: 700;
  src: local('Lato Bold Italic'), local('Lato-BoldItalic'), url(https://fonts.gstatic.com/s/lato/v14/S6u_w4BMUTPHjxsI5wq_FQft1dw.woff2) format('woff2');
  unicode-range: U+0100-024F, U+0259, U+1E00-1EFF, U+2020, U+20A0-20AB, U+20AD-20CF, U+2113, U+2C60-2C7F, U+A720-A7FF;
}
/* latin */
@font-face {
  font-family: 'Lato';
  font-style: italic;
  font-weight: 700;
  src: local('Lato Bold Italic'), local('Lato-BoldItalic'), url(https://fonts.gstatic.com/s/lato/v14/S6u_w4BMUTPHjxsI5wq_Gwft.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
}
/* latin-ext */
@font-face {
  font-family: 'Lato';
  font-style: normal;
  font-weight: 400;
  src: local('Lato Regular'), local('Lato-Regular'), url(https://fonts.gstatic.com/s/lato/v14/S6uyw4BMUTPHjxAwXjeu.woff2) format('woff2');
  unicode-range: U+0100-024F, U+0259, U+1E00-1EFF, U+2020, U+20A0-20AB, U+20AD-20CF, U+2113, U+2C60-2C7F, U+A720-A7FF;
}
/* latin */
@font-face {
  font-family: 'Lato';
  font-style: normal;
  font-weight: 400;
  src: local('Lato Regular'), local('Lato-Regular'), url(https://fonts.gstatic.com/s/lato/v14/S6uyw4BMUTPHjx4wXg.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
}
/* latin-ext */
@font-face {
  font-family: 'Lato';
  font-style: normal;
  font-weight: 700;
  src: local('Lato Bold'), local('Lato-Bold'), url(https://fonts.gstatic.com/s/lato/v14/S6u9w4BMUTPHh6UVSwaPGR_p.woff2) format('woff2');
  unicode-range: U+0100-024F, U+0259, U+1E00-1EFF, U+2020, U+20A0-20AB, U+20AD-20CF, U+2113, U+2C60-2C7F, U+A720-A7FF;
}
/* latin */
@font-face {
  font-family: 'Lato';
  font-style: normal;
  font-weight: 700;
  src: local('Lato Bold'), local('Lato-Bold'), url(https://fonts.gstatic.com/s/lato/v14/S6u9w4BMUTPHh6UVSwiPGQ.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
}
/* latin-ext */
@font-face {
  font-family: 'Fjalla One';
  font-style: normal;
  font-weight: 400;
  src: local('Fjalla One'), local('FjallaOne-Regular'), url(https://fonts.gstatic.com/s/fjallaone/v5/Yq6R-LCAWCX3-6Ky7FAFrO96kigt.woff2) format('woff2');
  unicode-range: U+0100-024F, U+0259, U+1E00-1EFF, U+2020, U+20A0-20AB, U+20AD-20CF, U+2113, U+2C60-2C7F, U+A720-A7FF;
}
/* latin */
@font-face {
  font-family: 'Fjalla One';
  font-style: normal;
  font-weight: 400;
  src: local('Fjalla One'), local('FjallaOne-Regular'), url(https://fonts.gstatic.com/s/fjallaone/v5/Yq6R-LCAWCX3-6Ky7FAFrOF6kg.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
}

	/* Stop header from scrolling with page 

	header.out {

		position: relative !important;

		}

	body > header {

		transition: none !important;
		-o-transition: none !important;
		-moz-transition: none !important;
		-webkit-transition: none !important;

		}

	body > header.mini {

		height: 70px !important;
		transition: none !important;
		-o-transition: none !important;
		-moz-transition: none !important;
		-webkit-transition: none !important;

		} */

	/* Mini-header shadow */

	header.out.mini {

		box-shadow: 0px 0px 28px rgba(0, 0, 0, 0.6) !important;

		}

	/* Adjust ChromeShack new post indicator */
	
	a#jump_to_new_post {

		left: 820px !important;

		}

	/* Full page advertisement compromise */

	body {

		background-color: black !important;
		min-width: 830px !important;
		background-attachment: scroll !important;
		background-position: 50% 24px !important;
		cursor: auto !important;

		}

	.takeover #full-wrap {

		max-width: none !important;
		padding: 0 !important;
		box-shadow: none !important;
		background-color: transparent !important;

		}

	div#main {

		background-color: transparent !important;
		/*max-width: 984px !important;*/
		min-height: 510px !important;
		margin-left: auto !important;
		margin-right: auto !important;

		}

	/*#ad-leaderboard {

		margin-top: 25px !important;

		}*/

	div.content {

		padding-bottom: 0 !important;

		}

	div.content-inner.clear-fix {

		background-color: black !important;
		padding-bottom: 0 !important;

		}

	div.main-col {

		border: none !important;

		}

	div.article h1 {

		border-top: none !important;

		}

	div.aside {

		background: none !important;

		}

	div#chatty_comments_wrap {

		margin-top: 0 !important;

		}

	/* Comments block gets a purple border */

	div.commentsblock {

		background: black !important;
		padding: 25px 30px 30px 30px !important;
		margin-bottom: 0px !important;
		border-width: 5px 0px 5px 0px !important;
		border-style: solid !important;
		border-color: #4e4560 !important;

		}

	/* New comment and page navigation block */

	div#commenttools.commentstools {

		padding: 5px 0px 0px 0px !important;
		margin: 5px 0px -20px 3px !important;

		}

	.pagenavigation {

		margin-top: 20px !important;

		}

	/* "THREADS | COMMENTS | FILTERS" Links */

	div#chatty_settings {

		left: 0px !important;
		padding: 0px 0px 0px 0px !important;
		margin: -32px 0px 0px 0px !important;
		height: auto !important;
		width: auto !important;
		font-size: 15px !important;
		font-family: 'Fjalla One', sans-serif !important;
		text-align: center !important;
		text-transform: uppercase !important;
		position: absolute !important;

		}

	div#chatty_settings a:link,
	div#chatty_settings a:visited,
	div#chatty_settings a:active {

		color: #E8E3BE !important;
		font-weight: normal !important;
		text-decoration: none !important;

		}

	div#chatty_settings a:hover {

		color: #E8E3BE !important;

		}

	div#chatty_settings span.pipe {

		padding: 0px 8px 0px 8px !important;
		margin: 0px 0px 0px 0px !important;
		font-size: 16px !important;
		color: #4e4560 !important;
		vertical-align: top !important;

		}

	/* "NEW COMMENT | BACK TO TOP" Links */

	div.commentnav {

		color: #4e4560 !important;
		padding: 0px !important;
		margin: 18px 0px 0px 0px !important;
		font-size: 16px !important;
		font-family: 'Fjalla One', sans-serif !important;
		text-transform: uppercase !important;

		}

	div.commentnav a:link,
	div.commentnav a:visited,
	div.commentnav a:active {

		color: #E8E3BE !important;
		font-size: 16px !important;
		font-family: 'Fjalla One', sans-serif !important;
		text-decoration: none !important;

		}

	div.commentnav a:hover {

		color: #E8E3BE !important;
		text-decoration: none !important;

		}

	/* New Comment button */

	div#newcommentbutton a:link,
	div#newcommentbutton a:visited,
	div#newcommentbutton a:active {

		height: 28px !important;
		width: 150px !important;
		line-height: 28px !important;
		padding: 0px 0px !important;
		margin: 12px 0px 0px -3px !important;
		color: white !important;
		font-family: 'Fjalla One', sans-serif !important;
		font-size: 16px !important;
		font-weight: 400 !important;
		text-decoration: none !important;
		background: none !important;
		background-color: #313034 !important;
		border: 0px solid black !important;
		text-align: center !important;
		text-indent: 0 !important;
		text-transform: uppercase !important;
		border-radius: 0 !important;
		transition: background-color 0.1s linear, border 0.1s linear !important;  
		o-transition: background-color 0.1s linear, border 0.1s linear !important;
		-moz-transition: background-color 0.1s linear, border 0.1s linear !important;
		-webkit-transition: background-color 0.1s linear, border 0.1s linear !important;
		vertical-align: top !important;

		}

	div#newcommentbutton a:hover {

		background-color: #4e4560 !important;
		border-bottom: 5px solid #6dafbf !important;
		transition: background-color 0s linear, border 0.1s linear !important;  
		o-transition: background-color 0s linear, border 0.1s linear !important;
		-moz-transition: background-color 0s linear, border 0.1s linear !important;
		-webkit-transition: background-color 0s linear, border 0.1s linear !important;

		}

	/* Remove spacing between page buttons */

	div.pagenavigation {

		font-size: 0px !important;
		margin-top: 21px !important;

		}

	/* Inactive Previous button */

	div.commentsblock div.pagenavigation span.nextprev {

		background: none !important;
		background-color: #313034 !important;
		border: 0px solid black !important;
		padding: 4px 23px !important;
		margin: 0px !important;
		color: #555 !important;
		font-family: 'Fjalla One', sans-serif !important;
		font-size: 16px !important;
		font-weight: 400 !important;
		text-transform: uppercase !important;

		}

	/* Current Page Number button */

	div.commentsblock div.pagenavigation a.selected_page:link,
	div.commentsblock div.pagenavigation a.selected_page:visited,
	div.commentsblock div.pagenavigation a.selected_page:active {

		background: none !important;
		background-color: #C1272D !important;
		border: 0px solid white !important;
		padding: 4px 23px !important;
		margin: 0px !important;
		color: white !important;
		font-family: 'Fjalla One', sans-serif !important;
		font-size: 16px !important;
		font-weight: 400 !important;
		text-transform: uppercase !important;
		border-radius: 0 !important;

		}

	/* Unselected Page buttons */

	div.commentsblock div.pagenavigation a:link,
	div.commentsblock div.pagenavigation a:visited,
	div.commentsblock div.pagenavigation a:active {

		background: none !important;
		background-color: #313034 !important;
		border: 0px solid #222 !important;
		padding: 4px 23px !important;
		margin: 0px !important;
		color: white !important;
		font-family: 'Fjalla One', sans-serif !important;
		font-size: 16px !important;
		font-weight: 400 !important;
		font-variant: small-caps !important;
		text-transform: uppercase !important;
		transition: background-color 0.1s linear, border 0.1s linear !important;  
		o-transition: background-color 0.1s linear, border 0.1s linear !important;
		-moz-transition: background-color 0.1s linear, border 0.1s linear !important;
		-webkit-transition: background-color 0.1s linear, border 0.1s linear !important;
		border-radius: 0 !important;

		}

	div.commentsblock div.pagenavigation a:hover {

		background-color: #4e4560 !important;
		border: 0px solid #09F !important;
		border-bottom: 5px solid #6dafbf !important;
		transition: background-color 0s linear, border 0.1s linear !important;  
		o-transition: background-color 0s linear, border 0.1s linear !important;
		-moz-transition: background-color 0s linear, border 0.1s linear !important;
		-webkit-transition: background-color 0s linear, border 0.1s linear !important;

		}

	/* Override negative left indent for all posts */

	div.threads {

		margin-left: 0px !important;

		}

	/* User name styling:

	There are four user name modifiers:

		.oneline_user{}				modifies all names in the one-line post previews, unless
		.oneline_user.this_user{}	overrides the logged in user name in the previews.
		.user{}						is used for all post header names, unless
		.user.this-user{}			overrides the logged in user name in the post header.

		*/

	.oneline_user {

		color       : #E8E3BE !important;
		font-family : Arial !important;
		font-size   : 16px !important;
		font-weight : 400 !important;
		font-variant: normal !important;
		text-shadow : none !important;

		}

	.oneline_user.this_user {

		color       : #FF295E !important;
		font-family : Arial !important;
		font-size   : 16px !important;
		font-weight : 400 !important;
		font-variant: normal !important;

		}

	.user, .user a {

		order       : 2;
		color       : #E8E3BE !important;
		font-family : Lato !important;
		font-size   : 18px !important;
		font-weight : 800 !important;
		font-variant: normal !important;
		padding     : 0px 12px 0px 0px !important;
		font-style  : italic !important;
		text-decoration: none !important;

		}

	.user.this-user, .user.this-user a {

		order       : 2;
		color       : #FF295E !important;
		font-family : Lato !important;
		font-size   : 18px !important;
		font-weight : 800 !important;
		font-variant: normal !important;
		padding     : 0px 12px 0px 15px !important;
		font-style  : italic !important;		
		text-decoration: none !important;

		}

	/* Special Shacknews post name styling */

	div.fpauthor_14475 .user a {

		color: white !important;
		text-shadow : black 0px 0px 6px !important;
		text-transform: uppercase !important;
		font-style: normal !important;
		font-weight: bold !important;

		}

	/* Full post styling */

	div.root {

		margin: 40px 0px 0px 0px !important;

		}

	/* Fullpost flex-box structure:

		1) Postmeta (Author flex-box)
			a) Shackmessage icon
			b) User name
			c) Lightning bolt icon
			d) LOL buttons
		2) Post date
		3) Expiration marker
		4) Permalink
		5) Closepost (actually part of postmeta, but outside of the flow)
		6) Post body
		7) Refresh
		8) Reply button

	*/
	
	.fullpost, .getPost {

		position: relative !important;
		display: flex !important;
		flex-flow: row wrap !important;
		justify-content: flex-end !important;
		align-items: center !important;
		align-content: center !important;
		display: -webkit-flex;
		-webkit-flex-flow: row wrap;
		-webkit-justify-content: flex-end;
		-webkit-align-items: center;
		-webkit-align-content: center;
		margin: 0 !important;
		padding: 0 !important;
		background-color: #262428 !important;
		border: 2px solid #2C2636 !important;
		border-left: 2px solid #695D82 !important;
		font-size: 0 !important;

		}

		/* ChromeShack embedded posts */

		.getPost {

			border: 2px solid rgb(148, 255, 185) !important;
			margin: 0px 15px !important;

			}

		.getPost .postnumber {

			margin-right: 0 !important;
			padding-right: 35px !important;

			}

	.postmeta {

		order: 1 !important;
		-webkit-order: 1;
		height: 28px !important;
		background-color: #313034 !important;
		border-bottom: 1px solid #202020 !important;
		border: 0px solid blue !important;
		float: none !important;
		position: static !important;
		margin: 0 !important;
		padding: 0 !important;
		border-bottom: 1px solid #202020 !important;

		}

		/* Author flex-box */
	
		.author {

			display: flex !important;
			flex-flow: row wrap !important;
			justify-content: flex-start !important;
			align-items: center !important;
			display: -webkit-flex;
			-webkit-flex-flow: row wrap;
			-webkit-justify-content: flex-start;
			-webkit-align-items: center;
			height: 28px !important;
			font-size: 0px !important;
			margin: 0 !important;
			padding: 0 !important;

			}

		span.author a.shackmsg img {

			display: none !important;

			}

		span.author a.shackmsg {

			order: 1;
			-webkit-order: 1;
			height: 28px !important;
			width: 35px !important;
			font-size: 16px !important;
			text-align: center !important;
			margin: 0 !important;
			padding: 0 !important;
			background-image: url('http://chattypics.com/files/sn_logo_das6o199bi.png');
			background-repeat: no-repeat;
			background-position: 10px 8px;

			}

		span.user, span.user.this-user {

			order: 2;
			-webkit-order: 2;
			width: auto !important;
			padding: 0 !important;
		
			}

		span.author a.lightningbolt img {

			display: none !important;

			}

		span.author a.lightningbolt {

			order: 3;
			-webkit-order: 3;
			width: 26px !important;
			height: 28px !important;
			font-size: 16px !important;
			text-align: center !important;
			margin: 0 !important;
			padding: 0 !important;
			background-image: url('http://chattypics.com/files/lime_yp4b7qv48i.png');
			background-repeat: no-repeat;
			background-position: 0px 8px;
			border: 0px solid blue;

			}

		div.lol {

			order: 4;
			-webkit-order: 4;
			padding-left: 0px !important;
			height: 28px;
			line-height: 26px;

			}

	.postdate {

		order: 2 !important;
		-webkit-order: 2;
		flex-grow: 1 !important;
		-webkit-flex-grow: 1;
		height: 28px !important;
		line-height: 28px !important;
		text-align: right !important;
		background-color: #313034 !important;
		font-family: 'Arial', sans-serif !important;
		font-size: 11px !important;
		color: #888 !important;
		border-bottom: 1px solid #202020 !important;
		float: none !important;
		position: static !important;
		margin: 0 !important;
		padding: 0 !important;
		padding-right: 100px !important;

		}

	.countdown-wrap, .expired-wrap {

		order: 3;
		-wekit-order: 3;
		position: absolute !important;
		top: 0px !important;
		right: 70px !important;
		padding-right: 20px !important;
		height: 28px !important;
		background-color: #313034 !important;
		border-bottom: 1px solid #202020 !important;
		background-size: 48px 14px !important;
		background-repeat: no-repeat !important;
		background-position: 0px 7px !important;
		border-radius: 0 !important;
		background-image: url("http://cf.shacknews.com/images/promo-fade.png") !important;

		}

	.countdown-value {

		height: 12px !important;
		border-radius: 0 !important;
		margin: 8px 1px 1px 1px !important;

		}

	.countdown-doom {

		order: 3;
		-wekit-order: 3;
		position: absolute !important;
		bottom: auto !important;
		right: 70px !important;
		padding-right: 20px !important;
		height: 28px !important;
		width: 38px !important;
		background-color: #313034 !important;
		border-bottom: 1px solid #202020 !important;
		background-repeat: no-repeat !important;

		}

	.expired-value {

		height: 12px !important;
		width: 46px !important;
		border-radius: 0 !important;
		margin: 8px 1px 1px 1px !important;

		}

	.postnumber {

		order: 4 !important;
		-webkit-order: 4;
		width: 35px !important;
		height: 28px !important;
		line-height: 31px !important;
		font-size: 16px !important;
		text-align: center !important;
		background-color: #313034 !important;
		border: none !important;
		border-bottom: 1px solid #202020 !important;
		float: none !important;
		position: static !important;
		margin: 0 !important;
		padding: 0 !important;
		margin-right: 35px !important;
