	/* Stop header from scrolling with page */

	header.out {

		position: relative !important;

		}

	body > header {

		transition: none !important;
		-o-transition: none !important;
		-moz-transition: none !important;
		-webkit-transition: none !important;

		}

	body > header.mini {

		height: 70px !important;
		transition: none !important;
		-o-transition: none !important;
		-moz-transition: none !important;
		-webkit-transition: none !important;

		}

	/* Fix page advertisements */

	body {

		background-color: black !important;
		background-attachment: scroll !important;
		background-position: 50% 24px !important;
		cursor: auto !important;

		}

	.takeover #full-wrap {

		background-color: black !important;

		}

	/* Adjust side advertisement */

	div.aside {

		float: none !important;
		background: none !important;
		margin-left: auto !important;
		margin-right: auto !important;

		}

	/* Fix search results width */

	#main .content .main-col {

		width: auto !important;

		}

	/* Search tab group */

	ul.search-nav {

		display: block !important;
		width: 100% !important;
		margin: 0 !important;
		padding: 0 !important;
		background-color: black !important;

		}

	/* Search tab (unselected) */

	ul.search-nav li {

		background: none !important;
		background-color: #333 !important;
		margin: 0 !important;
		padding: 0px 12px !important;
		border-radius: 12px 3px 0px 0px !important;

		}

	ul.search-nav li a {

		padding: 0px !important;
		background-image: none !important;
		text-decoration: none !important;
		font-family: 'Fjalla One', sans-serif !important;
		font-size: 16px !important;
		text-transform: uppercase;
		color: white !important;
		font-weight: normal !important;

		}

	ul.search-nav li:hover {

		background-color: #4E4560 !important;

		}

	/* Selected tab */

	ul.search-nav li.on {

		background: none !important;
		background-color: #C1272D !important;
		margin: 0 !important;
		padding: 0px 12px !important;
		border-radius: 12px 3px 0px 0px !important;

		}

	ul.search-nav li.on a {

		background-image: none !important;
		margin: 0 !important;
		padding: 0px !important;
		text-decoration: none !important;
		font-family: 'Fjalla One', sans-serif !important;
		font-size: 16px !important;
		text-transform: uppercase;
		color: white !important;
		font-weight: normal !important;
		border-radius: 12px 3px 0px 0px !important;

		}

	/* Search container */

	#chatty_search {

		border-radius: 0px !important;
		border: 2px solid #2C2636 !important;
		border-top: 2px solid #C1272D !important;
		border: 2px solid #313034 !important;
		background-color: #262428 !important;
		width: auto !important;
		margin: 0 !important;

		}

	/* Input box labels */

	div.input-wrap label {

		color: white !important;
		font-family: 'Lato', sans-serif !important;
		font-size: 16px !important;
		font-weight: normal !important;

		}

	/* Input boxes */

	#chatty_search .search-fields input {

		border: 1px solid #333 !important;
		background-color: black !important;
		color: white !important;
		border-radius: 0px !important;
		outline: none !important;
		height: 28px !important;
		padding: 0px 5px !important;

		}

	/* Search button */

	#chatty_search #chatty_search_submit {

		font-size: 16px !important;
		font-family: 'Fjalla One', sans-serif !important;
		font-weight: 400 !important;
		color:#FFFFFF !important;
		width: 100px !important;
		height: 33px !important;
		padding: 0px !important;
		margin-top: 1px !important;
		background-color: #313034 !important;
		border: none !important;
		border-bottom: 5px solid #262428 !important;
		text-transform: uppercase !important;
		outline: none !important;

		}

	#chatty_search #chatty_search_submit:hover {

		background-color: #4e4560 !important;
		cursor: pointer !important;
		border-bottom: 5px solid #6dafbf !important;
		o-transition: all .1s linear;
		-moz-transition: all .1s linear;
		-webkit-transition: all .1s linear;

		}

	/* "All Posts / Informative / Not Work Safe" container */

	.search-filters {

		margin-top: 20px !important;

		}

	div.search-filters div.input-wrap label {

		line-height: 12px !important;

		}

	/* "Comment filters may limit your results" */

	div.input-wrap.hint {

		color: #ddd !important;
		font-style: normal !important;
		float: right !important;
		margin-right: 40px !important;
		font-family: 'Arial', sans-serif !important;

		}

	div.input-wrap.hint a {

		color: #E8E3BE !important;
		text-decoration: none !important;

		}

	/* Sort results by */

	div#chatty_sort {

		margin: 20px 0px 40px 100px !important;
		padding: 0px !important;

		}

	span.sort-results {

		font-family: 'Lato', sans-serif !important;
		color: white !important;
		line-height: 16px !important;
		font-weight: normal !important;

		}

	div.sort-filters div.input-wrap label {

		margin: 2px 6px !important;
		color: #6DAFBF !important;
		font-family: 'Fjalla One', sans-serif !important;
		text-transform: uppercase;
		font-size: 16px !important;
		font-weight: normal !important;

		}

	/* Number of results found */

	h2.search-num-found {

		color: white !important;
		text-align: center;
		font-family: 'Lato', sans-serif;
		text-transform: capitalize !important;
		padding-top: 100px !important;

		}

	/* Category results header and footer */

	li.search-label {

		color: #6DAFBF !important;
		background-color: #313034 !important;
		border: 2px solid #2C2636 !important;
		border-radius: 0 !important;
		margin-top: 20px !important;
		border-left: 2px solid #6DAFBF !important;
		font-family: 'Lato', sans-serif !important;
		font-style: italic !important;
		margin-bottom: -2px !important;

		}

	li.search-label-footer {

		color: #ddd !important;
		background-color: #313034 !important;
		border: 2px solid #2C2636 !important;
		border-left: 2px solid #695D82 !important;
		border-radius: 0 !important;
		font-family: 'Lato', sans-serif !important;
		font-style: italic !important;
		margin-top: -6px !important;
		margin-bottom: 36px !important;

		}

	li.search-label-footer a {

		color: white !important;
		text-decoration: none !important;

		}

	/* Result boxes */

	li.result {

		background-color: #262428 !important;
		border-radius: 0px !important;
		border: 2px solid #2C2636 !important;
		border-left: 2px solid #695D82 !important;
		margin-bottom: 2px !important;

		}

	li.result a {

		color: white !important;
		text-decoration: none !important;
		font-family: 'Arial', sans-serif !important;
		font-size: 16px !important;
		margin-top: 0px !important;
		display: block !important;
		clear: left !important;

		}

	/* "Games / News / Videos / Files" search results */

	ul.results h3 {

		margin: 0px !important;

		}
  
	ul.results h3 a {

		font-style: italic !important;
		margin: 3px 0px !important;
		font-size: 18px !important;

		}

	ul.results p {

		padding: 0px !important;
		margin: 0px !important;
		display: block !important;
		margin-top: 28px !important;
		color: #ddd !important;
		font-family: 'Arial', sans-serif !important;
		font-size: 16px !important;

		}

	/* Chatty search results */

	span.chatty-author a.more {

		display: inline !important;
		width: auto !important;
		float: left !important;
		clear: left !important;
		font-family: 'Lato', sans-serif !important;
		font-size: 18px !important;
		font-weight: bold !important;
		font-style: italic;
		color: #E8E3BE !important;
		text-decoration: none !important;
		text-shadow: 0px 0px 5px black !important;

		}

	span.postdate {

		font-family: 'Arial', sans-serif !important;
		font-size: 11px !important;
		display: inline !important;
		float: left !important;
		clear: left !important;
		margin-bottom: 4px;
		color: #888 !important;

		}

	/* Page numbers */

	div.pagination {

		color: white !important;
		margin: 30px 0px 10px 0px !important;
		font-family: 'Fjalla One', sans-serif !important;
		text-align: center !important;
		text-transform: uppercase !important;
		text-decoration: none !important;

		}

	div.pagination a {

		min-width: 20px !important;
		background-color: #313034 !important;
		padding: 3px 20px !important;
		color: white !important;
		font-family: 'Fjalla One', sans-serif !important;
		font-size: 16px !important;
		text-decoration: none !important;

		}

	div.pagination a:hover {

		background-color: #4E4560 !important;
		border-bottom: 5px solid #6DAFBF !important;

		}

	}



