css:
@-moz-document url-prefix("https://steamcommunity.com/chat"), url-prefix("http://steamcommunity.com/chat") {
.chat_page_header {
    display: none;
}

#chat_friendslist_area {
    position: fixed;
    top: 0;
    left: 0;
    bottom: 0;
}

#chat_msg_area {
    position: fixed;
    top: 0;
    left: 270px;
    /*max-width: 600px;*/
}

#chat_container {
	height: 0px !important;
}

/* Users list */

#chat_friendslist .friendslist_entry {
    height: 27px;
}

#chat_friendslist .friendslist_entry_content.in-game div {
    margin-top: -13px;
}

#chat_friendslist .friendslist_entry_content.in-game div::first-line {
    color: rgba(0, 0, 0, 0);
}

#chat_friendslist .friendslist_entry_content div {
    color: #555;
}

#chat_friendslist .playerAvatar {
    width: 27px;
    height: 27px;
    padding: 2px;
}

#chat_friendslist .playerAvatar img {
    width: 25px;
    height: 25px;
}
#hzImg {
    display: none;
}
}
