css:
@-moz-document domain("allthingsweezer.com") {
/* Top Stuff */
html {
    background: #F1F1F1;
}

.pageWidth {
    max-width: 1024px;
}

#moderatorBar {
    border: none !important;
    background: none !important;
    position: fixed;
    width: 100%;
    top: 0;
    z-index: 1000;

}

#moderatorBar .visitorTabs {
    padding-right: 7px;
}

#moderatorBar .visitorTabs a {
    font-weight: normal;
}
#moderatorBar .pageWidth a, #moderatorBar .pageWidth .itemCount{
    background-color: rgba(218, 28, 58, 0.93);
    margin-right: 5px;
    color: white;
}
/* Logo and Header Areas */
#logoBlock {
    display: none !important;
}

#headerMover #header {
    position: relative !important;
}

#headerMover #headerProxy {
    height: 0 !important;
}

#header {
    display: none;
}

#content .pageContent {
    padding: 10px;
    padding-top: 0;
    background: none;
}

.breadBoxTop {
    height: 75px;
    background-size: contain;
    position: relative;
    padding: 0;
    margin: 0;
    background-image: url("http://allthingsweezer.com/styles/xendisconnect/xenforo/logo-monster.png");
    background-repeat: no-repeat;
    background-position: 50% 50%;
    background-color: rgb(54, 178, 226);
}

@media only screen and (min-width: 481px) {
    .breadBoxTop {
        height: 220px;
        /*background-size: contain;*/
    }
}

.breadBoxTop .topCtrl {
    display: none;
}

.breadBoxTop .breadcrumb {
    position: absolute;
    bottom: 0;
    width: 100%;
    background: rgba(19, 101, 148, 0.29);
    padding: 5px 0;
}

.breadcrumb .crust a.crumb {
    font-size: 1.3em;
    background: rgb(216, 48, 48);
    padding: 2px 8px;
    text-transform: lowercase;
    margin: 0 3px;
    color: white;
    border-radius: 5px;
}
.breadcrumb .crust:hover a.crumb{
    color: #CECECE;
}

.breadcrumb .crust a.crumb:after {
    display: none;
}

.breadcrumb .crust:nth-child(2) a.crumb, .breadcrumb .crust.firstVisibleCrumb a.crumb {
    
}

.breadcrumb .crust:nth-child(3) {
    display: none !important;
}

.forum_view .crust.homeCrumb, .thread_view .crust.homeCrumb {
    display: none !important;
}

.breadcrumb .crust:last-child a.crumb {
    font-weight: normal;
}

.breadcrumb .jumpMenuTrigger {
    display: none;
}

.navPopup .PopupItemLinkActive:hover {
    background: #f6f6f6;
}

/* General Page Layout Styles */
body {}

.sharePage{
    display: none;
}

.prefix {
    background: none;
}

.prefix.prefixGreen {
    background-color: #95CB95;
    border: none;
}


.titleBar {
padding: 0;
padding-bottom: 10px;
margin-bottom: 0;
text-align: center;
/* margin-top: -40px; */
position: relative;
width: 50%;
/* float: right; */
margin: auto;
margin-top: -36px;
}

.titleBar h1 {
font-size: 22px;
color: white;
background: rgba(43, 154, 202, 1);
display: inline;
padding: 3px 5px;
}

#pageDescription {
    display: none;
}
.button {
    font-weight: normal;
    box-shadow: none;
    -webkit-box-shadow: none;
}

.section {
    margin: 0;
}

.secondaryContent {
    border: 0;
    background: none;
    padding: 0 10px;
}

.sectionMain {
    padding: 0;
    margin: 0;
}

.aside {
    clear: both;
}

.sidebar .section .primaryContent h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3 {
    background: none;
    border: none;
}

.sidebar {
    width: auto;
    float: none;
}

/* Forum Home Page */
.section.membersOnline.userList .listInline:before {
    content: "Members Online:";
}

.hashTagCloud {
    margin-top: 10px;
}

.hashTagCloud .secondaryContent {
    font-size: 0;
}

.hashTagCloud h3 {
    display: none;
}

.hashTagCloud .secondaryContent a {
    background: #d94d4d;
    color: white;
    padding: 5px;
    line-height: 2;
    display: inline;
    font-size: 14px;
    margin-right: 3px;
    border-radius: 4px;
}

.PageNav a {
    border: none;
    background: none;
    font-weight: normal;
}
.PageNav .pageNavHeader {display: none;}
.mainContent {
    margin-right: 0;
    float: none;
}

.mainContainer {
    float: none;
    margin-right: 0;
    width: 100%;
}

#twitter-widget-0 {
    background: white;
}

.section.visitorPanel {
    display: none;
}

.section.staffOnline {
    display: none;
}

.section.membersOnline.userList {
    display: none;
}

.section.infoBlock.sharePage {
    display: none;
}

.secondaryContent.statsList {
    display: none;
}

.footer .pageContent {
    display: none;
}

/* Forum Listing Pages */

.forum_list .titleBar {
    display: none;
}

.forum_list .breadcrumb {
    display: none;
}

.forum_view .breadBoxTop {}

@media only screen and (min-width: 481px) {
    .forum_view .breadBoxTop {
        /*height: 162px; */
    }
}

.prefixListPrefixes {
    display: none;
}

.nodeListNewDiscussionButton {
    display: none;
}

.sectionHeaders {
    display: none;
}

.discussionList .sectionHeaders {
    display: none;
}

.discussionList.section.sectionMain {
    padding-top: 0;
}

.categoryStrip {
    display: none;
}

.node .forumNodeInfo.unread .nodeIcon, .node .categoryForumNodeInfo.unread .nodeIcon {
   
}

.nodeList .node.level_1 {
    margin-bottom: 0;
}

.discussionListItem .posterAvatar, .discussionListItem .stats {
    background: none;
}

.discussionListItem .posterAvatar .avatar {
    padding: 0;
}

.discussionListItem {
    background: none;
    border-bottom-style: solid;
    border-bottom-color: #F6F6F6;
}

.discussionListItem:hover {
    background: #F6F6F6;
}

.discussionListItems .unread .title a {
    font-weight: normal;
}

.discussionListItems .unread .lastPostInfo .username {
    font-weight: normal;
}

.sectionMain {
    background: #FDFDFD;
    padding: 5px;
}

.sectionMain.nodeList {
    padding: 0;
}

.forum_view .breadBoxTop {
    height: 75px;
}

@media only screen and (min-width: 481px) {
    .forum_view .breadBoxTop, .thread_view .breadBoxTop {
        height: 162px;
        /*background-size: contain; */
    }
}

@media only screen and (min-width: 1024px) {
    .forum_view .breadBoxTop, .thread_view .breadBoxTop {
        height: 200px;
        /*background-size: contain; */
    }
}

.nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo {
    background: none;
}

#DiscussionListOptionsHandle {
    display: none;
}

.sectionFooter.InlineMod.SelectionCountContainer {
    display: none;
}

.pageNavLinkGroup:nth-of-type(1){
    display: none;
}

/* Post list view */

.message .likesSummary {
    border: none;
}
.message iframe {
    display: block;
    padding: 10px 0;
}
iframe.redactor_MessageEditor{
    padding: 0;
}
.message .dark_postrating.likesSummary, .dark_postrating {
    margin-top: 0;
    margin-bottom: 0;
}

.messageUserInfo {
    background-color: #F0F8FC;
}

.messageUserBlock div.avatarHolder {
    background: none;
}
.messageUserBlock a.username {
    line-height: 1.2;
}
@media only screen and (min-width: 481px) {
    .messageLeft {
        background: #F0F8FC;
        border-right: 1px solid #DEF1FA;
    }
}

.messageUserBlock .extraUserInfo {
    background: none;
}

.messageUserBlock {
    text-align: left;
}

@media only screen and (min-width: 481px) {
    .messageUserBlock {
        text-align: center;
    }
}

.messageInfo.primaryContent {
    border: none;
}

.messageList li.message:nth-child(2n) {}

.messageList .message {
    background: #fdfdfd;
    border-radius: 0;
    -webkit-border-radius: 0;
}

.messageUserBlock .arrow span {
    display: none;
    /*border-left-color: #F0F8FC;*/
}

.bbCodeQuote .attribution {
    background: #eee;
    border: none;
    color: #666;
}

.bbCodeQuote .quoteContainer {
    border: none;
    background: #fff;
}

.bbCodeBlock.bbCodeQuote {
    width: 100%;
}

.signature {
    display: none;
}

.sectionMain.pollBlock{
    background: none;
}

.sectionMain.similarThreadsThreadView{
    background: none;
    padding: 0;
}
.sectionMain.similarThreadsThreadView .primaryContent{
    background: none;
    margin: 0;
    padding: 0;
}

.dataTable tr.dataRow th {
    background: rgb(54, 178, 226) !important;
}

.heading, .xenForm .formHeader {
    background: rgb(54, 178, 226);
}
}
