css:
@-moz-document domain("webdiplomacy.net") {
/* Increase the size of the chatbox so you can actually read that shit */
div.chatbox { max-height: 40em !important; }

/* Decrease the size of the text in the sendbox to 10pt */
textarea#sendbox { font-size: 10pt !important; }

/*England*/
table.chatbox td.country1 { background-color: #efc4e4 !important; color: #40343d !important; }

/*France*/
table.chatbox td.country2 { background-color: #79afc6 !important; color: white !important; }

/*Italy*/
table.chatbox td.country3 { background-color: #a4c499 !important; color: white !important; }

/*Germany*/
table.chatbox td.country4 { background-color: #a08a75 !important; color: white !important; }

/*Austria-Hungary*/
table.chatbox td.country5 { background-color: #c48f85 !important; color: white !important; }

/*Turkey*/
table.chatbox td.country6 { background-color: #eaeaaf !important; color: #404030 !important; }

/*Russia*/
table.chatbox td.country7 { background-color: #a87e9f !important; color: white !important; }

table.chatbox tr { border-top: 1px solid white !important; }

* { font-family: sans-serif; }

}
