css:
@-moz-document domain("slack.com") {

.channels_list_holder h2 {
    font-size: 10px;
}

.list_more {
    font-size: 10px;
}

.channels_list_holder ul li {
    font-size: 14px;
    line-height: 16px;
    height: 16px;
}

.channels_list_holder ul li a {
    height: 16px;
}

.channels_list_holder .section_holder {
    margin: 5px 0px 20px;
}

#team_menu {
    background: #082830;
    border-bottom: 2px solid #000000;
}

#user_menu {
    background: #082830;
    border-bottom: 2px solid #000000;
    border-top: 2px solid #000000;
}

#col_channels {
    background: #061418;
}

#monkey_scroll_wrapper_for_channels_scroller .monkey_scroll_bar {
    background: #000000;
}

#body {
    background: #000000;
}

#header {
    background: #061418;
}

#footer {
    background: #061418;
}

#message-input {
    background: #082830;
    border: 2px solid #061418;
    color: #AFAFAF;
}

#primary_file_button {
    background: #082830;
    border: #061418;
}

.light_theme .message {
    color: #AFAFAF;
}

.light_theme .message_sender {
    color: #AFAFAF;
}

.msgs_holder {
    background: #061418;
}

.day_divider {
    background: #061418;
    color: white;
}

.day_divider_label {
    background: #061418;
}

.message .mention {
    background: #0D595F;
}

.message .mention_channel {
    background: #0D595F;
}

#channel_members_toggle {
    background: #061418;
}

.monkey_scroll_bar {
    width: 8px;
    background: #000000;
}

}
