css:
@-moz-document domain("dihitt.com"), domain("dihitt.com.br") {
/*
@import url('http://fonts.googleapis.com/css?family=Open+Sans:400italic,400,300,700,800');
*/

body, * {
  font-family: 'Open Sans', "Helvetica Neue", "Helvetiva", sans-serif;
}

#container{
  border: 0
}

#top-bar {
  background-image: none;
  background-color: #FF6F00;
  height: auto;
  overflow: hidden;
}

#top-bar .dihitt-logo{
  display: block;
  top: initial;
  position: initial;
  left: initial;
  
  padding: 0;
  margin: 0;
  width: 140px;
  height: auto;
  float: left;
}

.dihitt-logo a {
  background: url(http://cf.dihitt.net/v5/images/h_logo.png) no-repeat center 15px;
  position: initial;
  width: auto;
  padding-top: 52px;
  padding-bottom: 10px;
}


#top-bar #search-bar{
  display: block;
  top: initial;
  position: initial;
  left: initial;
  bottom: initial;
  
  float: left;
  width: auto;
  height: auto;
  margin: 0 0 0 20px;
}

#top-bar #search-bar input {
  float: left; 
  border: 0;
  background: none;
  padding: 16px; 
  font-size: 20px;
  font-family: 'Open Sans', "Helvetica Neue", "Helvetiva", sans-serif;
  border-radius: 5px;
  font-weight: 400;
  margin: 15px 0;
  margin-bottom: 0
}

#top-bar #search-bar input[type=text] {
  background: #FFF; 
  border-radius: 5px 0 0 5px;
  width: 310px;
}

#top-bar #search-bar input[type=submit] {
  background: #2491F3;
  color: #FFF;
  text-indent: 0;
  border-radius: 0 5px 5px 0;
  padding: 24px;
  font-size: 18px;
  height: auto;
  width: auto;
}

#top-bar #header_u_menu {
  display: block;
  top: initial;
  position: initial;
  left: initial;
  
  float: right;
  width: auto;
  height: auto; 
  overflow: auto;
}

#top-bar #welcome-menu{
  display: block;
  top: initial;
  position: initial;
  left: initial;
  
  width: auto;
  height: auto; 
  overflow: auto;
  
  margin: 10px 0 0 0;
}

.cat-menu {
  background: #FFF;
  border: 1px solid #FF6F00; 
  border-radius: 4px;
  margin-top: 20px;
  padding: 0 0 0 30px;
}

.cat-menu li.first{
	margin-right: 30px
    }
    
.cat-menu a {
  font-family: 'Helvetica Neue', Helvetica, sans;
}

#menu{
  margin-top: 20px;
  bottom: initial;
  position: initial;
  left: initial;
  clear: both;
}

#menu li.nDrop.submit-item{
  display: block;    
}

#menu li.nDrop.submit-item a, ul.sub-navigation li.active a {
  background: #2491F3;
  border-radius: 3px;
}

#news_header {
  overflow: auto;
  margin-bottom: 10px;
float: left;
}

ul.sub-navigation li.active a span {
  background: none; 
}

ul.sub-navigation{
  border: none;
  margin: 0;
}

ul.sub-navigation a {
    margin: 0
}

ul.sub-navigation a:hover {
	margin-bottom: 0;
}

ul.sub-navigation li.active a span {
  margin: 0;
  padding: 0;
  height: auto;
}

ul.sub-navigation li  {
  margin: 5px;
  padding: 0;
  display: block;
}

ul.sub-navigation li:first-child {
  margin-left: 0; 
}

ul.sub-navigation li a {
  padding: 0 5px;
  height: auto;
}

#menu li.nDrop.submit-item {
 	bottom: 5px; 
  	background: none;
}

#menu li.nDrop.submit-item a {
 	border-radius: 3px 
}

.vote-this a:hover, .top-news .count a:hover,#sidebar .widget .link a:hover, #menu a:hover, .more_button:hover {
	text-decoration: none;
    padding-bottom: 0;
}
 .link_list .vote-this {
border-radius: 3px 3px 0 0;
overflow: hidden;
    }
    
.vote-this p.number{
 	background: #FF6F00;  
    padding-right: 0;
}
  
 .vote-this p.vote a {
 	background: #2491F3;
  	content: "Votar";
    color: #FFF;
    text-align: center;
  	font-weight: 700;
  	line-height: 2;
    height: auto;
 }

.vote-this p.vote a:after {
	background: #2491F3;
  	content: "Votar";
}
    
#sidebar {
  margin-right: 0
}



#sidebar .widget{
  background: none;
  border: 1px solid #FF6F00;
  box-shadow: 0 0 3px #FFA259 ;
  border-radius: 3px;
  padding: 0;
}

#sidebar .widget a {
	line-height: 35px;
}

#sidebar .widget .link a {
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

#sidebar h2 {
 background: #FF6F00; 
  padding: 0 5px;
  margin: 0;
  line-height: 2;
  
}

#sidebar .widget ul {
  margin: 10px 5px;  
}



#sidebar div.widget li {
  overflow: auto;
  padding: 0;
  margin-bottom: 10px;
}

#sidebar div.widget span.link {
  width: 200px;
  display: block;
  float: left;
}

#sidebar div.widget span.link a {
  width: auto;
  display: block;
}

#sidebar div.widget span.count {
  background: #FF6F00;  
  border-radius: 50%;
  margin: auto;
  margin-right: 5px
}
    
#sidebar div.widget span.count a {
	width: auto;     
}

.sub h1 a {
  font-family: "Open Sans", serif;  
  line-height: 1.2;
}

.user-avatar img, .ur_avatar img {
   border-radius: 50% 
  }

.link-item {
  border: 0;  
  padding: 10px;
    width: auto;
  border-radius: 3px;
    box-shadow: 0 0 3px #B3B3B3;
  margin-left: 0;
  margin-right: 15px;
}

.link_list .sub {
   margin: 0;
 width: 590px;
  float: left;
}

.link_list .sub.with_thumb {
  margin: 0;
  float: left;
  width: 490px;
}

.link_thumb{
margin: 0;
    margin-right: 15px;
  }
  
.link_list h1, .link_list p.link-info, .link_list .link-description {
  margin-bottom: 10px;
}
        
.link_list .vote-this {
  display: block;
  top: initial;
  position: initial;
  left: initial;
  bottom: initial;
  
  float: right;
  height: auto; 
  margin: 0;
}

.ur_position{
    background: none;
    color: #FF6F00;
}
  
.more_button {
  background: #FF6F00;
  color: #FFF;
  text-indent: 0;
  width: auto;
  height: auto;
    }
.more_of_the_same{
   margin-left: 0;
    margin-right: 15px;
  }
}
