css:
@-moz-document url-prefix("http://klavogonki.ru") {
/*!
* Bootstrap v3.0.0
* 2238 1306 DanillaTech CSS
* ѕатч для клавогонок от DanillaTech, улучшающий отображение сайта. 
*/

/*! normalize.css v2.1.0 | MIT License | git.io/normalize */

.userpanel #stats-block td.title {
font-size: 13px !important;
}
#posts-list .post .text {
line-height: 119% !important;
}

#forums-list .item td.last-post .date {
font-size: 13px !important;
}

body, input, select, textarea {
font-size:13px !important;
}

 A {
    text-decoration: none !important;
   } 
   A:hover { 
    text-decoration: underline !important;
   } 
.userpanel
{

max-width:1020px !important;
margin:0 auto !important;
text-align:left !important;
}

html {
max-width:1020px !important;
margin:0 auto !important;
font:"segoe ui" !important;

}


#head #logo {
padding: 2px 2px 2px 2px !important;
}

#head .right {
padding: 0px 0 0 0 !important;
}
.fourcol .col1 {
width:20 !important;	
left:0 !important;	
}
.fourcol .col2 {
width: 22% !important;	
left:4% !important;	
}
.fourcol .col3 {
width:22% !important;	
left:8% !important;	
}
.fourcol .col4 {
width:22% !important;	
left:12% !important;	
}
.index-icon-container.quickstart a {
color: #EE7700 !important;
text-decoration: inherit !important;
}

.index-icon-container.gamelist a {
color: #EE7700 !important;
text-decoration: inherit !important;
}

.index-icon-container.create a {
color: #EE7700 !important;
text-decoration: inherit !important;
}

#forums-list .item .moderators
{

font-size: 0px !important;
}

#posts-list .post td .post-container {
padding-right: 2px !important;
line-height: inherit !important;
font:"segoe ui" !important;

}
#posts-list .post {
font-size: 15px !important;
font:"segoe ui" !important;

}

#head .right .menu a {
text-decoration: inherit !important;
font-family:"Segoe UI";
font-size: 17px !important;

}

#chat-wrapper * {
font-size:14px !important;
font:"segoe ui" !important;

}

.rte-buttonbar img
{
height: 30px !important;

}

#topics-list table.foot td.pages, #posts-list table.foot td.pages {
white-space: normal !important;
text-align: right !important;
padding-right: 0px !important;
max-width: 1020px !important;
}

.topbanner-back, .ownbanner-back
{
display: none !important;

}

#posts-list .post-container .text .modified
{
font: italic 0.7em "segoe ui" !important;
color: #777;
margin-top: 5px;
}

.chat .messages .chat-header {
display: none !important;
}
#forums-list .item td.last-post {
font-size: 15px !important;
white-space: nowrap;
}
#forums-list .item td {
padding: 5px 5px !important;
border-bottom: 1px solid #ddd;
}
body, input, select, textarea {
font: normal 10pt tahoma, sans-serif !important;
}
#topics-list table.list {
margin-top: 10px !important;
}
.forum-up {
font-size: 15px !important;
margin-bottom: 10px !important;
}
#topics-list .item td {
padding: 10px 10px !important;
font-size: 15px !important;
}
#topics-list .item td.title {
font-size: 15px !important;
}

.warn {
display: none !important;
}
#playads, #playads_dummy {
display: none !important;
}
}
