css:
@-moz-document domain("4chan.org") {

/*Colors tables  bg etc*/

* {
font-family:"Mona", sans-serif !important;
-moz-appearance: none !important;
font-size:14px !important;
}

body {

}

.delform {
color:rgb(0,0,150) !important;
}

.postarea { padding: 0px !important; }

.pages {
background-color:transparent !important;
}

span.plus {
color:rgb(0,0,150) !important;
}

.hr {
color:rgb(70,0,70) !important;
}

.logo font b span{
color:#f38f9e !important;
font-size: 300% !important;
}

.omittedposts{
color:rgb(150,0,255) !important;
}

blockquote {
color:rgb(70,0,70) !important;
max-width:870px !important;
}

table {
color:rgb(70,0,70) !important;
}

.filetitle {
color:rgb(70,0,70) !important;
}

textarea{
height:10em !important;
width:45em !important;
}

img.thumb,img[src*="/thumb/"] {
background:transparent !important;
border:solid 1px rgb(0,0,150) !important;
-moz-border-radius:2px !important;
opacity:.99 !important;
z-index:3 !important;
}

html,body {
font-size:20px !important;
background-color:#FCDFFF !important;
background-image:url('http://img443.imageshack.us/img443/2751/93656112pz7aq8.png') !important;
background-position: right bottom !important;
background-repeat:no-repeat !important;
background-attachment:fixed !important;
}

.replymode {
background-color:transparent !important;
color:#000000 !important;
}


th {
background-color:#FAAFBA !important;
opacity:50 !important;
-moz-border-radius:10px !important;
}

*[id="footer"]  {-moz-border-radius:5px !important;opacity:.90 !important;font-size:10px !important; background-color:rgb(221,217,255) !important;color:rgb(70,0,70) !important; border:solid 2.5px rgb(100,90,190) !important; padding: 10px 16px 10px 16px !important;}


tr {
background-color:transparent !important;
color:rgb(0,0,0) !important;
}

.unkfunc{
color:rgb(0,0,200) !important;
}

#bod1 {
background-color:transparent !important;
}


td input, td textarea{
-moz-border-radius:10px !important;
}

[class*="postername"] {
background-color:transparent !important; 
color:rgb(165,20,165) !important;
}

[class*="postertrip"] {
color:rgb(190,50,50) !important;
}

div {
color:rgb(70,0,70) !important;
}

* [id="ft"]{
display:none !important;
}

input[type="file"] {
background-color:transparent !important;
}

.postarea .postblock{
-moz-border-radius:15px /*2px 0 0 2px*/ !important;
margin-right:-1px !important;
padding:0 3px !important;
border:solid 0px #555 !important;
opacity:0.3 !important;
border-right-style: dashed !important;
border:solid 2px rgb(100,90,190) !important;
text-align:center !important;
background-color:rgb(221,217,255) !important;
color:rgb(70,0,70) !important;
}

a[class="quotejs"]{
font-size:14px !important;
}

/* hides ads */

a[target=_top]{display:none !important;}

div.logo img{display:none !important;}

iframe{display:none !important;}

div[style*="text-align: center"]{display:none !important;}

img[style*="border:"]{display:none !important;}

div[id="ad"]{display:none !important;}

a[href="http://track.teenmodels.com/track/MjU5LjEuMS4xLjEuMC4wLjA"]{
display:none !important;
}


/* hr */

hr{display:none !important;}

form hr{display:block !important;}

hr{height:0px !important;border:none !important;background:#999 !important;}


/* SUPER rounded corners 
ANNNNNNNNNNNNNNDDDDDDDD
POOOOOOOOOOSSSSSSSTTTTTTTTSSSSSSSS*/

td.reply {-moz-border-radius:5px !important;opacity:.90 !important;background-color:rgb(221,217,255) !important;color:rgb(70,0,70) !important;
border:solid 2.5px rgb(100,90,190) !important;padding: 0 30px 16px 0 !important}

td.replyhl {-moz-border-radius:5px !important;opacity:.90 !important;background-color:#FAAFBA !important;color:rgb(70,0,70) !important;
border:solid 2.5px rgb(100,90,190) !important;padding: 0 30px 16px 0 !important}


/* links */

a{text-decoration:none !important;color:rgb(0,0,150) !important;}

a.quotejs:hover, a:hover{font-weight:bold !important;color:rgb(0,0,150) !important;}


/* removes ugly borders on page overview [0,1,2...] */

table,td{border:none !important;}

td.rules{display:none !important;}


/* logo */

img + br {display:none !important;}


/* blurry thumbnails fix */

img[md5] {

height: auto !important;

width: auto !important;

}
}
