css:
@-moz-document domain(forums.southpark.cc.com)
 {

span[style="font-size: 150%; line-height: normal"] {color: black !important;}		
span[style="font-size: 50%; line-height: normal"]{color:black!important;}
span[style="font-size: 85%; line-height: normal"] {color: black !important;}
span[style="font-size: 200%; line-height: normal"] {color: red !important;}						
span[style="font-weight: bold"] {color: #000000 !important; font-weight: bold !important;}		

/*BODY*/

body {
background: #000000 !important;
}

section.content > header section.nav .main-nav .logo > a {
position: relative !important;
    top: 8px !important;
}

form {
width: 100% !important
}

#phpbb, .forum-wrapper {
width: 1010px !important;
margin-left: auto !important;
margin-right: auto !important;
}

#phpbb, .gensmall, #phpbb .btnlite {
}

section.page-content > div.forum .forum-wrapper .column.primary
{
  width: 100% !important;
}

#phpbb .moderators {
    font-size: 10px !important;
    font-weight: lighter !important;
}

#phpbb h2 {
    text-align: center !important;
    text-decoration: none !important;
    padding-top: 0px !important;
    padding-right: 0px !important;
    padding-bottom: 30px !important;
    padding-left: 0px !important;
    font-size: 25px;
    text-transform: none;
}

section.page-content > div.forum #phpbb .viewtopic_post_author {
font-size: 12px !important;
}

/*HIDE ADS ETC*/

.page_title_wrap, .top15, #meebo.meebo-00, .page, #promo_357951, .column.secondary, h1.page_title, .page_description.sub, #footer, .ad.leaderboard.sz_728x90.expandable, .ad.leaderboard.sz_728x90.wide, .ad.leaderboard.sz_728x90, #tout, section.hulu-help, .powered-by, section.content .shadows, section.page-content > div.forum h1, section.content > footer
{
  display: none !important;
}

/*SCROLL IMAGES IN POST*/

#phpbb .viewtopic_post_online, #phpbb .viewtopic_post_ranktitle, #phpbb .viewtopic_post_avatar {

text-align: center !important;
border: 1px !important;
border-style: none solid none solid !important;

}

#phpbb .viewtopic_post_contact {
display: inline !important;
text-align: center !important;
border: 1px !important;
border-style: solid solid solid solid !important;
padding-bottom: 5px !important;
padding-top: 5px !important;
}

#phpbb .viewtopic_post_joined {
text-align: center !important;
border: 1px !important;
border-style: none solid solid solid !important;
padding-bottom: 5px !important;
padding-top: 5px !important;
}

#phpbb .viewtopic_post_from {
text-align: center !important;
border: 1px !important;
border-style: none solid solid solid !important;
padding-bottom: 5px !important;
padding-top: 5px !important;
}

#phpbb .viewtopic_post_posted {
text-align: center !important;
padding-left: 10px !important;
padding-top: 0px !important;
background: none !important;

}

#phpbb .viewtopic_message {
overflow-x: auto; overflow-y: hidden;
font-weight:bold !important;
margin-top:20px;
padding-top: 20px !important;
padding-left: 10px !important;

}

#phpbb .quotetitle {
overflow-x: auto; overflow-y: hidden;
font-weight:bold !important;
margin-top:20px;
border: 1px !important;
border-style: solid !important;
}

#phpbb .quotecontent {
overflow-x: auto; overflow-y: hidden;
font-weight:bold !important;
margin-top:0px !important;
border: 1px !important;
border-style: solid !important;
}

#phpbb .viewtopic_signature {
overflow-x: auto; overflow-y: hidden;
font-weight:bold !important;
margin-top:20px;
padding-left:10px !important;
padding-bottom:5px !important;

}

/*ADJUST MARGINS AND PLACEMENT + COLOR*/
 
.options2 {
    left: 280px;
    padding: 6px 0 0 !important;
    padding-left: 38px !important;
    position: absolute;
    top: 0;
    width: 388px;
}


#phpbb .viewtopic_post_subject {
    text-decoration:none !important;
    font-size: 15px;
    text-align: left !important;
    top: 18px;
    left: 10px !important;
    background: none !important;
  
}

#phpbb .viewtopic_post_author {
    text-align: center !important;
    font-size: 18px !important;
    border: 1px !important;
border-style: solid solid solid solid !important;
}

.options1 {
    height: 32px !important;
    padding: 6px 0 0 0px !important;
}

.column.primary, #phpbb
{
  width: 980px !important;
}

h1.page_title {
height:150px;
margin-bottom:10px;
text-indent:-54000pt;
width:950px;
}

#phpbb .topicdetails {
overflow: hidden;
font-weight:normal !important; font-size:12px !important;
margin-top:20px;
}

#phpbb table * {
    font-weight: bold;
    text-align: left;
    vertical-align: top;
}

#phpbb .topicauthor {
overflow: hidden;
font-weight:normal !important; font-size:12px !important; text-align:left !important;
margin-top:20px;
}

.posttbl textarea {
padding:10px;
width:750px !important;
}

.helpline, .post {
padding:10px;
width:750px !important;
}

.posttbl .post {
padding:10px;
width:750px !important;
}

.posttbl div.smilies {
  width:100px !important;
}

.row1 .post {
width: 190px !important;
}
}
