Thanks to nepa3ot
 */

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix(http://bash.org.ru) {

div:first-child[style*="padding: 0px 0px 0px 30px; width: auto;"]
{
  background-color: rgb(238, 238, 238) !important;
  padding-top:5px !important;
  border: #eee solid 1px;
  width:95% !important;
  -moz-border-radius:5px;
  margin:0 auto !important;
  margin-top:-5px !important;
}

#cm_strip
{
  border: solid 1px #ffcc00 !important;
  -moz-border-radius: 15px !important;
  padding: 10px !important;
  background-color:#FFFFCC !important;
  
}


body 
{
  background-color: #fff !important ;
  color: #000 !important;
}


#page .q center
{
  display:none !important;
}

#page
{ 
  width: 1020px !important; 
}

.header 
{
  color: #FFF !important;
  border: solid 1px #000 ! important;
  -moz-border-radius: 10px ! important;
  padding: 5px !important;
  background:#000 !important;
}

.menu 
{
  border: solid 1px #000 !important;
  -moz-border-radius: 10px !important;
  padding: 5px !important;
}

element.style 
{
  float:left !important;
  width:600px !important;
}

.header a:link ,
.menu a:link ,
.header a:visited ,
.menu a:visited 
{
  color: #ff6600 !important ;
}

#bar 
{
  display: none !important;
}

form[action="/"] input[type="text"]
{
  width:440px !important; 
}

form[action="/abyss"] input[type="text"]
{
  width:929px !important; 
}

div#quotes div.q
{
  font-family: Tahoma, Arial, Helvetica, sans-serif !important;
  width:1020px !important; 
}

div#quotes div.q div,
div.stats,
div.aby
{
  border: solid 1px #ffcc00 ! important;
  -moz-border-radius: 15px ! important;
  padding: 10px ! important;
  background-color: #ffffcc ! important;
}

div#quotes div.q:hover div, 
div.stats:hover 
{
  border: solid 1px #ffcc00 ! important;
  padding: 10px ! important;
  background-color: #FFF ! important;
}

div#quotes div.q div.vote 
{
  background: #fff !important;
  border: 0px solid #ffcc00 !important;
  margin: 0px !important;
  padding: 1px !important;
}

.pager a 
{
  background: #FFF !important;
  border: solid 1px #ffcc00 ! important;
  -moz-border-radius: 10px ! important;
}

.pager a:hover 
{
  background: #FFF !important;
  color: #000 !important;
  border: solid 1px #000 ! important;
  -moz-border-radius: 10px ! important;
}

a 
{
  text-decoration: none !important;
  color: #ffa800 !important; 
}

#cm_navi td.navi a:hover
{
background-color:#FFFFCC !important;
-moz-border-radius: 10px ! important;
}

div.aby a:hover
{
background-color:#FFF !important;
-moz-border-radius: 10px ! important;
}

a:link { color: #ff6600 !important; }
a:visited { color: #ff6600 !important; }
a:active { color: #ff7800 !important; }
a:hover { color:#ff7800 !important;}

center:last-child,
small:last-child
{ display:none; }

}
