css:
@-moz-document url("http://yuchan.org"), url-prefix("http://yuchan.org"), domain("yuchan.org") {
body
{
background-image:url('http://yuchan.org/src/b/1405465369586.jpg') !important;
color: white !important;
font: monospace;
}
div.post
{
background: black !important;
min-width: 50%;
border: solid 0px;
border-right: solid 1px #666666;
border-left: solid 1px #666666;
border-bottom: solid 1px #666666;
margin: 0 7% 60px;
padding: 0px;
}
div.reply
{
  background: black !important;
  margin: 0 0 0 0 !important;
  min-width: 100%;
  border: solid 0px;
}
h2.postheader
{
background-image:url('http://yuchan.org/src/b/1402768675279.png') !important;
border: solid 0px;
}
h3.postheader
{
background-image:url('http://yuchan.org/src/b/1402770222274.png') !important;
}
.postername
{
color: white !important;
}
.ptime
{
color: #008fff !important;
}
.postbox input
{
  background: rgba(0 , 0 , 0 , 0.5) !important;
  color: #fff;
}
.postbox table
{
  background: rgba(0 , 0 , 0 , 0.5) !important;
  border: 0 !important;
}
.postbox td
{
  border: 0 !important;
}
.postbox textarea
{
  background: rgba(0,0,0,0.5) !important;
  color: #fff;
}
.postbox td:first-child
{
  background-color: rgba(0,0,0,0.5) !important;
}
.replyhl
{
  color: red !important;
}
.postbox
{
  position: fixed;
  right: 10px;
  z-index: 1;
}
#main
{
  margin: 60px 0 0 0 !important;
}
.thread:last-child 
{
  border-bottom: 1px solid #666666 !important;
}
.spoiler
{
  background: none repeat scroll 0 0 red;
  color: red;
}
}
