css:
@-moz-document url-prefix("http://www.yogajournal.com/poses/sequence_builder?"), url-prefix("http://www.yogajournal.com/poses/sequence_builder/?") {
body {
  width: 25rem; 
}

div#header, div#menu, table#shadow_top, div.clear, td#bottom, div#bottom_subform, div#footer, #content #right_column, td#topright, div#disclaimer {
  display: none;
}

div[style="width: 1008px; margin: auto; position:relative; z-index:9999;"] {
  width: 0 !important;
  position: initial !important;
  z-index: 0 !important;
}
  
div#sb_header {
  height: 124px;
  width: 714px !important;
  background-image: url('http://www.yogajournal.com/images/sequence_sb_background.gif');
}

#sb_header h1 {
  height: 0;
  width: 0;
}

#sb_header h1:after {
  content: "Yoga Sequence Generator";
  font-family: sans-serif;
  position: absolute;
  width: 500px;
  margin-left: 8px;
}

div#shadow {
  width: 684px
}

table#content {
  width: 0;
}

td#left_column {
  width: 700px;
}

div#sequence_main {
  width: 250px;
}

div#scroll {
  background-color: #CFDEF1;
}

td.pose_details {
  width: 177px; 
  height: 102px;
}

table#shadow_bottom {
  width: 720px; 	 
}
}

@-moz-document url("http://www.yogajournal.com/poses/sequence_builder/print_sequence/") {
div#name {
  text-align: center;
}

div#heading {
  display: none;
}

div#poses {
  display: inline-flex;
  width: 100%;
}

table {
  margin: 0 auto;
}

div#poses tr:last-of-type {
  display: none;
}

div#poses table {
  width: auto;
  margin: 0 auto;
  border-collapse: separate;
  border-spacing: 4px; 
}

div#poses table {
  text-align: center;
}

div#poses tr {
  text-align: left;
  display: inline-block;
  vertical-align: top;
  margin: 3px;
}

div#poses td {
  background: #F5F3F3;
  border-collapse: separate;
  border-spacing: 4px;
}

#poses td:nth-child(2n+1) {
  display: table-caption;
}

#poses td:nth-child(2n) {
  float: left;
  width: 150px;
  height: 60px;
  padding-top: 0;
}

.pose_image img {
  width: 150px;
  height: 150px;
}

.pose_level {
  display: inline;
  float: right;
}

.sanskrit_name {
  display: inline;
  vertical-align: super;
  padding-top: 1px;
  min-height: 14px;
}
  
.english_name {
  color: #666666;
}

#poses hr {
  display: none;
}
}
