css:
@-moz-document domain("forum.stabyourself.net") {
body,html{
  background:black!important;
  color:#eef!important;
  padding:0!important;
  margin:0!important;
}
*{transition-duration:0.2s!important;}
h2{
  color:white!important;
  text-align:center!important;
  font-family:"Roboto Condensed",sans-serif!important;
  font-size:4em!important;
  margin:0!important;
}
h2 a{color:inherit!important;}
#wrap{min-width:0!important;max-width:inherit!important;padding:0!important;
  margin:0!important;width:auto!important;}
#logo{display:none!important;}
.inner{
  background:#222!important;padding:5px!important;}
.bg2 .inner{
  background:#181818!important;}

.postbody,.content{color:white!important;
  font-family:"Roboto Condensed",sans-serif!important;}
a{color:#f63!important;}

.corners-top,.corners-bottom,.corners-top>span,.corners-bottom>span{display:none!important;}
.post,.panel,.rules,.topic-actions{padding:0!important;margin:0!important;}
.linklist{padding:0 10px;}
#page-header{position:fixed!important;width:100%;z-index:999;}
.headerbar{display:none!important;}
/*.navbar{min-height:0!important;background:#222!important;}*/
.navbar ul.linklist{margin:0!important;}
.navbar ul.rightside{margin:0!important;display:inline-block!important;float:none!important;}
.navbar ul.rightside li:before{content:'- '!important;}
.navbar ul.leftside{display:inline-block!important;float:none!important;}
.navbar .inner{padding:0!important;background:linear-gradient(0deg, rgba(0,0,0,0),rgba(0,0,0,0.8))!important;}
.navbar{padding:0!important;background:none!important;}
ul.navlinks{border:0!important;margin:0!important;}

#page-body{padding-top:80px!important;max-width:1200px!important;margin:0 auto!important;}


.postprofile{border:none!important;width:100px!important;color:inherit!important;}

a.print, a.sendemail, a.fontsize{text-indent:0!important;width:auto!important;background-image:none!important;height:auto!important;}
a.print:after, a.sendemail:after{content:" - ";}
.icon-home,.icon-faq,.icon-members,.icon-logout {
background-image:none!important;
padding-left:0!important;
}

.reply-icon span,.profile-icons a{background-image:none!important}
.reply-icon,.profile-icons li,.button2,.button1,#format-buttons input, #format-buttons select{
  font-size:1.3em;
  text-transform: uppercase;
  background:#f33!important;
  width:auto!important;
  height:auto!important;
  padding:6px!important;
  margin-bottom:3px!important;
  border:none!important;
  font-weight: 900!important;
}
.icon-search{background-image:none!important;}
.reply-icon:hover,.profile-icons li:hover, .button2:hover,.button1:hover{background:#c22!important;}
.reply-icon:active,.profile-icons li:active, .button2:active,.button1:active{background:white!important;}
.postprofile .profile-icons li{margin:3px!important;}
.reply-icon a,.profile-icons a,.button2,.button1{color:white!important;font-weight:900!important;}
.reply-icon:active a,.profile-icons li:active a, .button2:active,.button1:active{color:#c22!important;}
ul.profile-icons li span{display:inline!important;}
ul.profile-icons li{margin-left:3px!important;margin-right:0!important;}
.pm-icon span,.email-icon span,.report-icon span,.quote-icon span{font-size:0!important;line-height:0!important;}
.pm-icon a:after{content:"PM"!important;}
.email-icon a:after{content:"@"!important;}
.report-icon a:after{content:"REPORT"!important;}
.quote-icon a:after{content:"QUOTE"!important;}
.codebox{background:#000!important;}.codebox,.codebox dt{border-color:#333!important;}
blockquote{background:rgba(0,0,0,0.5)!important;border:none!important;}
cite{border-bottom:solid rgba(255,255,255,0.2) 1px;}
.postbody{
  width:calc(100% - 150px)!important;
  width:-webkit-calc(100% - 150px)!important;
  width:-moz-calc(100% - 150px)!important;
  float:right!important;
  }
.content{margin-top:20px;}
p.author{margin:0!important;}

ul.forums{background:#111!important;}
li.row strong,ul.topiclist li{color:white!important;}
.row:hover{background:#333!important;}
ul.topiclist dd{border:none!important;}
.bg1,topiclist{background:#111!important;}
.bg2{background:#181818!important;}
.forumbg,.forabg{padding:0!important;border:solid #333 1px;}
li.header dl.icon dt{padding-left:5px!important;}
.top{display:none!important;}
h3{border-bottom-color:#333!important;color:inherit!important;}
hr{border-color:#333!important;}
@media (max-width: 700px) {
.posts,.views,dl>.topics,.forum-image{display:none!important;}
}
@media (min-width: 1000px) {
.navbar .navlinks{display:inline-block;}
.navbar>.inner{margin:0 auto!important;padding:0!important;display:block!important;}
}

.rules,.rules .inner{background:inherit!important;}

.topics>.row>dl[style*="unread"]{
  border-left:solid #f33 5px;}
.topics>.row>dl[style*="mine"][style*="unread"]{
  border-left:solid #fc3 5px;}
.topics>.row>dl[style*="_read"][style*="mine"]{
  border-left:solid #116 5px;}
.topics>.row>dl[style*="sticky"] a{
  color:#fc3!important;}
.topics>.row>dl[style*="sticky"]{
  border-left:double #fc3 5px;}
.topics>.row>dl[style*="moved"]{
  border-left:dotted #3f3 5px;}
.topics>.row>dl[style*="announce"]{
  border-left:solid #a6f 5px;
  border-left-color:#a6f!important;
  }
.topics>.row>dl[style*="announce_unread"]{
  border-left-color:#c9f!important;}
.topics>.row>dl{
  background-image:none!important;}
.topics>.row>dl[style*="hot"]>dt{
  padding-left:9px!important;
}
.topics>.row>dl[style*="hot"]{
  border-left-width:11px!important;
}
.topics>.row>dl[style*="topic_read_hot.gif"]{
  border-left:dotted #810 11px;}

.topics>.row>dl[style*="topic_read.gif"]>dt,.row>dl[style*="topic_read_hot.gif"]>dt,.row>dl[style*="topic_read_locked"]>dt{padding-left:20px!important;}
.topics>.row>dl>dt{padding-left:15px!important;}


.row>dl[style*="_read"] a,.read{color:#39f!important;}
.row>dl[style*="_locked"] a{color:#999!important;}
a.postlink{border-bottom-color:#f63!important;}
a.postlink:hover{background:inherit!important;color:#fc3!important;border-bottom-color:#fc3!important;;}
.notice{border-top-color:#444!important;}
label,.panel{color:inherit!important;}
#message-box textarea{background:#111!important;color:white!important;border-color:#444;}
#topicreview .postbody{
float:none!important;width:auto!important;}
.row{border-bottom-color:#444!important;}
.clear{clear:none!important;}
#preview .postbody{float:none!important;margin:10px 20px!important;}
#tabs a,#tabs a span{background-image:none!important;background-color:#111!important;border-bottom:none!important;color:white!important;}
#tabs a span:hover{color:#f63;}
#tabs .activetab a span{color:#f33!important;}
#navigation a{background-color:#111!important;background-image:none!important;}
#navigation #active-subsection a{color:#fa6!important;}
#cp-main .pm{background-color:#111!important;}
.postprofile strong{color:#f33!important;}
.current{color:white!important;}
dl.details dt{color:#f33;float:none!important;display:inline-block!important;}
dl.details dd{color:white!important;float:none!important;display:inline-block!important;}
.left-box{float:none!important;display:inline-block;}
.inputbox,select{
	background:#111!important;
	color:white!important;
  	border-color:#444;
}
#format-buttons input, #format-buttons select{border:none!important;}
#cp-main .panel li.row{border:none!important;border-color:#080808!important;}
ul.cplist{border-top-color:#080808!important;}
#cp-main .panel li.header dd, #cp-main .panel li.header dt{color:white!important;}
#smiley-box{display:none!important;}
#message-box{width:100%!important;width:calc(100% - 10px)!important;}
#postform .panel,#postingbox{width:45%;width:calc(50% - 10px);float:left;}
#topicreview,#review{width:45%;width:calc(50% - 10px);float:right!important;}
table.table1 tbody td{border-top-color:#080808;}

#tabs a:hover{position:relative!important;top:5px!important;}
#cp-main {float:none!important;}
#cp-menu>#navigation>ul{padding-right:10px!important;}
#tabs{margin:0!important;}

.panel{background:inherit!important;}

.pagination span a, .pagination span a:link, .pagination span a:visited{background:#831!important;}
.pagination span strong{background:#f63!important;line-height: 1.5em!important;}
.pagination span a:nth-child(4n+1){background:#620!important;}
.pagination span strong,.pagination span a,.page-dots{display:inline-block!important;margin:0!important;border:none!important;color:white!important;}
.pagination span strong:hover,.pagination span a:hover{background:white!important;color:#f63!important;}
.page-dots{display:none!important;}
.pagination span .page-dots + a,.pagination span .page-dots + strong{border-left:solid #310 5px!important;}
.row>dl>dt>.pagination>span a {color:white!important;}
table.table1 tbody td{border:none!important;}
table.table1 td{color:white!important;}

.copyright{color:white!important;}

.icon-ucp a{content:"UCP"!important;}
}
