css:
@-moz-document url-prefix("http://www.jeuxvideo.com/forums/")
{
 
/*****************************  Fond du site  **************************/
 
body {
  background-color: #acacac !important; /* Couleur du fond */
  background-image: none !important;
}
 
 
/******************************  Haut de page  ************************/
 
#header {
   background-color: #707070 !important; /* Couleur du fond du haut de page */
}
 
#header #newheader {
   background: none !important; /* Supprime le filtre couleur du haut de page */
}
 
 
/*************************  Couleur du sommaire  **********************/
 
#menu2 a {
  color : #000 !important; /* Couleur des liens du sommaire */
}
#menu2 a:hover, #menu2 a:focus {
  background-color : #646464 !important; /* Couleur des liens du sommaire au passage et au clic de la souris */
}
#menu2 div, #menu2 div div, #menu2 ul, #menu2 li {
  background-image : url("http://s.jvflux.com/stylish/images/iphone/sommaire_fonds.png") !important; /* Image de fond des sous-menus */ 
}
.bloc1 h3, .bloc2 h3 {
  background-image : url("http://s.jvflux.com/stylish/images/iphone/bloc_h3.png") !important; /* Image de fond du haut des sous-menus */
}
.col1-1-3, .col2-1-3 {
  background : url("http://s.jvflux.com/stylish/images/iphone/bloc1_1-3_bas.png") left bottom no-repeat !important; /* Image de fond du bas des sous-menus */
}
.bloc1 .bloc_inner, .bloc2 .bloc_inner,.dv1-3.accroches.bloc1 ul, .dv1-3.accroches.bloc2 ul, .dv1-3.accroches2.bloc1 ul, .dv1-3.accroches2.bloc2 ul {
  border-color : #C8C6C6 !important; /* Couleur de la bordure */
  background : #FAFAFA !important; /* Couleur de fond */
}
#menu_telecharger, #menu_videos, #menu_interactif, #menu_concours, #menu_apropos, #menu2 ul {
  background-color : #BEBEBE !important; /* Couleur de fond */
}
#menu_bas, #menu_100_80 {
  display : none !important; /* Sous menu inconnu au bataillon et qui gene donc qu'on vire */
}
 
/***************  Menu machines consoles haut de page v.1  *************/
 
#table_menu_mc {
  background-image : url("http://s.jvflux.com/stylish/images/iphone/menu_mc_fond.png") !important;}
 
#table_menu_mc td, #table_menu_mc #menu_mc, #table_menu_mc #menu_mc {
  background : none !important;
}
 
 
/*************************  Suppression de sous-menus  **********************/
 
#menu_magazine, #menu_videos, #menu_telecharger, #menu_boutique, #menu_concours {
  display : none !important;
}
 
/**************************  Videos a la une  **************************/
 
#col2 .bloc3:first-child .titre_bloc {
  display: none !important; /* Supprime le titre du bloc */
}
 
#col2 .bloc3:first-child {
  background: none !important; /* Supprime la couleur de fond */
}
 
#col2 .bloc3:first-child a {
  visibility: hidden !important; /* Cache le bas dy bkic */
}
 
#col2 .bloc3:first-child .bloc_inner {
  background-image : url('http://image.noelshack.com/fichiers/2014/29/1405515767-noelprint.png') !important; /* Url de l image */
}
 
 
/**********************  Texte du corps de la page  ********************/
 
#col1 .titre_forum  {
  color : #cdcdcd  !important; /* Couleur du nom du forum */
}
 
 
/**********************  Coloris du corps de la page  ********************/
 
#col1 .bloc_forum h1.titre_forum, .bloc_forum h3 {
  background : url('http://s.jvflux.com/stylish/images/iphone/bloc_forum_h3.png') !important;
}
 
#col1 .bloc_forum {
  background : url('http://s.jvflux.com/stylish/images/iphone/bloc_forum_bas.png') no-repeat bottom !important;
  border-bottom: none !important;
}
 
#col1 .bloc_inner {
  border-color: #4C4C4C !important;
}
 
/*****************  Couleurs de la liste des topics  *******************/
 
#liste_topics th {
  background-color : #000000  !important; /* Couleur du fond de la barre Sujet Auteur Nb */
}
 
#liste_topics .pseudo {
  color : #ffffff  !important; /* Couleur des pseudonymes des forumeurs */
}
 
#liste_topics .topic_mod {
  color : #80ffff  !important; /* Couleur des pseudonymes des moderateurs */
}
 
#liste_topics td+td+td+td {
  color : #ffffff  !important; /* Couleur du nombre de messages */
}
 
#liste_topics a:link {
  color : #000000  !important; /* Couleur des topics par defaut */
}
 
#liste_topics a:visited {
  color : #d1d1d1  !important; /* Couleur des topics deja visites */
}
 
#liste_topics a:hover {
  color : #80ffff  !important; /* Couleur des topics au passage de la souris */
}
 
#liste_topics .tr1 {
  background-color : #828282  !important; /* Couleur du fond des topics impairs */
}
 
#liste_topics .tr2 {
  background-color : #aeaeae  !important; /* Couleur du fond des topics pairs */
}
 
 
/***********************  Couleurs du pied de page  ********************/
 
#footer {
  background-image : url("http://s.jvflux.com/stylish/images/iphone/footer_fond.png") !important; /* Image de fond milieu du pied de page */ 
}
 
#liens_pied {
  background-image : url("http://s.jvflux.com/stylish/images/iphone/footer_haut.png") !important; /* Image de fond milieu du pied de page */ 
}
 
#partenaires {
  background-image : url("http://s.jvflux.com/stylish/images/iphone/footer_bas.png") !important; /* Image de fond milieu du pied de page */ 
}
 
#liens_pied p, #partenaires p {
  background-image : none !important; /* Image de fond milieu du pied de page */ 
}
 
 
}
