css:
@-moz-document url-prefix("https://eksisozluk.com"), url-prefix("http://eksisozluk.com") {
/* obsessed with blue for GICIK BETA
 * css antikten alınıp BETA için modifiye edildi. Orijinal tema hakları bana ait değil
 * 17.07.2014 - Shizof
 */

/* pager buttons */
.pbut {
  font-size: 7pt;
  width:16px;
  height:14px;
}

/* top buttons */
.nav TD {
  padding:2px;
  margin:0;
  vertical-align:middle;
}

/* badilist */
#badi
{
  padding: 2px;
  font-size: 8pt;
  width: 108px;
  overflow: hidden;
  border: 1px inset #778899;
}

/* panel leds */

.pmsg {
  background-color: #525873;
  color: #1B2031;
  font-size: 9pt;
}

.pmsghi {
  background-color: #CCD0E1;
  color: #1B2031;
  font-size: 9pt;
}

.pbin {
  background-color: #484f6d;
  color: #1B2031;
  font-size: 9pt;
}

.pbinhi {
  background-color: #727998;
  color: #1B2031;
  font-size: 9pt;
}

.pevt {
  background-color: #4d5370;
  color: #1B2031;
  font-size: 9pt;
}

.pevthi {
   background-color: #949CC0;
   color: #1B2031;
   font-size: 9pt;
}

/* page browser text */
.pagi {
  font-size: 7pt;
  text-align: center;
}

/* page selector (select) */
.pagis {
  font-size: 6pt;
}

/* highlight color */
.highlight
{
  background-color: #3B4467;
}

/* copyright line */
.copyright
{
  font-size: 8pt;
  color: #B0C4DE;
}

/* topic title */
.title {
  font: bold 11pt Verdana, "sans-serif";
  width: 100%;
  background-color: transparent;
  color: #B0C4DE;
  border: none;
}

/* buttons */
.but {
  font: 8pt verdana,"sans-serif";
  cursor: pointer;
  background-color: #566484;
  color: #FAF0E6;
  border: 1px outset #a6b4d4;
}

/* buttons highlighted */
#butOver {
  font: 8pt verdana,"sans-serif";
  cursor: pointer;
  background-color: #667494;
  color: #FAF0E6;
  border: 1px outset #a6b4d4;
}

/* button link adjustments */
.but A {
  color:#FAF0E6;
}

.but A:Hover {
  background-color: transparent;
  color:#FAF0E6;
}

#butOver A {
  color:#FAF0E6;
}

#butOver A:Hover {
  background-color: transparent;
  color:#FAF0E6;
}


/* entry buttons (derived from but) */
.icon {
  font-size: 8pt;
}

.timer {
  font-size: 8pt;
  vertical-align: middle;
}

/* entry author line */
.aul {
  font-size: 8pt;
  line-height: 9pt;
  text-align: right;
}

/* panel classes */
.pi {
  border: 1px inset #778899;
  font-size: 8pt;
  padding: 0px;
  text-align: center;
  width: 50px;
}

.pi A:Hover {
  background-color: transparent;
}

/* top inputtexts */
.input {
  height:16px;
  font: 8pt Verdana, sans-serif;
}

/* message textarea */
.msgtext {
  font-size: 9pt;
}

/* messages table */
.msg {
  padding: 2px;
  font-size: 9pt;
  color: white;
}

.msg TD {
  font-size: 9pt;
}

/* ad box */
.spot {
  width: 100px;
  float: right;
}

.spot TR {
  vertical-align: top;
}

.spot TD {
  font-size: xx-small;
}

/* entry number */
.el {
  width:40px;
  text-align:right;
  line-height:14pt;
  padding:0;
  margin:0;
}

/* entry text */
.e {
  padding:0;
  margin:0;
  line-height:14pt;
}

/* entry id */
.ei {
  color: #DCDCDC;
  margin: 0;
  padding: 0;
  font-size: 8pt;
}

/* regular tab */
.tab {
  padding-top:2px;
  border-bottom: 1px inset #98A0C3;
  font-size: 8pt;
}

.tab DIV {
  line-height: 12pt;
  white-space: nowrap;
  margin-left: 1px;
  padding-left: 4px;
  padding-right: 4px;
  padding-top: 2px;
  padding-bottom: 0;
  border: 1px outset #B3C0CC;
  border-right: 1px outset #B3C0CC;
  border-bottom: 0;
}

.tab A:Hover {
  background-color: transparent;
}

/* selected tab */
.tabsel {
  border: 1px outset #B3C0CC;
  line-height: 11pt;
  white-space: nowrap;
  font-size: 7pt;
  padding-top: 2px;
  padding-bottom: 2px;
  padding-left: 4px;
  padding-right: 4px;
  cursor: default;
  border-bottom:0;
}

/* tab contents */
.tabin {
  border: 1px outset #B3C0CC;
  border-top: 0;
  padding: 4px;
  height: 245px;
  font-size: 8pt;
}

/* hayvan ara */
.adiv {
  display:block;
  position:absolute;
  top:44px;
  left:215px;
  width:18px;
  overflow:hidden
}

.amain {
  background-color: #333a57;
  border:1px outset;
  border-left:0;
  width:100%;
  padding:4px
}

TD.amain {
  font-size: 8pt;
}

.aup {
  border-right:1px inset;
  height:18px
}

.amid {
  background-color:#333a57;
  border:1px outset;
  border-right:0;
  padding:4px;
  line-height:8pt;
  font-weight:bold;
  font-size:8pt;
  
  cursor:pointer;
}

.abot {
  border-right:1px inset;
  height:140px
}

/* default styles */
BODY {
  font: 10pt Verdana, sans-serif;
  background-color: #333A57;
  color: #FAF0E6;
}
/* xhtml css workaround for ie -teo */

HTML {
  scrollbar-base-color : #333A57;
  scrollbar-face-color : #47517A;
  scrollbar-arrow-color : #7882AF;
  scrollbar-darkshadow-color : #515C8C;
  scrollbar-highlight-color : #444D75;
  scrollbar-track-color : #333A57;
  scrollbar-shadow-color : #434B72;
  scrollbar-3dlight-color : #4C5884;
}

UL {
  list-style: lower-roman;
  line-height: 12pt;
}

A {
  color : #B0C4DE;
  text-decoration: none;
}

A:Hover {
  background-color: transparent;
  color : #FAF0E6;
}

SELECT, INPUT, OPTGROUP {
  font: 9pt Verdana, sans-serif;
  background-color: #454D76;
  color: #FAF0E6;
  border: inset 1px #778899;
}

TEXTAREA {
  font-family: Verdana, "sans-serif";
  width: 90%;
  border: 1px inset #778899;
  background-color: #454D76;
  color : #FAF0E6;
}

HR {
  color: #B3C0CC;
  height: 1px;
}

TD {
	font-size: 9pt;
	line-height: 12pt;
	vertical-align: top;
}

P {
  font-size: 9pt;
  line-height: 14pt;
  vertical-align: top;
}

IMG, FORM, TABLE {
  padding: 0;
  margin: 0;
  border: 0;
}

#container {
  margin: auto 30px auto auto;
  float: left;
  width: auto;
  position: relative;  
  max-width: none;  
  width: 98%;
}

#index-section {
  margin: auto auto auto 5px;
  float: left;
  width: 210px;
}

#main {
  position: relative;
  width: auto;
  max-width: none;
  margin-left: 210px;
}

#content-body {
  width: 100%;
  max-width: none;
}

#aside {
  position: relative;
  float: auto;
  margin: auto auto auto 30px;
}

#content {
  position: relative;
  width: auto;
  max-width: none;
}

#entry-list>li:before {
  border-bottom: 0px;
  color: #AAA;
}

.topic-list li {
border-top: 0px;
}

small {
color: #AAA;
}


.ad-bannertop728, .ad-bannerbottom728, .ads, .ad-medyanet, #partial-index > ul > li > .sponsored {
  display:none;
  height:0px;
    
}

#message-thread .incoming, #message-thread .outgoing{
background-color: #454D76;
border: 1px solid #dedede;
-webkit-border-radius: 3px;
-moz-border-radius: 3px;
border-radius: 3px;
-webkit-box-shadow: 0 5px 10px rgba(0,0,0,0.25);
-moz-box-shadow: 0 5px 10px rgba(0,0,0,0.25);
box-shadow: 0 5px 10px rgba(0,0,0,0.25);
word-wrap: break-word;
padding: 10px;
margin-bottom: 20px;
position: relative;
}

.dropdown .dropdown-menu, #user-notifications ul li, #top-bar>#advanced-search-form, .confirmbox, .ui-autocomplete, .share-dialog, #in-topic-search-options, .modal {
background-color: #454D76;
border: 1px solid #dedede;
}

#balon, #sfmenu {
background-color: #333A57 !important;
color: #faf0e6 !important;
box-shadow: 3px 3px 3px #dedede !important;
}

time {
color: #AAA;
}

body>header, #sub-navigation, .hedir {
background-color: #333a57 !important;
}

#sub-navigation>ul,#top-bar input[type=submit].primary,#top-navigation>ul>li>a
{
  background-color:#566484 !important;
  background-image:none !important;
  border: 1px outset #a6b4d4 !important;
  color:#FAF0E6 !important;
  font:8pt verdana,"sans-serif" !important;
  height:22px !important;
  white-space: nowrap !important;
  vertical-align: middle !important;
  text-align: center !important;
  padding: 2px;
}    

#a3-toggle {
  background-color:#566484 !important;
  background-image:none !important;
  border: 1px outset #a6b4d4 !important;
  color:#FAF0E6 !important;  
  font:8pt verdana,"sans-serif" !important;
  padding: 2px 4px 4px 6px;
  height:22px !important;    
}
    
#top-bar input[type=submit].primary {
      padding: 2px 4px 4px 6px;
      border-radius: 0px;
      -webkit-border-radius: 0px;
-moz-border-radius: 0px;
-webkit-box-shadow: 0 0px 0px rgba(0,0,0,0.1);
-moz-box-shadow: 0 0px 0px rgba(0,0,0,0.1);
box-shadow: 0 0px 0px rgba(0,0,0,0.1);
      height:22px !important;
}

#top-bar input[type=submit].primary:hover{
   text-decoration:underline;
}

#sub-navigation>ul>li>a:hover, #sub-navigation>a:hover,#top-navigation>ul>li>a:hover
{
  font: 8pt verdana,"sans-serif";
  cursor: pointer;
  background-color: #667494;
  color: #FAF0E6;
  height: 16px;
  vertical-align: middle;
}

#sub-navigation>ul>li>a {
color: #FAF0E6;
}

#entry-list footer div.other>a:after, #entry-list footer .share-link:after {
border-top: 4px solid #91A1B1;
}

#entry-list footer div.feedback>a, #entry-list footer div.options>a, #entry-list footer div.other>a, #entry-list footer .rate-options a {
color: #91A1B1;
font-size: .8em;
}

.sub-title-menu>a, .sub-title-menu>div>a, .sub-title-menu>div>.dropdown>a {
font-size: .9em;
color: #91A1B1;
}

.sub-title-menu #topic-share-menu>.toggles:after, .sub-title-menu #topic-research-menu .toggles:after, .sub-title-menu #in-topic-search-menu>.toggles:after {
  border-top: 4px solid #91A1B1;
  color: #91A1B1;
}

.topic-list li
{
  background-color: #333a57;
  color: #B0C4DE;
}

.topic-list li a:hover,html.no-touch a.more-data:hover,html.no-touch .pager>a:hover, .ui-autocomplete>li.ui-state-focus, .ui-autocomplete>li:hover
{
  background-color: #454D76 !important;
  color: #FAF0E6 !important;
}

#partial-index > ul > li > a, .topic-list li a
{
  background-color: #333a57;
  color: #B0C4DE;
}

.tabs {
background-color: #454D76;
border-bottom: 1px solid #dedede;
}

.tabs>li.active>a, .tabs>li.active>#srlgndm {
color: #fff !important;
}

.tabs>li>#srlgndm {
color: #B0C4DE !important;
}
  
.tabs>li.active {  
  background-color: #353D66;
}

.tabs>li:hover, .tabs>li.active:hover, html.no-touch .dropdown .dropdown-menu li:hover, .ui-autocomplete>li>a:hover, html.no-touch #in-topic-search-options>li:hover
{
  background-color: #353D66 !important;
}

#threads .unread
{
  background-color:#656DA6 !important;
}

#threads li:hover, html.no-touch #threads li article:hover,html.no-touch #channel-follow-list li:hover {
  background-color: #454D76 !important;
}
    
#threads li article p, #channel-follow-list li>p, .muted, .topic-list li>a.empty-index-item,h1 small, h2 small, h3 small, h4 small, h5 small, h6 small, #user-entry-stats, #profile-cards>li>ul li span, html.no-touch .topic-list li>a.empty-index-item:hover, #trash-items .delete-info, .topic-list li .detail {
color: #AAA;
}
    
h1,h2,h3,h4,h5,h6,fieldset legend
{
  color: #99AABB;
}

#entry-list .hidden, #entry-list .deleted {
background-color: #333a57;
padding: 5px;
font-style: italic;
  border: 1px solid #666;
}

#top-navigation .new-update, #sub-navigation .new-update {
background-color: #3da724 !important;
}

html.no-touch #entry-list footer div.links a:hover, html.no-touch .rate-options li a:hover, html.no-touch div.options a:hover, html.no-touch .entry-date a:hover {
color: #fff;
}

#content-body > ul{
 padding-left: 25px;
}
}
