css:
@-moz-document domain("wwws.cnpq.br"), domain("cnpq.br"), domain("buscatextual.cnpq.br") {
html {
  overflow: auto !important;
}
html body {
  width: auto;
  height: auto;
  overflow: visible;
}
body .megamenu {
  margin-top: 10px !important;  /* originally 21px */
}
body .menuCtl a:link,
body .menuCtl a:visited,
body .menuCtl a:active {
  padding-left: 10px;  /* originally 14px */
  padding-right: 6px;  /* originally 8px */
}
}
