css:
@-moz-document url-prefix("http://www.bungie.net/en/Forum/"), url-prefix("https://www.bungie.net/en/Forum/"), url-prefix("http://admin.bungie.net/en/Forum/"), url-prefix("https://admin.bungie.net/en/Forum/"), url-prefix("http://www.bungie.net/de/Forum/"), url-prefix("https://www.bungie.net/de/Forum/"), url-prefix("http://admin.bungie.net/de/Forum/"), url-prefix("https://admin.bungie.net/de/Forum/"), url-prefix("http://www.bungie.net/es/Forum/"), url-prefix("https://www.bungie.net/es/Forum/"), url-prefix("http://admin.bungie.net/es/Forum/"), url-prefix("https://admin.bungie.net/es/Forum/"), url-prefix("http://www.bungie.net/fr/Forum/"), url-prefix("https://www.bungie.net/fr/Forum/"), url-prefix("http://admin.bungie.net/fr/Forum/"), url-prefix("https://admin.bungie.net/fr/Forum/"), url-prefix("http://www.bungie.net/it/Forum/"), url-prefix("https://www.bungie.net/it/Forum/"), url-prefix("http://admin.bungie.net/it/Forum/"), url-prefix("https://admin.bungie.net/it/Forum/"), url-prefix("http://www.bungie.net/pt-br/Forum/"), url-prefix("https://www.bungie.net/pt-br/Forum/"), url-prefix("http://admin.bungie.net/pt-br/Forum/"), url-prefix("https://admin.bungie.net/pt-br/Forum/"), url-prefix("http://www.bungie.net/ja/Forum/"), url-prefix("https://www.bungie.net/ja/Forum/"), url-prefix("http://admin.bungie.net/pja/Forum/"), url-prefix("https://admin.bungie.net/ja/Forum/") {
/*Background Colours*/
.content_promo_top {
    background: #1B1B1B ! important;
    border-bottom: 2px solid #1B1B1B ! important;
}

.column.main:before {
    background: none repeat scroll 0 0 #1B1B1B ! important;
}

.PublicForum .column.main {
    background: none repeat scroll 0 0 #1B1B1B ! important;
}

.topics li {
    border-top: 1px solid #525252 ! important;
}

.container_sort {
    border-bottom: 1px solid #525252 ! important;
    border-top: 1px solid #525252 ! important;
}

#post > .post {
    background: none repeat scroll 0 0 #1B1B1B ! important;
}

.forumPost article .avatar {
    box-shadow: 0 0px 0px 0px transparent inset, 0 0px 0 #FFFFFF ! important;
}

.forumPost .header_post {
    background: none repeat scroll 0 0 #1B1B1B ! important;
}

.nav_side > li {
    background: #1e242b ! important;
}

/*Text Colours*/
.topics .title {
    color: #eeeeee ! important;
}

.container_underHeader .btn_openCreateNewTopic {
    background: url("https://www.bungie.net/img/theme/destiny/icons/icon_createPost_white.png") no-repeat scroll 13px 8px rgba(0, 0, 0, 0) ! important;
    border: 1px solid #c9cbce ! important;
    color: #eeeeee ! important;
}

a.searchedTag {
    border: 1px solid #c9cbce ! important;
    color: #eeeeee ! important;
}

.container_sort .sort a {
    color: #eeeeee ! important;
}

.container_sort .sort h2 {
    color: #eeeeee;
}

.container_voting a {
    display: block;
    height: 10px;
    background: url("http://i.imgur.com/qACKKRT.png") no-repeat;
    overflow: hidden;
    text-indent: -9999px;
}

.forumPost article .post_body, .forumPost article .post_urlLinkOrImage {
    color: #eeeeee ! important;
}

.forumPost article h1 a {
    color: #eeeeee ! important;
}

.forumPost a {
    text-decoration: none;
    cursor: pointer;
    color: #0096DB;
}

.forumPost .post {
    color: #C1C1C1 ! important;
}

.btn_blue {
    color: #FFF ! important;
}

.forumPost .post_title {
    color: #eeeeee ! important;
    text-shadow: none ! important;
}

.forumPost .post_title a:link, .forumPost .post_title a:hover, .forumPost .post_title a:visited {
    color: #828282 ! important;
}

.nav_side a {
    color: #E8E8E8 ! important;
}

.nav_side h4 {
    color: #E8E8E8 ! important;
}

.forumPost article .avatar:before {
    border: 0px solid rgba(99, 99, 99, 0.4) ! important;
}

.forumPost blockquote {
    color: #DADADA ! important;
}
/*Sol*/
a.avatar [data-membershipid="920628"]{
    content:url(http://i.imgur.com/lHagtLv.jpg);
}
}
