css:
@-moz-document domain("mightytext.net") {


/* 
Color Palette
Pastel Green #43ad45
Pastel Blue #039eda
Dark Grey #181818
*/



body {
  color: #181818!important; 
  font-variant: small-caps!important; 
  background-color: #181818!important;
  overflow: hidden!important;	
  font-family: tahoma!important;
  letter-spacing: .03em!important;
	}

			/* START Left Nav Bar (LEFT COLUMN) SECTION */
#sideNavigation {
  width: 14%!important;
  background-image: url(http://www.cssmatic.com/img/bg-right-column.jpg)
	}

.mainMenuItem:hover {
  background-color: rgba(0, 0, 0, .5)!important;
  color: #43ad45!important;
    }

/* Left Sub Nav Bar Positioning */
.sbnavcontainer {
  position: relative;
  top: 8%;
	}

.sideIcon {
padding-left: 5px!important;
	} 

/* Currently Selected Left Nav Bar Settings */
.activeLeftNavBar {
  color: #43ad45!important;
  background: rgba(0,0,0,.9)!important;
  box-shadow: 0px 0px 3px 3px #43ad45 inset!important;
  border-radius: 1px!important;
  margin: 0 1px 0 1px!important;
  	}

/* New Message Button Settings */
#newSms {
  background-color: rgba(0,0,0,.5)!important;
  border: initial!important;
  box-shadow: 0 0 3px 2px rgba(255, 255, 255, .3)!important;
  padding: 10px 8px 10px 6px!important;
  background-size: 30px!important;
  background-position: 10px 10px!important;
	}

#newSms:hover {
  box-shadow: 0 0 3px 3px rgba(255, 255, 255, .8)!important;
  color: #039eda!important;
	}

#newSMSWrapper {
  margin: 0px;
  position: relative;
  transform: translatey(-93%);
  font-weight: 700!important;
  font-size: 15px!important; 
	}

.newMessageIcon {
  margin-right: 10px!important;
  	}

.iconLabel {
  text-shadow: none!important;
  font-weight: 700!important;
  font-size: 15px!important;
  	}

#batstat {
  visibility: hidden!important;
	}

.emailDropdown {
  color: #43ad45!important;
  font-size: 15px!important;
  font-weight: 700!important;
  text-align: center!important;
  	}

#userNameDropdown .dropdown-toggle:hover {
  cursor: default!important;
	}

.emailDropdown .address {
  width: 125px!important;
    }

			/* END Left Nav Bar (LEFT COLUMN) SECTION */



			/* START thread contact nav bar (MIDDLE COLUMN) SECTION */
#pinnedContent{
  position: fixed!important;
  width: 86%!important;
  transform: translatex(8px);
	}

#threadListHolder {
  border-right: none!important;
  background-color: #181818!important;
  width: 24%!important;
	}

#navBarTabs {
  background-color: inherit!important;
  border-right: none!important;
  position: relative;
	}

.thread-tab img {
  border-radius: initial!important;
  opacity: .8;
  margin: 1px 0 1px .5px!important;
  	}

#navBarTabs .thread-tab {
  	font-size: 15px!important;
  	font-weight: 700!important;
    border-bottom: none!important;
  	margin: 0px .05em 0px 3px!important;
  	color: #039eda!important;
  	position: relative;
	}

.thread-tab:hover {
  background-color: rgba(0,0,0,.6)!important;
  color: inherit!important;
    }

.msgCountAndTimeStampInTab {
	color: rgba(255,255,255,.85)!important;
	opacity: initial!important;
  	font-size: 10px;
  	letter-spacing: initial!important;
  	font-weight: 500!important;
	}

	/* START (active contact) section */
#navBarTabs > .active > a {
  box-shadow: 0 0 10px 3px rgba(3, 158, 218, .9) inset!important;
  background-color: rgba(0,0,0,.6)!important;
  border-radius: 2px!important;
	}


#navBarTabs li.active a .threadNameOrNumber {
  	font-size: 19px!important;
  	font-weight: 700!important;
  	text-decoration: underline!important;
  	margin-right: 7px!important;
  	color: #039eda!important;
	}

.active .msgCountAndTimeStampInTab {
	color: rgba(255,255,255,.85)!important;
	}
	/* END (active contact) section */

#threadListHolder::-webkit-scrollbar {
        display:none!important;
        }


			/* END thread contact nav bar (MIDDLE COLUMN) SECTION */


		
			/* START Main Content (RIGHT COLUMN) SECTION */

	/* START (conversation field) section */
#singleMessageContainer {
  width: 76%!important;
  float: right!important;
	}

.messageThread {
  	width: 100%!important;
  	background-color: #000!important;
  	border-left: none!important;
	}

.threadConversationHolder {
  	padding-bottom: 0!important;
    bottom: initial!important;
  	max-height: 84%!important;
  	margin-bottom: 4px!important;
  	background-image: url(https://i.imgur.com/vQsrfoh.jpg)!important;
  	background-size: contain;
  	background-repeat: no-repeat;
  	background-position: 45%;
	}

.loadMoreMessages {
  width: 50%!important;
  margin-top: 10px!important;
  margin-right: auto!important;
  margin-left: auto!important;
  margin-bottom: 10px!important;
  padding: initial!important;
  text-align: center!important;
  border-radius: 3px!important;
  box-shadow: 0 0 3px 2px rgba(255, 255, 255, .2)!important;
  position: relative!important;
  background-color: inherit!important;
  color: #fff!important;
  text-transform:uppercase!important;
  font-size: 12px!important;
  font-weight: 600!important;
  transform: translatey(10%);
  	}
  
.loadMoreMessages:hover {
  background-color: inherit!important;
  box-shadow: 0 0 3px 3px rgba(255, 255, 255, .5)!important;
    }

	/* END (conversation field) section */

	
	/* START conversation thread item (Sent & Received) subSECTION */
.threadItem {
  font-weight: 700!important;
  font-size: 16px!important;
  line-height: 115%!important;
  width: 34%!important;
  max-width: initial!important;
  border-radius: 3px!important;
	}
        
.receivedText {
    color: #039eda!important;
    background-color: rgba(255, 255, 255, .1)!important;
    border: none!important;
    box-shadow: 0px 0px 2px 2px #039eda!important;
  	margin-left: 4%!important;
    letter-spacing: .02em;
  	}

.receivedText:after, .receivedText:before {
  border-color: transparent!important;
  border-right-color: inherit!important;
  right: 100.5%!important;
    }

.sentText {
  background-color: rgba(255, 255, 255, .1)!important;
  box-shadow: 0px 0px 2px 2px #43ad45!important;
  border: none!important;
  color: #43ad45!important;
  margin-right: 3%!important;
  letter-spacing: .02em;
  	}

.sentText:after, .sentText:before {
  border-color: transparent!important;
  border-left-color: inherit!important;
  left: 100.5%!important;
    }

.sentFromIcon {
    color: #43ad45!important;
  	}

.timestamp-msg {
    font-size: 10px;
    color: rgba(255,255,255,0.7);
  	font-weight: 500;
  	letter-spacing: .01em;
    }
	/* END (conversation thread item) section */


	/* START (msg input footer) section */
.responseArea {
  height: 15%!important;
  border-top: 2px solid rgba(255,255,255,.5);
  padding-top: 1px!important;
  background-color: #181818!important;
  margin-bottom: 2px!important;
	}

.textResponse {
  background-color: rgb(24,24,24)!important;
  color: rgba(255,255,255,.8)!important;
  font-size: 15px!important;
  font-weight: 600!important;
  line-height: 115%!important;
  letter-spacing: .03em!important;
  border: 1px solid #fff!important;
  width: 88.4%!important;
  position: relative;
  left: 3px!important;
  overflow: auto!important;
  height: 74.5%!important;
  font-variant: initial!important;
  padding: 7px 10px 6px 10px!important;
	}

.textResponse:focus{
  box-shadow: 0px 0px 3px 1px #fff inset !important;
  background-color: rgba(0,0,0,.5)!important;
    }

#upload-image-mms {	
  background-color: inherit!important;
  box-shadow: 0 0 3px 2px rgba(255, 255, 255, .3)!important;
  position: absolute;
  z-index: 2;
  right: 2.1%;
  transform: translatey(15%);
	}

#upload-image-mms:hover {
  box-shadow: 0px 0px 3px 4px rgba(255, 255, 255, .3)!important;
	}

	/* END (msg input footer) section */

		/* END Main Content (RIGHT COLUMN) SECTION */

		

		/* START Scrollbar Settings SECTION*/

	/* Scrollbar field */		
::-webkit-scrollbar {
  width: 12px!important;
  background-color: inherit!important;
  border-radius: 2px!important;
	}

	/*Scrollbar thumb */
::-webkit-scrollbar-thumb {
  background-color: rgba(255,255,255, .2)!important;
  box-shadow: none!important;
  border: initial!important;
  border-radius: 2px!important;
	}
	
::-webkit-scrollbar-thumb:hover {
  background-color: rgba(255,255,255,.4)!important;
	}
	
	/* END Scrollbar Settings SECTION */
  

		/* START Remove Elements SECTION */
/* Hide Message View Toggle Menu */
#messageViewToggle {
  display: none!important;
  	}

/* Hide Email Drop Down icon */
.emailDropdownIcon {
  display: none!important;
  	}
  
/* Hide send button */
.sendButton {
  display: none!important;
    }

/* Hide Message Count Button */
#singleMessageContainer .responseButtonsHolder {
  display: none!important;
	}

/* Hide phone number in main content panel header */
.contentPanelHeaderNum {
  display: none!important;
	}

/* Hide Main Content Panel Header */
.contentPanelHeader {
  display: none!important;
  	}

/* Hide Ad's and Popups */
#alert-bottom-right-corner {
  display: none!important;
	}

/* Hide emoticon selector & Schedule selector buttons */
.emoticonSelector, .scheduleSelector {
  display: none!important;
	}

/* Hide Go Pro button */
#goProButton, #events {
  	display: none;
	}

/* Hide Far Right Questions Button */
#uvTab {
  display: none!important;
	}

/* Hide New Message Popups */
.highlightedAlert, .alert-info, .alert {
    display: none!important;
  	}

/* Hide Forward and Delete Message Buttons */
.forwardMessageIcon, .deleteOneMessage {
    display: none!important
    }

/* Hide refresh contact icon */
.shfobtn2, .contactPhotoIconRefresh {
  	visibility: hidden!important;
    display: none!important;
  	}

/* Hide share and follow buttons */
#share-button-main, #follow-button-main {
    visibility: hidden!important;
    display: none!important;
  	}

/* Hide save draft and emoji-selector buttons */
.save-draft-button-holder, .emoji-selector-container {
	display: none!important;
	}
	
/* Hide Drop Up Icon */
#singleMessageContainer .templateDropUpIcon {
  	display: none!important;
	}

/* Hide Star Hover Buttons */
.unstarred {
  	display: none!important;
  	}
	
/* Hide heart-mt Button */
#heart-mt-button {
	display: none!important;
	}
  
	/* END Remove Elements SECTION */

		/* START Disused SECTION
This isn't necessarily incompatible with the above or bad, I just decided not to use it. There is still some functional tidbits you might want to use though

Hide Logo
#logo {
  display: none!important;
  	}

Hide Message Side Triangle Icons
.receivedText:after, .receivedText:before, .sentText:after, .sentText:before {
    border: none!important;
    }

#goProWrapper, .charsLeft {
  display: none;
  margin: 0px;
	}

.contentPanelHeader, .contentPanelHeaderNum, .contentPanelHeaderTextNonGroup, .contentPanelHeaderText {
  background-color: rgba(0,0,0,0)!important;
  opacity: 1!important;
	}

#singleMessageContainer .responseArea {
  height: auto!important;
  margin-bottom: default!important;
	}

#singleMessageContainer .textResponse {
  top: default!important;
  width: 700px!important;
	}

#singleMessageContainer .contentPanelHeaderText, #singleMessageContainer .contentPanelHeaderNum {
  	font-weight: 650;
	}

.shareThisContent, .shareContentCheckBoxSent {
    background-color: #039eda;
  	} 

.batteryLevelPhoneIcon {
  	color: #43ad45!important;
  	}
 
#batstat > div.batwrap > div.batshell > div {
  background-color: #43ad45!important;
  	}
  
#batstat {
  position: absolute;
  bottom: 0px;
  margin-bottom: 15px;
  margin-top: 5px;
	}

#userNameDropdown .dropdown-menu {
  top: 30px!important;
  right: 5px!important;
  margin: 1px 0px 0px 0px!important;
  border-radius: initial!important;
  background-color: rgba(0, 0, 0, .6)!important;
	}

#userNameDropdown ul.dropdown-menu > li > a {
  color: #fff!important;
  font-size: 14px;
  font-weight: 600;
  box-shadow: initial;
	}

#userNameDropdown ul.dropdown-menu > li > a:hover{
  background-color: rgba(0,0,0,.6);
  color: #43ad45!important;
	}
END Disused SECTION */

	

		

}
