css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("pr2hub.com") {
body{
    color:#ffffff;
    background-image: none !important;
    background-color: none !important;
    background: linear-gradient(rgb(0,0,0), #272d33) !important;
    background-repeat: no-repeat !important;
    background-attachment: fixed !important;
}
#body {
    font-size:14px;
    font-family:Arial,Helvetica,sans-serif;
    padding-bottom: 25px !important;
    
}
#container{
min-height:100%;
position:relative;
}
#header{
width:100%;
height: 75px !important;
margin-left:auto;
margin-right:auto;
}
#footer{
background-image: none;
height: 75px !important;
}
.footer_links{
max-width:730px;
margin-left:auto;
margin-right:auto;
padding:20px;
}
.footer_links
{list-style-type:none;
}
.footer_links li{
float:left;
width:182px;
text-align:
center;
}
a:link{
color:#00dddd !important;
}
a:visited {
color:#598799 !important;
}
a:hover {
color: #00eeee !important;
}
.content{
max-width:730px;
margin-left:auto;
margin-right:auto;
padding:20px;
background-color: rgba(255,255,255,.25) !important;
}

.archived{
font-color:#666666;
}
.date{
font-color:#666666;
}
}
