css:
@-moz-document url("http://space.galaxybuster.net/go.php") {
body
{
	background-color:#281737;
  	background-image: url("http://i.imgur.com/et8VAuK.png");
}

label, a
{
	color: #fa78a6;
}

#dash
{
	background-color: #281737;
	border-bottom: 1px solid #fa78a6;

	color: #fa78a6;
}

#right-sidebar
{
	background-color: #281737;
	color: #fa78a6;

	border-left: 1px solid #fa78a6;
  
  	background-image:url('http://i.imgur.com/fSmfCCQ.png');
}

#container
{
	background-color: #281737;
	border: 1px solid #fa78a6;
}

#titlebar
{
	background-color: #57206c;
	border-bottom: 3px double #281737;
}

#comSender, #comSubject, #comBody
{
	border: 1px solid #fa78a6;
	background-color: #281737;

	color: #fa78a6;
}

#t-container
{
	background-color:#281737;
}

#inbox
{
	border: 1px solid #fa78a6;
	color: #fa78a6;
}

.row-message:hover, .admin, .row-header
{
	background-color: #fa78a6;
	color: #281737;
}

.msg-red { color: #e22b2e; }
.msg-red:hover { background-color: #e22b2e;color: #281737;cursor: pointer; }
.msg-lime { color: #6ce235; }
.msg-lime:hover { background-color: #6ce235;color: #281737;cursor: pointer; }
.msg-cyan { color: #19a8bf; }
.msg-cyan:hover { background-color: #19a8bf;color: #281737;cursor: pointer; }
.msg-blue { color: #363cce; }
.msg-blue:hover { background-color: #363cce;color: #281737;cursor: pointer; }
.msg-white { color: #fff; }
.msg-white:hover { background-color: #fff;color: #281737;cursor: pointer; }
.msg-pink { color: #f32a87; }
.msg-pink:hover { background-color: #f32a87;color: #281737;cursor: pointer; }


#options-container, #preview, #options-view, #compose-options-view, #pages, #notif, #view-container
{
	color: #fa78a6;
}

#starred-container
{
	background-color:#281737;
}

#starred-inbox
{
	border: 1px solid #fa78a6;

	color: #fa78a6;
}

.bb-hr
{
	border-top: 1px solid #fa78a6;
	color: #fa78a6;
}

#bannerbody
{
	background-color: #fa78a6;
}

#banner a
{
	color: #281737;
}

#banner-close:hover
{
	color: #57206c;
}

#b-payme
{
	background-color: #281737;
	color: #fa78a6;
	border: 3px solid #281737;
}

#b-payme:hover
{
	background-color: #fa78a6;
	border: 3px solid #281737;
	color: #281737;
}

#sideb-payme
{
	background-color: #fa78a6;
	color: #281737;
	border: 3px solid #fa78a6;
}
#sideb-payme:hover
{
	background-color: #281737;
	border: 3px solid #fa78a6;
	color: #fa78a6;
}

#pwyw
{
	background-color: #fa78a6;
	border-bottom: 4px solid #281737;
}

.btn
{
	display: block;
	border: 1px solid #fa78a6;
	background-color: #281737;

	color: #fa78a6;
}

.btn:hover, .on
{
	background-color: #fa78a6;
	color: #281737;
}

.select-container select
{
	background-color: #fa78a6;
	border: 1px solid #fa78a6;
	color: #281737;
}

#notif-spin > div {
	background-color: #fa78a6;
}

.spinner > div {
	background-color: #fa78a6;
}

#motd
{
color: #f32a87;
}

input[type="checkbox"] + label span
{
	background:url('http://i.imgur.com/u6gOL4b.png') left top no-repeat;
}
input[type="checkbox"]:checked + label span
{
	background:url('http://i.imgur.com/u6gOL4b.png') -14px top no-repeat;
}
}
