@-moz-document domain("www.waymarking.com") {

.bannerimage TR TD { background: none !important; }

a { color:#7C4C13 !important; outline:none !important; }
a:hover { color:#ffa500 !important; }
#title { background-color: #56849B !important; background-image: none !important; }

#homepage_bc_wrap, #bc_wrap { background-color: #A0C1E1 !important; }
body { background: #56849B !important; }

#content .gutter { background-color:#CAC7B8 !important; padding-bottom: 50px !important; }
#content_profile .gutter { background-color:#A5998A !important; }

#hprighttd { border:none !important; }

#hpcenterwording { text-align: center !important; font-size:90% !important; background-color:#A5998A !important; }

#wrap { 
max-width:1400px !important;
/*twt*/min-width:800px !important;
background-color:#cac7b8 !important; }

.box, .catlist_options { background:#cac7b8 !important; }

#searchwaymarks { background-color:#A5998A !important; }

#sidebar { background-color:#BFD5EB !important; }

#pushreturn { background:#A5998A !important; text-align: center !important; font-size: 90% !important; }

.statupdate_date { color:#A5998A !important; }

#searchtabs { width:99.8% !important; background: #A5998A !important; }
#profilelinks { width:100% !important; background: #A5998A !important; }

#sidelist { border:1px solid #ccc !important; background-color:#cac7b8 !important; }

.sidelistitems { background-color:#A5998A !important; }

#sidelisttabs { background:#A5998A !important; }

.gutter #hpcentertable { background-color:#A5998A !important; }

TABLE TR TD,.column1shade{
background-color:#cac7b8 !important;
}

.searchtable, .searchtable td { background: #A5998A !important; padding-bottom: 15px !important; }
.profile_options { background: #CAC7B8 !important; }

}
