css:
@-moz-document url-prefix('https://www.youtube.com/watch?') {

ytd-watch-flexy[theater] #player-theater-container.ytd-watch-flexy, ytd-watch-flexy[fullscreen] #player-theater-container.ytd-watch-flexy {
  max-height: calc(100vh - 56px) !important;
}

ytd-app[is-watch-page] ytd-searchbox[mode=legacy] #container.ytd-searchbox {
  border-color: #666;
  box-shadow: none;
  background-color: #333;
}

ytd-app[is-watch-page] ytd-searchbox[mode=legacy] #container.ytd-searchbox input.ytd-searchbox {
  color: #ccc;
}

ytd-app[is-watch-page] #search-icon-legacy.ytd-searchbox {
  background-color: #666;
  border-color: #666;
}

.ytd-masthead button.yt-icon-button > yt-icon {
  color: #666;
}

}
