css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com") {

* {font-family:Arial!important;opacity:1!important}

body {background-image:none!important;background-color:inherit!important}
input {width:auto!important}

#baseDiv {width:99% !important; margin-top:20px !important ;}
#masthead, #footer, .signup-main, #main-table {width: 100%!important;}
#body-column {width:100%!important;overflow:visible!important;}
#left-column  {width:auto!important;-moz-column-count: 5!important; text-align:center!important;}

#homepage-main-content, #watch-this-vid{width: 79%!important;padding:0 0 0 0!important;margin:0!important;}
#homepage-side-content {width: 20%!important;border:1px solid #ccc!important;-moz-border-radius:22px !important;padding:0!important;background:#eee!important}

[id*="statmodules_"] {width:auto!important;border:0!important;background:inherit!important}
[class*="statModule"] {width:100%!important;background:inherit!important}

.homepage-side-block,.homepage-yellow-block {background:inherit!important;}
.homepage-content-block {background: none!important;}

.vlcontainer, #profile-side-content, #profile-main-content {width: 50%!important;}

#rightAccount, #leftAccount {width: 40%!important;}

.vstill a img {width:60%!important;height:60%!important;}

div[class*="smallText"] a, div[class*="vfacets"] a {text-decoration: none!important;color:#900!important;}

.homepage-block-heading, #active_sharing_div {text-align:center!important;}
.homepage-sponsored-video {width:23%!important;display:table-cell!important;}

.videoIconWrapperOuter, .videoIconWrapperInner {
width:auto!important;height:auto!important;border:0!important;
padding-top:5px!important;overflow:visible!important;}

#watch-vid-title {text-align:center!important;width:80%!important}
#watch-this-vid {text-align:center!important;}
.watch-comment-body {text-align:left!important;}
#watch-other-vids {width:20%!important}
#embed_code, #old-footer, [class="contentBox eeeBG"], .pagingDiv {width:100%!important}
#watch-channel-brand-cap > a > img {width:100%!important;height:auto!important;}

.v75WideEntry {-moz-column-count: 1!important;}

#watch-promoted-container .watch-promoted-vid {float: none!important;width:100%!important}

.v75WrapperOuter, .v75WrapperInner, .v75WideEntry, #old-masthead {width:100%!important;border:0!important}

div[class="expand-panel expanded small-expand-panel"], #watch-video-responses-children {width:100%!important;border:0!important}

.search, .links, .pagingDiv {width:auto!important}

.viewPlaylistMain {width:78%!important;padding:0!important;}
.viewPlaylistSide {width:19%!important;padding:0!important;}

.ad-div, #sideAd, .homepage-yellow-block-bot, #right-column, iframe, .statModule-img-cap, [class*="img-stat"]  {display:none!important;}

}
