css:
@-moz-document domain("fabricjs.com") {
	* {
		background-color: transparent !important;
		color: #D4C4A9 !important;
		text-shadow: none !important;
	}

	body, div, form, table, tbody, tr, td, fieldset, ul, li, a {
		background-color: #404040 !important;
	}

	input, select, button, checkbox {
		background-color: #616161 !important;
	}

	a {
		color: #7F9F7F !important;
	}

	a:hover {
		background-color: #505050 !important;
	}

	a:visited {
		color: #CC9393 !important;
	}

	code, pre, pre span, code span {
		color: #CC9393 !important;
		background: #505050 !important;
	}

	table, tr, th, td {
		border: 1px solid #808080 !important;
	}

	.details dt {
		border-left: 3px solid  #D4C4A9 !important;
	}

	.prettyprint, code {
		border: none !important;
	}

	h1,h2,h3,h4,h5 {
		color: #ACC1AC !important;
	}

	h3,h4 {
		background: #505050 !important;
	}

	#header {
		border-bottom: 1px solid #505050 !important;
	}

	nav {
		border-left: 1px solid #505050 !important;
	}

	#___gcse_0 *, .gsc-wrapper *, .gsc-control-cse table, .gsc-control-cse tr, .gsc-control-cse th, .gsc-control-cse td {
		border: none !important;
	}
}
