css:
/*======================================
  new ひろのブログ all freesoft ja jp liquid compact 2014/07
  @-moz-document url-prefix("http://all-freesoft-blog.seesaa.net/article/")
  ======================================*/
@-moz-document domain("all-freesoft-blog.seesaa.net") {

/*--- unwanteds ---*/
#common-header,
dummy#dummy.dummy {display: none !important;}

/*--- generic containers ---*/
body, div, h1, h2, h3, h4, h5, h6 {
 box-sizing: border-box; -moz-box-sizing: border-box;
 margin: inherit; padding: inherit;
 }
body, div, p, span, h1, h2, h3, h4, h5, h6 {
 font-family: inherit !important;
 font-size: medium !important; font-weight: normal !important;
 line-height: 1.4em !important;
 }
body {padding: 0 !important;}
#container {width: auto !important; background-size: contain !important;}

/*--- header ---*/
#banner {
 padding: 0 !important; background: none !important;
 border-bottom: 1pt solid;
 }

/*--- footer ---*/
#footer {display: none !important;}

/*--- main contents ---*/
#links-left, #content {position: relative !important;}
#links-left {z-index: 1;}
#content {z-index: 0;}
#links-left {width: 150pt !important; border-right: 1pt solid; padding-left: 0.5em;}
#links-left * {max-width: 150pt !important;}
#content {width: 100% !important; margin: 0 0 0 -150pt !important;}
#content>* {width: auto !important; margin: 0 0 0 150pt !important;}
#content>* {padding-left: 0.5em !important;}

/*-- blog text --*/
.blog>* {margin: 0 !important;}
.text br {display: none !important;}
.text a[href^="http://all-freesoft"] {display: block !important;}

/*-- tag --*/
.tag-word {margin: 0 !important; padding: 0 !important;}

/*-- bookmark button --*/
.bookmark {padding: 0 !important;}
.seesaaBookmarks {height: auto !important;}
.seesaaBookmarks img {position: static !important;}

}
