css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("anime-pictures.net") {

body[class] {
background:repeating-linear-gradient(-55deg, #0C5725, #0C5725 10px, #0D5E28 10px, #0D5E28 20px);
background-attachment:fixed;
color:#DDD
}
.menu {background:linear-gradient(to right, #000 0%, #F1BF48 40%, #066DAB 100%) black}
#footer {background-color:#19552D!important;}
h3 {color:black!important}
h1 {color:#EC3F09} b {color:#DDD}
.post_content_avatar {border-right:2px solid #0A0}

.form_table,.sidebar_menu ul,.edit_tag_wrap,#tags_sidebar div[style] {
background-color:rgba(0,50,0,0.6) !important
}
.form_table,.comment_block td,.comment_block td:nth-child(1),.comment_block td:nth-child(2) {border-color:#400}
.form_table tr:nth-child(2n+1),.all_tags tr:nth-child(2n+1) {
background-color:#3D160B
}
.form_table tr:nth-child(2n),.all_tags tr:nth-child(2n) {
background-color:#531D0E
}
.all_tags td, .all_tags th {border-color:#444}
ul.autocomplite li {color:black;background-color:white}
ul.autocomplite li:hover,li.autocomplite_active {background-color:limegreen!important}
ul.autocomplite li b {color:black}
.optional_form_section_1,.optional_form_section_2,.optional_form_section_3 {background-color:transparent}
.optional_form_section_1 {color:#FFDDDD} .optional_form_section_2 {color:#DDFFDD} .optional_form_section_3 {color:#DDDDFF}

.users_messages_new {background-color:#008000}
.chat_message {background-color:rgba(128,128,0,0.5)} .chat_message_sel {background-color:rgba(0,0,0,0.1)}
#chat_field tr td:nth-child(1), #chat_field tr td:nth-child(2) {border-right:1px solid #444}
#chat_field tr {border-bottom:1px solid #444}
.numeric_pages a, .numeric_pages span {background: linear-gradient(to bottom, #850000 0%, #000000 100%)}
.numeric_pages span.active {background: none repeat scroll 0 0 #676F7D}
.numeric_pages a:hover {background: none repeat scroll 0 0 #007F00}

.star_it,.unstar_it:hover {background-image:url("https://anime-pictures.net/static/styles/second/images/score_star.svg")}
.star_it:hover,.unstar_it {background-image:url("https://anime-pictures.net/static/styles/second/images/score_star.svg");background-position:-32px}
.download_icon {background-image:url("https://anime-pictures.net/static/styles/second/images/savefile.svg")}
.icon_delete_big {background-image:url("https://anime-pictures.net/static/styles/second/images/delete_big.svg")}
.icon_edit_big {background-image:url("https://anime-pictures.net/static/styles/second/images/edit_big.svg")}

.post_content {background:repeating-linear-gradient(-55deg, #773, #773 10px, #870 10px, #870 20px);background-attachment:fixed}
.sidebar_block,.sidebar_block .title,.post_content .title {background: linear-gradient(to bottom , sienna 0%, black 100%)!important}
blockquote {background-color:olive;color:black!important}
a {color:limegreen}
.img_block_text {text-decoration:none}
.img_block_text a {text-decoration:none;font-weight:bold}
.numeric_pages a {background-color:#030}
h2 a, h2 {color:#D53707!important;}
a[href*="view_ext_profile"] {color:goldenrod}
}
