css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("anime-pictures.net") {

body[class] {
background:repeating-linear-gradient(-55deg, #00299A, #00299A 10px, #00258A 10px, #00258A 20px);
background-attachment: fixed;
color:#DDD
}
.menu {background:#000}
#footer {background-color:#002690!important;}
h3 {color:#009CCC!important}
h1 {color:#0040FF} b {color:#DDD}
.post_content_avatar {border-right:2px solid #00A}
.form_table,.post_content,.sidebar_menu ul,.edit_tag_wrap,#tags_sidebar div[style] {
background-color:rgba(0,0,120,0.6)
}
.form_table,.comment_block td,.comment_block td:nth-child(1),.comment_block td:nth-child(2) {border-color:grey}
.spoiler {border:1px solid #AAA}
.form_table tr:nth-child(2n+1),.all_tags tr:nth-child(2n+1) {
background-color:#1A0066
}
.form_table tr:nth-child(2n),.all_tags tr:nth-child(2n) {
background-color:#23008C
}
.all_tags td, .all_tags th {border-color:#444}
ul.autocomplite li {color:black;background-color:white}
ul.autocomplite li:hover,.autocomplite_active {background-color:lightblue!important}
ul.autocomplite li b {color:red}
.optional_form_section_1,.optional_form_section_2,.optional_form_section_3 {background-color:transparent}
.optional_form_section_1 {color:#FFDDDD} .optional_form_section_2 {color:#DDFFDD} .optional_form_section_3 {color:#DDDDFF}

.users_messages_new {background-color:#0000A0}
.chat_message {background-color:rgba(255,255,255,0.1)} .chat_message_sel {background-color:rgba(0,0,0,0.1)}
#chat_field tr td:nth-child(1), #chat_field tr td:nth-child(2) {border-right:1px solid #444}
#chat_field tr {border-bottom:1px solid #444}
.numeric_pages a,.numeric_pages span {background: linear-gradient(to bottom, #4548FD 0%, #000000 100%)}
.numeric_pages span.active {background:#4548FD}

.star_it,.unstar_it:hover {background-image:url("https://anime-pictures.net/static/styles/second/images/score_star.svg")}
.star_it:hover,.unstar_it {background-image:url("https://anime-pictures.net/static/styles/second/images/score_star.svg");background-position:-32px}
.download_icon {background-image:url("https://anime-pictures.net/static/styles/second/images/savefile.svg")}
.icon_delete_big {background-image:url("https://anime-pictures.net/static/styles/second/images/delete_big.svg")}
.icon_edit_big {background-image:url("https://anime-pictures.net/static/styles/second/images/edit_big.svg")}

.post_content {background: linear-gradient(to bottom, rgba(0,128,192,0.5), transparent 100%), repeating-linear-gradient(40deg, #006, #006 1px, #003 1px, #003 3px)}
.sidebar_block,.sidebar_block .title,.post_content .title {background: linear-gradient(to bottom, midnightblue, black);border-width:0}
blockquote {background-color:#9326FF;color:black!important;border-color:blue}
a {color:#A6EAFF}
.img_block_text {text-decoration:none}
.img_block_text a {text-decoration:none;font-weight:bold}
h2 a, h2 {color:#00C7FF!important;}
a[href*="view_ext_profile"] {color:#DAA520}
}
