css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("weasyl.com") {

@media (max-width: 959px) {
	#user-content {padding-right: 3%!important;}
}

@media (max-width: 479px) {

#nav-toggle {
	position:absolute!important;
	background:none!important;
	border:none!important;
	top:18px!important;
	left:0px!important;
	padding-left:100px!important;
}
	
#nav-toggle > I {
	display:none;
}
	
#header-nav {
	position:absolute;
	background-color:#121a1d;
	width:100%;
	top:73px!important;
}
	
}
	
/* Unround */

.avatar {
	border-radius:0px!important;
}

/* Flatten Colors */

#footer, .stage {
	background-image:none!important;
}

.button.positive, .comment-submit.clear > .button {
	background-color:#e5e5e5;
	background-image:none!important;
}

#header {
    border:none!important;
    margin-bottom:0px!important;
}
    
#user-image, .pad-left.typeface-a.color-b {
    border-top:1px solid #363f42!important;
}
    
#header:before, #footer:before {
    background-image:none!important;
    display:none!important;
}
    
/* Fix Side Bar*/

 #user-content {
	padding-right:0px;
}

#user-content:before {
	right:0px!important;
}

#user-content .on-side {
	padding:0 1.5%!important;
	text-align:justify!important;
}

/* Fix Footer Border */

#footer-nav .column:last-child {
	border-right:1px solid #35414a;
}

/* Bigger Journal List*/

#journals-content\ user-journals H3 {
	margin-bottom:20px!important;
}

#journals-content\ user-journals a {
	width:100%!important;
	height:100%!important;
	position:relative!important;
	display:block!important;
	padding: 0.5em;

}
    
 #journals-content\ user-journals h4 {
	font-size:14px!important;
	float: none;
	display: block;
	text-align: left;
	margin: 0;
	border-top: 1px dashed #c0c0c0;
	background-color:#d6d6d6!important;
	font-family: "Museo500","Lucida Sans","Lucida Grande",Arial,"Roboto",sans-serif;
	font-weight: normal !important;
}

 #journals-content\ user-journals h4:last-child {
	border-bottom-left-radius: 4px;
	border-bottom-right-radius: 4px;
	box-shadow: 0 1px 0 rgba(0,0,0,0.1);
}
    
/*New Page Titles*/
    
.page-title {
    margin:0px!important;
    background-color:#242d31!important;
}
    
.page-title a:before, .page-title > A:after {
    border:none!important;
    display:none!important;
}
    
.stage.clear > H2 > A {
    border-right:1px solid #3b464c!important;
    padding-right:15px!important;
    padding-top:3px!important;
    margin-top:-3px!important;
}
    
#user-info {
    margin:0px!important;
}
	
/* User Banners */
	
#user-image {
	max-height:300px;
	overflow:hidden;
}
	
#user-image > IMG {
	width:100%!important;
}
	
#user-info {
	padding-top:0!important;
	min-height:100px!important;
}
	
}
